import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';

const AdminDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();

  // State
  const [stats, setStats] = useState({
    totalStudents: 0,
    totalCourses: 0,
    liveSessions: 0,
  });

  const [liveSessions, setLiveSessions] = useState([]);
  const [recentActivity, setRecentActivity] = useState([]);
  const [error, setError] = useState(null);
  const [mobileDrawerOpen, setMobileDrawerOpen] = useState(false);

  // Fetch data on mount
  useEffect(() => {
    if (!loading && (!currentUser || userRole !== 'admin')) {
      navigate('/login');
      return;
    }
    if (!loading && currentUser && userRole === 'admin') {
      fetchDashboardData();
    }
  }, [currentUser, userRole, loading, navigate]);

  const fetchDashboardData = async () => {
    try {
      // Fetch stats
      const [studentsRes, coursesRes, sessionsRes] = await Promise.all([
        supabase.from('users').select('id', { count: 'exact' }).eq('role', 'student'),
        supabase.from('courses').select('id', { count: 'exact' }),
        supabase.from('sessions').select('*').order('created_at', { ascending: false }).limit(10),
      ]);

      setStats({
        totalStudents: studentsRes.count || 0,
        totalCourses: coursesRes.count || 0,
        liveSessions: 12, // Mock: In real app, count sessions with status='active'
      });

      // Get live sessions (those created today)
      const today = new Date().toISOString().split('T')[0];
      const activeSessions = (sessionsRes.data || []).filter(
        s => s.session_date === today
      ).slice(0, 2);

      const sessionsWithDetails = await Promise.all(
        activeSessions.map(async (session) => {
          const classRes = await supabase
            .from('classes')
            .select('class_name, course_id')
            .eq('id', session.class_id)
            .single();

          const courseRes = await supabase
            .from('courses')
            .select('course_code, course_title')
            .eq('id', classRes?.data?.course_id)
            .single();

          return {
            ...session,
            course_code: courseRes?.data?.course_code || 'N/A',
            course_title: courseRes?.data?.course_title || 'Unknown Course',
            instructor: 'Professor ' + (Math.random() > 0.5 ? 'Smith' : 'Chen'),
            room: 'Room ' + (Math.floor(Math.random() * 500) + 100),
          };
        })
      );

      setLiveSessions(sessionsWithDetails);

      // Mock recent activity
      setRecentActivity([
        {
          id: 1,
          type: 'closed',
          title: 'Attendance closed for Bio-101',
          detail: 'Delegate: Mark Johnson',
          time: '5 mins ago',
          color: 'primary',
        },
        {
          id: 2,
          type: 'student',
          title: 'New Student added: John Doe',
          detail: 'Admin: Sarah Wilson',
          time: '20 mins ago',
          color: 'green-500',
        },
        {
          id: 3,
          type: 'alert',
          title: 'System Alert: Delegate Disconnected',
          detail: 'Server Log',
          time: '1 hour ago',
          color: 'orange-500',
        },
      ]);
    } catch (err) {
      console.error('Error fetching dashboard data:', err);
      setError('Failed to load dashboard data');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-background-light dark:bg-background-dark">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border border-gray-300 border-t-primary mb-4" />
          <p className="text-gray-600 dark:text-gray-400">Loading...</p>
        </div>
      </div>
    );
  }

  if (!currentUser || userRole !== 'admin') {
    return null;
  }

  return (
    <div className="relative flex h-auto min-h-screen w-full flex-col overflow-x-hidden bg-background-light dark:bg-background-dark text-gray-900 dark:text-white transition-colors duration-200">
      {/* Mobile Drawer Overlay */}
      {mobileDrawerOpen && (
        <div
          className="fixed inset-0 z-50 bg-[#141414]/40 backdrop-blur-sm"
          onClick={() => setMobileDrawerOpen(false)}
        >
          <div className="flex h-full w-10/12 max-w-[300px] flex-col gap-4 bg-white dark:bg-background-dark p-4 shadow-xl">
            <div className="flex items-center justify-between px-4 pt-4">
              <p className="text-[22px] font-bold leading-tight tracking-tight">Admin Portal</p>
              <button
                onClick={() => setMobileDrawerOpen(false)}
                className="cursor-pointer"
              >
                <span className="material-symbols-outlined">close</span>
              </button>
            </div>
            <ul className="flex flex-col gap-2 mt-4">
              <li className="flex h-12 items-center gap-4 rounded-lg px-4 bg-primary text-white">
                <span className="material-symbols-outlined">dashboard</span>
                <p className="text-base font-bold leading-tight truncate">Dashboard</p>
              </li>
              <li className="flex h-12 items-center gap-4 rounded-lg px-4 hover:bg-primary/10 transition-colors cursor-pointer">
                <span className="material-symbols-outlined">group</span>
                <p className="text-base font-bold leading-tight truncate">Users</p>
              </li>
              <li className="flex h-12 items-center gap-4 rounded-lg px-4 hover:bg-primary/10 transition-colors cursor-pointer">
                <span className="material-symbols-outlined">book</span>
                <p className="text-base font-bold leading-tight truncate">Courses</p>
              </li>
              <li className="flex h-12 items-center gap-4 rounded-lg px-4 hover:bg-primary/10 transition-colors cursor-pointer">
                <span className="material-symbols-outlined">bar_chart</span>
                <p className="text-base font-bold leading-tight truncate">Reports</p>
              </li>
              <li className="flex h-12 items-center gap-4 rounded-lg px-4 hover:bg-primary/10 transition-colors cursor-pointer">
                <span className="material-symbols-outlined">history_edu</span>
                <p className="text-base font-bold leading-tight truncate">Logs</p>
              </li>
            </ul>
            <div className="mt-auto p-4 border-t border-gray-100 dark:border-gray-800">
              <div className="flex items-center gap-3">
                <div className="size-10 rounded-full bg-primary/20 flex items-center justify-center text-primary">
                  <span className="material-symbols-outlined">account_circle</span>
                </div>
                <div>
                  <p className="text-sm font-bold">Admin User</p>
                  <p className="text-xs text-gray-500">Super Admin</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* TopAppBar */}
      <div className="sticky top-0 z-40 flex items-center bg-white dark:bg-background-dark px-4 py-3 justify-between border-b border-gray-100 dark:border-gray-800">
        <div className="flex items-center gap-3">
          <button
            className="flex size-10 items-center justify-center rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800"
            onClick={() => setMobileDrawerOpen(true)}
          >
            <span className="material-symbols-outlined text-gray-700 dark:text-gray-200">
              menu
            </span>
          </button>
          <h2 className="text-lg font-bold leading-tight tracking-tight">Admin Central</h2>
        </div>
        <div className="flex items-center gap-1">
          <button className="flex size-10 items-center justify-center rounded-lg text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-800">
            <span className="material-symbols-outlined">notifications</span>
          </button>
          <div className="size-8 rounded-full bg-primary text-white flex items-center justify-center text-xs font-bold">
            AD
          </div>
        </div>
      </div>

      {/* Scrollable Content */}
      <main className="flex-1 pb-24">
        {/* Welcome Header */}
        <div className="px-4 pt-6 pb-2">
          <h1 className="text-2xl font-bold">Good morning, Admin</h1>
          <p className="text-sm text-gray-500 dark:text-gray-400">University Attendance Overview</p>
        </div>

        {/* Stats Grid */}
        <div className="grid grid-cols-2 gap-3 p-4">
          {/* Total Students Card */}
          <div className="flex flex-col gap-2 rounded-xl p-4 bg-white dark:bg-gray-900 shadow-sm border border-gray-100 dark:border-gray-800">
            <div className="flex items-center justify-between">
              <span className="material-symbols-outlined text-primary text-xl">group</span>
              <span className="text-[#07883b] text-xs font-bold leading-normal">+5%</span>
            </div>
            <p className="text-gray-500 dark:text-gray-400 text-xs font-medium leading-normal">
              Total Students
            </p>
            <p className="tracking-light text-xl font-bold leading-tight">{stats.totalStudents}</p>
          </div>

          {/* Courses Card */}
          <div className="flex flex-col gap-2 rounded-xl p-4 bg-white dark:bg-gray-900 shadow-sm border border-gray-100 dark:border-gray-800">
            <div className="flex items-center justify-between">
              <span className="material-symbols-outlined text-primary text-xl">school</span>
              <span className="text-[#07883b] text-xs font-bold leading-normal">+2%</span>
            </div>
            <p className="text-gray-500 dark:text-gray-400 text-xs font-medium leading-normal">
              Courses
            </p>
            <p className="tracking-light text-xl font-bold leading-tight">{stats.totalCourses}</p>
          </div>

          {/* Live Sessions Card */}
          <div className="flex flex-col gap-2 rounded-xl p-4 bg-white dark:bg-gray-900 shadow-sm border border-gray-100 dark:border-gray-800 col-span-2">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <span className="relative flex h-2 w-2">
                  <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-red-400 opacity-75"></span>
                  <span className="relative inline-flex rounded-full h-2 w-2 bg-red-500"></span>
                </span>
                <p className="text-gray-500 dark:text-gray-400 text-xs font-medium leading-normal">
                  Live Sessions Now
                </p>
              </div>
              <span className="material-symbols-outlined text-red-500 text-xl">sensors</span>
            </div>
            <div className="flex items-baseline gap-2">
              <p className="tracking-light text-2xl font-bold leading-tight">{stats.liveSessions}</p>
              <p className="text-red-500 text-xs font-medium">Ongoing Classes</p>
            </div>
          </div>
        </div>

        {/* Live Sessions Section */}
        <div className="flex items-center justify-between px-4 pt-4 pb-2">
          <h2 className="text-lg font-bold leading-tight tracking-tight">Live Sessions</h2>
          <button className="text-primary text-sm font-bold">See all</button>
        </div>

        <div className="flex flex-col gap-3 px-4">
          {liveSessions.length > 0 ? (
            liveSessions.map((session) => (
              <div
                key={session.id}
                className="flex items-stretch justify-between gap-4 rounded-xl bg-white dark:bg-gray-900 p-4 shadow-sm border border-gray-100 dark:border-gray-800"
              >
                <div className="flex flex-1 flex-col justify-between">
                  <div className="flex flex-col gap-1">
                    <div className="flex items-center gap-2 mb-1">
                      <span className="bg-red-100 dark:bg-red-900/30 text-red-600 dark:text-red-400 text-[10px] font-bold px-2 py-0.5 rounded-full uppercase tracking-wider">
                        Live
                      </span>
                      <p className="text-gray-500 dark:text-gray-400 text-xs font-normal">
                        {session.room}
                      </p>
                    </div>
                    <p className="text-base font-bold leading-tight">
                      [{session.course_code}] {session.course_title}
                    </p>
                    <p className="text-gray-500 dark:text-gray-400 text-xs font-normal">
                      {session.instructor}
                    </p>
                  </div>
                  <div className="mt-4 flex gap-2">
                    <button className="flex-1 flex cursor-pointer items-center justify-center rounded-lg h-9 px-4 bg-primary text-white text-xs font-bold transition-all active:scale-95">
                      Join Room
                    </button>
                    <button className="flex size-9 cursor-pointer items-center justify-center rounded-lg bg-gray-100 dark:bg-gray-800 text-gray-700 dark:text-gray-200">
                      <span className="material-symbols-outlined text-sm">more_horiz</span>
                    </button>
                  </div>
                </div>
                <div
                  className="w-24 h-24 bg-center bg-no-repeat bg-cover rounded-lg"
                  style={{
                    backgroundImage: `url("https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?w=200&h=200&fit=crop")`,
                  }}
                />
              </div>
            ))
          ) : (
            <div className="flex items-center justify-center rounded-xl bg-white dark:bg-gray-900 p-8 shadow-sm border border-gray-100 dark:border-gray-800 text-center">
              <p className="text-gray-500 dark:text-gray-400 text-sm">No active sessions at the moment</p>
            </div>
          )}
        </div>

        {/* Recent Activity Section */}
        <div className="px-4 pt-8 pb-3">
          <h2 className="text-lg font-bold leading-tight tracking-tight">Recent Activity</h2>
        </div>
        <div className="mx-4 bg-white dark:bg-gray-900 rounded-xl shadow-sm border border-gray-100 dark:border-gray-800 overflow-hidden">
          <div className="flex flex-col divide-y divide-gray-100 dark:divide-gray-800">
            {recentActivity.map((activity, idx) => (
              <div key={activity.id} className="p-4 flex gap-4">
                <div className="flex flex-col items-center">
                  <div
                    className={`size-2 rounded-full ring-4 ${
                      activity.color === 'primary'
                        ? 'bg-primary ring-primary/20'
                        : activity.color === 'green-500'
                        ? 'bg-green-500 ring-green-100 dark:ring-green-900/30'
                        : 'bg-orange-500 ring-orange-100 dark:ring-orange-900/30'
                    }`}
                  />
                  {idx !== recentActivity.length - 1 && (
                    <div className="w-0.5 flex-1 bg-gray-100 dark:bg-gray-800 mt-2" />
                  )}
                </div>
                <div className="flex-1">
                  <p className="text-sm font-bold text-gray-900 dark:text-white">{activity.title}</p>
                  <p className="text-xs text-gray-500 mt-1">
                    {activity.detail}  {activity.time}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>

      {/* Floating Action Button */}
      <button className="fixed bottom-24 right-6 z-40 flex size-14 items-center justify-center rounded-full bg-primary text-white shadow-lg shadow-primary/40 active:scale-90 transition-transform">
        <span className="material-symbols-outlined text-3xl">add</span>
      </button>

      {/* Bottom Tab Bar */}
      <nav className="fixed bottom-0 left-0 right-0 z-40 bg-white/80 dark:bg-background-dark/80 backdrop-blur-md border-t border-gray-100 dark:border-gray-800 px-6 pb-6 pt-2">
        <div className="flex items-center justify-between max-w-md mx-auto">
          <div className="flex flex-col items-center gap-1 text-primary">
            <span
              className="material-symbols-outlined"
              style={{ fontVariationSettings: "'FILL' 1" }}
            >
              home
            </span>
            <span className="text-[10px] font-bold">Home</span>
          </div>
          <div className="flex flex-col items-center gap-1 text-gray-400 cursor-pointer hover:text-primary transition-colors">
            <span className="material-symbols-outlined">group</span>
            <span className="text-[10px] font-medium">Students</span>
          </div>
          <div className="flex flex-col items-center gap-1 text-gray-400 cursor-pointer hover:text-primary transition-colors">
            <span className="material-symbols-outlined">analytics</span>
            <span className="text-[10px] font-medium">Stats</span>
          </div>
          <div className="flex flex-col items-center gap-1 text-gray-400 cursor-pointer hover:text-primary transition-colors">
            <span className="material-symbols-outlined">settings</span>
            <span className="text-[10px] font-medium">Settings</span>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default AdminDashboard;
