import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Button from '../components/Button';

const StudentProfile = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  const [isEditing, setIsEditing] = useState(false);
  const [profileData, setProfileData] = useState({
    name: currentUser?.name || 'Student One',
    matricule: currentUser?.matricule || 'STD001',
  });
  const [saving, setSaving] = useState(false);
  const [changingPassword, setChangingPassword] = useState(false);
  const [passwordForm, setPasswordForm] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
  });

  useEffect(() => {
    if (loading) return;
    
    if (!currentUser || userRole !== 'student') {
      navigate('/student');
      return;
    }
  }, [currentUser, userRole, loading, navigate]);

  const handleProfileUpdate = async () => {
    setSaving(true);
    try {
      console.log('üìù Updating profile:', profileData);
      alert('‚úÖ Profile updated successfully!');
      setIsEditing(false);
    } catch (err) {
      console.error('Error updating profile:', err);
      alert('‚ùå Error updating profile');
    } finally {
      setSaving(false);
    }
  };

  const handlePasswordChange = async () => {
    if (passwordForm.newPassword !== passwordForm.confirmPassword) {
      alert('‚ùå New passwords do not match!');
      return;
    }
    if (passwordForm.newPassword.length < 8) {
      alert('‚ùå Password must be at least 8 characters!');
      return;
    }

    setSaving(true);
    try {
      console.log('üîê Changing password');
      alert('‚úÖ Password changed successfully!');
      setChangingPassword(false);
      setPasswordForm({ currentPassword: '', newPassword: '', confirmPassword: '' });
    } catch (err) {
      console.error('Error changing password:', err);
      alert('‚ùå Error changing password');
    } finally {
      setSaving(false);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (!currentUser || userRole !== 'student') return null;

  return (
    <Layout role="student">
      <div className="space-y-8 max-w-2xl mx-auto">
        <div>
          <h1 className="text-3xl font-bold mb-2">My Profile</h1>
          <p className="text-gray-600">View and manage your profile information</p>
        </div>

        {/* Profile Picture and Name */}
        <Card>
          <div className="text-center py-8">
            <div className="w-24 h-24 rounded-full bg-blue-500 flex items-center justify-center text-white font-bold text-4xl mx-auto mb-4">
              {currentUser?.email?.charAt(0).toUpperCase()}
            </div>
            <h2 className="text-2xl font-bold mb-1">{currentUser?.name || currentUser?.email}</h2>
            <p className="text-gray-600">üéì Student</p>
          </div>
        </Card>

        {/* Personal Information */}
        <Card title="üìã Personal Information">
          <div className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-1">Full Name</label>
                <input
                  type="text"
                  value={currentUser?.name || 'Student One'}
                  disabled={!isEditing}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg bg-gray-50 disabled:bg-gray-50"
                />
              </div>
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-1">Matricule/ID</label>
                <input
                  type="text"
                  value={currentUser?.matricule || 'STD001'}
                  disabled={!isEditing}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg bg-gray-50 disabled:bg-gray-50"
                />
              </div>
            </div>
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-1">Email Address</label>
              <input
                type="email"
                value={currentUser?.email}
                disabled
                className="w-full px-4 py-2 border border-gray-300 rounded-lg bg-gray-50"
              />
            </div>
          </div>
        </Card>

        {/* Account Statistics */}
        <Card title="üìä Account Statistics">
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-blue-600">85%</p>
              <p className="text-xs text-gray-600 mt-1">Overall Attendance</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-green-600">18</p>
              <p className="text-xs text-gray-600 mt-1">Classes Attended</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-amber-600">1</p>
              <p className="text-xs text-gray-600 mt-1">Late Arrivals</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-red-600">1</p>
              <p className="text-xs text-gray-600 mt-1">Absences</p>
            </div>
          </div>
        </Card>

        {/* Security */}
        <Card title="üîí Security">
          <div className="space-y-4">
            <div className="p-4 bg-blue-50 rounded-lg border border-blue-200">
              <p className="text-sm font-semibold text-gray-900">Account Status</p>
              <p className="text-sm text-gray-600 mt-1">‚úÖ Active</p>
            </div>
            <Button variant="outline" className="w-full">
              üîë Change Password
            </Button>
          </div>
        </Card>

        {/* Actions */}
        <div className="flex gap-4">
          <Button 
            variant="primary" 
            className="flex-1"
            onClick={() => setIsEditing(!isEditing)}
          >
            {isEditing ? 'üíæ Save Changes' : '‚úèÔ∏è Edit Profile'}
          </Button>
          {isEditing && (
            <Button 
              variant="secondary" 
              className="flex-1"
              onClick={() => setIsEditing(false)}
            >
              ‚ùå Cancel
            </Button>
          )}
        </div>
      </div>
    </Layout>
  );
};

export default StudentProfile;
