import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import StatCard from '../components/StatCard';
import Card from '../components/Card';
import Table from '../components/Table';
import Badge from '../components/Badge';
import Button from '../components/Button';

const AdminDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalStudents: 0,
    totalDelegates: 0,
    totalClasses: 0,
    totalSessions: 0,
  });
  const [students, setStudents] = useState([]);
  const [delegates, setDelegates] = useState([]);
  const [sessions, setSessions] = useState([]);
  const [loadingData, setLoadingData] = useState(true);

  useEffect(() => {
    if (loading) return;

    if (!currentUser || userRole !== 'admin') {
      navigate('/admin');
      return;
    }

    fetchData();
  }, [currentUser, userRole, loading, navigate]);

  const fetchData = async () => {
    try {
      setLoadingData(true);

      // Fetch stats
      const [studentsRes, delegatesRes, classesRes, sessionsRes] = await Promise.all([
        supabase.from('users').select('id', { count: 'exact' }).eq('role', 'student'),
        supabase.from('users').select('id', { count: 'exact' }).eq('role', 'delegate'),
        supabase.from('classes').select('id', { count: 'exact' }),
        supabase.from('sessions').select('id', { count: 'exact' }),
      ]);

      setStats({
        totalStudents: studentsRes.count || 0,
        totalDelegates: delegatesRes.count || 0,
        totalClasses: classesRes.count || 0,
        totalSessions: sessionsRes.count || 0,
      });

      // Fetch students with attendance counts
      const { data: studentsData } = await supabase
        .from('users')
        .select(`
          id,
          name,
          matricule,
          created_at
        `)
        .eq('role', 'student')
        .order('created_at', { ascending: false });

      // Get attendance counts for each student
      const studentsWithAttendance = await Promise.all(
        (studentsData || []).map(async (student) => {
          const { data: attendanceData } = await supabase
            .from('attendance')
            .select('id')
            .eq('student_id', student.id);

          return {
            ...student,
            totalAttendance: attendanceData?.length || 0
          };
        })
      );

      setStudents(studentsWithAttendance);

      // Fetch delegates
      const { data: delegatesData } = await supabase
        .from('users')
        .select(`
          id,
          name,
          matricule,
          created_at
        `)
        .eq('role', 'delegate')
        .order('created_at', { ascending: false });

      setDelegates(delegatesData || []);

      // Fetch sessions with attendance counts
      const { data: sessionsData } = await supabase
        .from('sessions')
        .select(`
          id,
          token,
          created_at,
          expires_at,
          classes (
            class_name
          )
        `)
        .order('created_at', { ascending: false })
        .limit(20);

      // Get attendance counts for each session
      const sessionsWithCounts = await Promise.all(
        (sessionsData || []).map(async (session) => {
          const { data: attendanceData } = await supabase
            .from('attendance')
            .select('id')
            .eq('session_id', session.id);

          return {
            ...session,
            attendanceCount: attendanceData?.length || 0,
            isActive: new Date(session.expires_at) > new Date()
          };
        })
      );

      setSessions(sessionsWithCounts);
    } catch (err) {
      console.error('Error fetching data:', err);
    } finally {
      setLoadingData(false);
    }
  };

  if (loading || loadingData) {
    return (
      <div className="flex items-center justify-center h-screen bg-blue-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-16 w-16 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600 font-medium">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!currentUser || userRole !== 'admin') {
    return null;
  }

  return (
    <Layout role="admin">
      <div className="space-y-8">
        <div>
          <h1 className="text-3xl font-bold mb-2">Admin Dashboard</h1>
          <p className="text-gray-600">System management and attendance overview</p>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <StatCard title="Total Students" value={stats.totalStudents} icon="ðŸ‘¥" color="blue" />
          <StatCard title="Total Delegates" value={stats.totalDelegates} icon="ðŸ§‘â€ðŸ’¼" color="green" />
          <StatCard title="Total Classes" value={stats.totalClasses} icon="ðŸ“š" color="amber" />
          <StatCard title="Total Sessions" value={stats.totalSessions} icon="ðŸ“‹" color="purple" />
        </div>

        {/* Student Attendance Records */}
        <Card title="ðŸ“Š Student Attendance Records">
          {students.length > 0 ? (
            <Table
              columns={[
                { key: 'name', label: 'Name' },
                { key: 'matricule', label: 'Matricule' },
                { key: 'totalAttendance', label: 'Total Attendance' },
                { key: 'created_at', label: 'Registered', render: (date) => new Date(date).toLocaleDateString() },
              ]}
              data={students}
              searchable
              paginated
              itemsPerPage={10}
            />
          ) : (
            <p className="text-gray-600">No students registered</p>
          )}
        </Card>

        {/* Delegate Management */}
        <Card title="ðŸ‘¨â€ðŸ« Delegate Management">
          {delegates.length > 0 ? (
            <Table
              columns={[
                { key: 'name', label: 'Name' },
                { key: 'matricule', label: 'Matricule' },
                { key: 'created_at', label: 'Registered', render: (date) => new Date(date).toLocaleDateString() },
              ]}
              data={delegates}
              searchable
              paginated
              itemsPerPage={10}
            />
          ) : (
            <p className="text-gray-600">No delegates registered</p>
          )}
        </Card>

        {/* Attendance Sessions Summary */}
        <Card title="ðŸ“‹ Attendance Sessions Summary">
          {sessions.length > 0 ? (
            <Table
              columns={[
                { key: 'classes.class_name', label: 'Course' },
                { key: 'created_at', label: 'Date', render: (date) => new Date(date).toLocaleDateString() },
                { key: 'created_at', label: 'Time', render: (date) => new Date(date).toLocaleTimeString() },
                { key: 'attendanceCount', label: 'Students Marked' },
                {
                  key: 'isActive',
                  label: 'Status',
                  render: (isActive) => <Badge label={isActive ? 'ACTIVE' : 'CLOSED'} variant={isActive ? 'success' : 'danger'} />,
                },
              ]}
              data={sessions}
              searchable
              paginated
              itemsPerPage={10}
            />
          ) : (
            <p className="text-gray-600">No attendance sessions created</p>
          )}
        </Card>
      </div>
    </Layout>
  );
};

export default AdminDashboard;
