import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
  adminFetchStats,
  adminFetchOpenSessions,
  adminFetchCourseAttendance,
  adminFetchAttendanceAlerts,
  adminFetchAttendanceExportData
} from '../utils/dashboardQueries';
import {
  generateAttendanceCSV,
  generateAttendanceTSV,
  generateSummaryReport,
  getFormattedDateForFilename,
  isValidDateRange
} from '../utils/excelExport';
import {
  StatCard,
  SectionCard,
  StatusBadge,
  ProgressBar,
  Button,
  AlertBox,
  Table,
  EmptyState,
  LoadingSpinner,
  Modal
} from '../components/UIComponents';

const AdminDashboard = () => {
  const { currentUser, userRole, loading, logout } = useAuth();
  const navigate = useNavigate();

  const [darkMode, setDarkMode] = useState(false);
  const [activeTab, setActiveTab] = useState('overview');

  const [stats, setStats] = useState({
    totalStudents: 0,
    totalDelegates: 0,
    totalCourses: 0,
    totalClasses: 0,
    sessionsToday: 0,
    error: null
  });

  const [openSessions, setOpenSessions] = useState([]);
  const [courseAttendance, setCourseAttendance] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [loadingData, setLoadingData] = useState(true);

  // Export modal states
  const [showExportModal, setShowExportModal] = useState(false);
  const [exportFilters, setExportFilters] = useState({
    courseCode: '',
    startDate: '',
    endDate: ''
  });
  const [exportLoading, setExportLoading] = useState(false);

  useEffect(() => {
    if (!loading && (!currentUser || userRole !== 'admin')) {
      navigate('/admin');
      return;
    }

    if (!loading && currentUser && userRole === 'admin') {
      fetchAllDashboardData();
      const interval = setInterval(fetchAllDashboardData, 30000);
      return () => clearInterval(interval);
    }
  }, [currentUser, userRole, loading, navigate]);

  const fetchAllDashboardData = async () => {
    try {
      setLoadingData(true);
      const [statsData, sessionsData, attendanceData, alertsData] = await Promise.all([
        adminFetchStats(),
        adminFetchOpenSessions(),
        adminFetchCourseAttendance(10),
        adminFetchAttendanceAlerts()
      ]);

      setStats(statsData);
      setOpenSessions(sessionsData.sessions);
      setCourseAttendance(attendanceData.courseAttendance);
      setAlerts(alertsData.alerts);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoadingData(false);
    }
  };

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/login');
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  const handleExportData = async (format = 'csv') => {
    try {
      setExportLoading(true);

      // Validate date range
      if (exportFilters.startDate && exportFilters.endDate) {
        if (!isValidDateRange(exportFilters.startDate, exportFilters.endDate)) {
          alert('Start date must be before end date');
          setExportLoading(false);
          return;
        }
      }

      // Fetch export data
      const { data, error } = await adminFetchAttendanceExportData(exportFilters);

      if (error) {
        alert('Error fetching data: ' + error);
        setExportLoading(false);
        return;
      }

      if (!data || data.length === 0) {
        alert('No data found for the selected filters');
        setExportLoading(false);
        return;
      }

      // Generate file based on format
      const dateString = getFormattedDateForFilename();
      const filename = `attendance_${dateString}.${format === 'summary' ? 'csv' : format}`;

      if (format === 'csv') {
        generateAttendanceCSV(data, filename);
      } else if (format === 'xlsx') {
        generateAttendanceTSV(data, filename);
      } else if (format === 'summary') {
        generateSummaryReport(data, filename);
      }

      // Close modal and reset filters
      setShowExportModal(false);
      setExportFilters({
        courseCode: '',
        startDate: '',
        endDate: ''
      });

      alert(`Data exported successfully as ${filename}`);
    } catch (error) {
      console.error('Export error:', error);
      alert('Error exporting data: ' + error.message);
    } finally {
      setExportLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gradient-to-br from-gray-50 to-gray-100 dark:from-gray-950 dark:to-gray-900">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (!currentUser || userRole !== 'admin') {
    return null;
  }

  const navItems = [
    { id: 'overview', label: 'Overview', icon: 'dashboard' },
    { id: 'sessions', label: 'Sessions', icon: 'event_note' },
  ];

  return (
    <div className={`min-h-screen bg-gray-50 dark:bg-gray-900 ${darkMode ? 'dark' : ''}`}>
      {/* TOP HEADER */}
      <header className="bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 rounded-lg bg-gradient-to-br from-blue-600 to-blue-700 flex items-center justify-center">
                  <span className="material-symbols-outlined text-white text-sm">admin_panel_settings</span>
                </div>
              </div>
              <div className="ml-3">
                <h1 className="text-xl font-semibold text-gray-900 dark:text-white">Admin Dashboard</h1>
                <p className="text-sm text-gray-500 dark:text-gray-400">Attendance Management System</p>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <button
                onClick={() => setDarkMode(!darkMode)}
                className="p-2 rounded-lg text-gray-500 hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
              >
                <span className="material-symbols-outlined">
                  {darkMode ? 'light_mode' : 'dark_mode'}
                </span>
              </button>

              <button className="relative p-2 rounded-lg text-gray-500 hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors">
                <span className="material-symbols-outlined">notifications</span>
                {alerts.length > 0 && (
                  <span className="absolute top-1 right-1 w-2 h-2 bg-red-500 rounded-full"></span>
                )}
              </button>

              <div className="w-8 h-8 rounded-full bg-gradient-to-br from-blue-600 to-blue-700 flex items-center justify-center text-white font-medium text-sm cursor-pointer">
                {currentUser?.email?.charAt(0).toUpperCase()}
              </div>

              <Button variant="outline" size="sm" onClick={handleLogout}>
                Logout
              </Button>
            </div>
          </div>
        </div>

        {/* TAB NAVIGATION */}
        <div className="border-t border-gray-200 dark:border-gray-700">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <nav className="flex space-x-8">
              {navItems.map((item) => (
                <button
                  key={item.id}
                  onClick={() => setActiveTab(item.id)}
                  className={`py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                    activeTab === item.id
                      ? 'border-blue-500 text-blue-600 dark:text-blue-400'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300 dark:text-gray-400 dark:hover:text-gray-300'
                  }`}
                >
                  <span className="material-symbols-outlined mr-2 text-base">{item.icon}</span>
                  {item.label}
                </button>
              ))}
            </nav>
          </div>
        </div>
      </header>

      {/* MAIN CONTENT */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {activeTab === 'overview' && (
          <div className="space-y-8">
            {/* KPI CARDS */}
            <div>
              <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">System Overview</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <StatCard
                  label="Total Students"
                  value={stats.totalStudents}
                  icon="group"
                  trend=" 12% from last month"
                />
                <StatCard
                  label="Total Delegates"
                  value={stats.totalDelegates}
                  icon="people"
                  trend="Active instructors"
                />
                <StatCard
                  label="Total Courses"
                  value={stats.totalCourses}
                  icon="book"
                  trend="Registered courses"
                />
                <StatCard
                  label="Sessions Today"
                  value={stats.sessionsToday}
                  icon="event_note"
                  trend="Active now"
                  trendColor="amber"
                />
              </div>
            </div>

            {/* ALERTS */}
            {alerts.length > 0 && (
              <div>
                <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                  System Alerts ({alerts.length})
                </h2>
                <div className="space-y-3">
                  {alerts.slice(0, 5).map((alert, idx) => (
                    <AlertBox
                      key={idx}
                      type={alert.severity === 'critical' ? 'error' : 'warning'}
                      title={alert.message}
                      message={alert.courseTitle ? `${alert.courseTitle}: ${alert.attendance}` : alert.message}
                      closable={false}
                    />
                  ))}
                </div>
              </div>
            )}

            {/* COURSE ATTENDANCE */}
            <div>
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-xl font-semibold text-gray-900 dark:text-white">Course Attendance</h2>
                <Button variant="secondary" size="sm" onClick={() => setShowExportModal(true)}>
                  <span className="material-symbols-outlined mr-2">download</span>
                  Export Data
                </Button>
              </div>

              <SectionCard>
                {courseAttendance.length > 0 ? (
                  <div className="space-y-4">
                    {courseAttendance.slice(0, 8).map((course, idx) => (
                      <div key={idx} className="flex items-center justify-between p-4 border-b border-gray-100 dark:border-gray-700 last:border-0">
                        <div className="flex-1">
                          <p className="font-medium text-gray-900 dark:text-white">{course.courseTitle}</p>
                          <p className="text-sm text-gray-500 dark:text-gray-400">
                            {course.courseCode}  {course.enrolledStudents} students
                          </p>
                        </div>
                        <div className="flex items-center space-x-4">
                          <div className="text-right">
                            <p className="text-2xl font-bold text-gray-900 dark:text-white">
                              {course.attendancePercentage}%
                            </p>
                            <StatusBadge status={course.status} />
                          </div>
                          <div className="w-24">
                            <ProgressBar percentage={course.attendancePercentage} status={course.status} />
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <EmptyState icon="analytics" title="No Data" message="No course attendance data available" />
                )}
              </SectionCard>
            </div>

            {/* QUICK ACTIONS */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">Quick Actions</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {[
                  { icon: 'person_add', title: 'Enroll Student', desc: 'Add new student' },
                  { icon: 'event_note', title: 'Create Session', desc: 'Start attendance session' },
                  { icon: 'upload_file', title: 'Import CSV', desc: 'Bulk import data' },
                  { icon: 'class', title: 'Add Class', desc: 'Create new class' },
                ].map((action, idx) => (
                  <div key={idx} className="bg-white dark:bg-gray-800 rounded-lg p-6 border border-gray-200 dark:border-gray-700 hover:shadow-md transition-shadow cursor-pointer">
                    <div className="w-12 h-12 bg-blue-100 dark:bg-blue-900/30 rounded-lg flex items-center justify-center mb-4">
                      <span className="material-symbols-outlined text-blue-600 dark:text-blue-400">{action.icon}</span>
                    </div>
                    <h3 className="font-medium text-gray-900 dark:text-white mb-1">{action.title}</h3>
                    <p className="text-sm text-gray-500 dark:text-gray-400">{action.desc}</p>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {activeTab === 'sessions' && (
          <div>
            <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">Active Sessions</h2>
            <SectionCard>
              {loadingData ? (
                <div className="space-y-3">
                  {Array(3).fill(0).map((_, i) => (
                    <div key={i} className="h-12 bg-gray-200 dark:bg-gray-700 rounded animate-pulse" />
                  ))}
                </div>
              ) : openSessions.length > 0 ? (
                <Table
                  columns={[
                    { label: 'Session ID', key: 'tokenPreview' },
                    { label: 'Course', key: 'className' },
                    { label: 'Delegate', key: 'delegateName' },
                    { label: 'Date', key: 'date' },
                    { label: 'Time', key: 'startTime' },
                    { label: 'Status', render: (row) => <StatusBadge status={row.status} /> }
                  ]}
                  data={openSessions}
                />
              ) : (
                <EmptyState icon="event_busy" title="No Sessions" message="No active sessions at the moment" />
              )}
            </SectionCard>
          </div>
        )}
      </main>

      {/* Export Modal */}
      <Modal
        isOpen={showExportModal}
        title="Export Attendance Data"
        onClose={() => setShowExportModal(false)}
        size="lg"
      >
        <div className="space-y-6">
          <div className="p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-800">
            <p className="text-sm text-blue-900 dark:text-blue-200">
              Export attendance records with optional filtering by course and date range.
              Choose your preferred format (CSV, Excel, or Summary Report).
            </p>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Filter by Course (Optional)
            </label>
            <input
              type="text"
              placeholder="Course code (e.g., CS101)"
              value={exportFilters.courseCode}
              onChange={(e) => setExportFilters({ ...exportFilters, courseCode: e.target.value })}
              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                Start Date (Optional)
              </label>
              <input
                type="date"
                value={exportFilters.startDate}
                onChange={(e) => setExportFilters({ ...exportFilters, startDate: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                End Date (Optional)
              </label>
              <input
                type="date"
                value={exportFilters.endDate}
                onChange={(e) => setExportFilters({ ...exportFilters, endDate: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
              Export Format
            </label>
            <div className="space-y-3">
              <button
                onClick={() => handleExportData('csv')}
                disabled={exportLoading}
                className="w-full p-4 text-left rounded-lg border-2 border-blue-300 dark:border-blue-700 bg-blue-50 dark:bg-blue-900/20 hover:bg-blue-100 dark:hover:bg-blue-900/30 transition-colors disabled:opacity-50"
              >
                <p className="font-medium text-gray-900 dark:text-white">CSV Format</p>
                <p className="text-xs text-gray-600 dark:text-gray-400">Universal compatibility</p>
              </button>

              <button
                onClick={() => handleExportData('xlsx')}
                disabled={exportLoading}
                className="w-full p-4 text-left rounded-lg border-2 border-green-300 dark:border-green-700 bg-green-50 dark:bg-green-900/20 hover:bg-green-100 dark:hover:bg-green-900/30 transition-colors disabled:opacity-50"
              >
                <p className="font-medium text-gray-900 dark:text-white">Excel Format</p>
                <p className="text-xs text-gray-600 dark:text-gray-400">Direct Excel compatibility</p>
              </button>

              <button
                onClick={() => handleExportData('summary')}
                disabled={exportLoading}
                className="w-full p-4 text-left rounded-lg border-2 border-purple-300 dark:border-purple-700 bg-purple-50 dark:bg-purple-900/20 hover:bg-purple-100 dark:hover:bg-purple-900/30 transition-colors disabled:opacity-50"
              >
                <p className="font-medium text-gray-900 dark:text-white">Summary Report</p>
                <p className="text-xs text-gray-600 dark:text-gray-400">Course & student statistics</p>
              </button>
            </div>
          </div>

          {exportLoading && (
            <div className="text-center py-4">
              <p className="text-sm text-gray-600 dark:text-gray-400">Generating export file...</p>
            </div>
          )}

          <div className="flex justify-end gap-3">
            <Button variant="outline" onClick={() => setShowExportModal(false)} disabled={exportLoading}>
              Cancel
            </Button>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default AdminDashboard;
