              <span className="material-symbols-outlined text-lg text-purple-300 group-hover:text-purple-200 transition-colors">assignment_ind</span>
              <span className="text-sm">Delegates</span>
            </button>
            
            {/* Classes & Courses */}
            <button
              onClick={() => window.location.href = '/admin/classes'}
