              <span className="material-symbols-outlined text-lg text-emerald-300 group-hover:text-emerald-200 transition-colors">class</span>
              <span className="text-sm">Classes & Courses</span>
            </button>
            
            {/* Sessions */}
            <button
              onClick={() => window.location.href = '/admin/sessions'}
              className="w-full flex items-center gap-3 px-3 py-2.5 rounded-lg text-slate-200 hover:bg-slate-700/50 transition-all duration-200 group font-medium"
