              <span className="material-symbols-outlined text-lg text-amber-300 group-hover:text-amber-200 transition-colors">event_available</span>
              <span className="text-sm">Attendance Sessions</span>
            </button>
            
            {/* Reports */}
            <button
              onClick={() => window.location.href = '/admin/reports'}
              className="w-full flex items-center gap-3 px-3 py-2.5 rounded-lg text-slate-200 hover:bg-slate-700/50 transition-all duration-200 group font-medium"
