import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';

const AdminDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();

  // State
  const [stats, setStats] = useState({
    totalStudents: 0,
    totalCourses: 0,
    todayAttendance: 0,
  });

  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [userFilter, setUserFilter] = useState('all');
  const [liveSessions, setLiveSessions] = useState([]);
  const [loading2, setLoading2] = useState(true);

  // Fetch data on mount
  useEffect(() => {
    if (!loading && (!currentUser || userRole !== 'admin')) {
      navigate('/login');
      return;
    }
    if (!loading && currentUser && userRole === 'admin') {
      fetchDashboardData();
    }
  }, [currentUser, userRole, loading, navigate]);

  // Filter users when filter changes
  useEffect(() => {
    if (userFilter === 'all') {
      setFilteredUsers(users);
    } else {
      setFilteredUsers(users.filter(u => u.role === userFilter));
    }
  }, [userFilter, users]);

  const fetchDashboardData = async () => {
    try {
      setLoading2(true);

      // Fetch stats
      const [studentsRes, coursesRes, usersRes, sessionsRes] = await Promise.all([
        supabase.from('users').select('id', { count: 'exact' }).eq('role', 'student'),
        supabase.from('courses').select('id', { count: 'exact' }),
        supabase.from('users').select('*').order('created_at', { ascending: false }),
        supabase.from('sessions').select('*').order('created_at', { ascending: false }).limit(10),
      ]);

      setStats({
        totalStudents: studentsRes.count || 0,
        totalCourses: coursesRes.count || 0,
        todayAttendance: Math.floor(Math.random() * 100),
      });

      // Set users
      setUsers(usersRes.data || []);
      setFilteredUsers(usersRes.data || []);

      // Get live sessions
      const today = new Date().toISOString().split('T')[0];
      const activeSessions = (sessionsRes.data || [])
        .filter(s => s.session_date === today)
        .slice(0, 3);

      const sessionsWithDetails = await Promise.all(
        activeSessions.map(async (session) => {
          const classRes = await supabase
            .from('classes')
            .select('class_name, course_id')
            .eq('id', session.class_id)
            .single();

          const courseRes = classRes?.data
            ? await supabase
                .from('courses')
                .select('course_code, course_title')
                .eq('id', classRes.data.course_id)
                .single()
            : null;

          const attendanceRes = await supabase
            .from('attendance')
            .select('id', { count: 'exact' })
            .eq('session_id', session.id);

          return {
            ...session,
            course_code: courseRes?.data?.course_code || 'N/A',
            course_title: courseRes?.data?.course_title || 'Unknown',
            instructor: 'Professor Smith',
            room: 'Room 402',
            attendeeCount: attendanceRes.count || 0,
          };
        })
      );

      setLiveSessions(sessionsWithDetails);
    } catch (err) {
      console.error('Error fetching dashboard data:', err);
    } finally {
      setLoading2(false);
    }
  };

  if (loading || loading2) {
    return (
      <div className="flex items-center justify-center h-screen bg-background-light dark:bg-background-dark">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border border-gray-300 border-t-primary mb-4" />
          <p className="text-gray-600 dark:text-gray-400">Loading...</p>
        </div>
      </div>
    );
  }

  if (!currentUser || userRole !== 'admin') {
    return null;
  }

  return (
    <div className="min-h-screen bg-background-light dark:bg-background-dark font-display text-gray-900 dark:text-white transition-colors duration-200">
      {/* ========== TOP NAVIGATION BAR ========== */}
      <header className="sticky top-0 z-40 bg-white dark:bg-background-dark border-b border-gray-200 dark:border-gray-700 backdrop-blur-sm">
        <div className="max-w-xl mx-auto px-4 py-4 flex items-center justify-between">
          {/* Left: Logo/Title */}
          <div className="flex items-center gap-3">
            <div className="size-10 rounded-lg bg-gradient-to-br from-primary to-primary/70 flex items-center justify-center text-white font-bold">
              <span className="material-symbols-outlined text-lg">admin_panel_settings</span>
            </div>
            <div>
              <p className="font-bold text-sm">Admin Central</p>
              <p className="text-xs text-gray-500 dark:text-gray-400">University Portal</p>
            </div>
          </div>

          {/* Right: Notification + Avatar */}
          <div className="flex items-center gap-3">
            <button className="relative p-2 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors">
              <span className="material-symbols-outlined text-gray-700 dark:text-gray-300">
                notifications
              </span>
              <span className="absolute top-1 right-1 size-2.5 bg-red-500 rounded-full animate-pulse" />
            </button>
            <div className="size-8 rounded-full bg-gradient-to-br from-primary to-primary/70 flex items-center justify-center text-white text-xs font-bold">
              AD
            </div>
          </div>
        </div>
      </header>

      {/* ========== MAIN CONTENT ========== */}
      <main className="pb-32">
        <div className="max-w-xl mx-auto px-4 py-6 space-y-6">
          {/* ========== SECTION 1: STATS CARDS ========== */}
          <section className="space-y-3">
            {/* Students Card */}
            <div className="rounded-xl bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-700 shadow-sm p-4">
              <div className="flex items-start justify-between mb-3">
                <div>
                  <p className="text-xs text-gray-500 dark:text-gray-400 font-medium mb-1">Total Students</p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">{stats.totalStudents}</p>
                </div>
                <div className="flex flex-col items-end">
                  <span className="material-symbols-outlined text-primary text-2xl">
                    group
                  </span>
                  <span className="text-xs font-bold text-green-600 dark:text-green-400 mt-1">+5%</span>
                </div>
              </div>
            </div>

            {/* Courses Card */}
            <div className="rounded-xl bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-700 shadow-sm p-4">
              <div className="flex items-start justify-between mb-3">
                <div>
                  <p className="text-xs text-gray-500 dark:text-gray-400 font-medium mb-1">Total Courses</p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">{stats.totalCourses}</p>
                </div>
                <div className="flex flex-col items-end">
                  <span className="material-symbols-outlined text-primary text-2xl">
                    school
                  </span>
                  <span className="text-xs font-bold text-green-600 dark:text-green-400 mt-1">+2%</span>
                </div>
              </div>
            </div>

            {/* Today's Attendance - Full Width */}
            <div className="rounded-xl bg-gradient-to-br from-primary/10 to-primary/5 dark:from-primary/20 dark:to-primary/10 border border-primary/20 dark:border-primary/30 shadow-sm p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-xs text-gray-600 dark:text-gray-300 font-medium mb-1">Today's Attendance</p>
                  <p className="text-3xl font-bold text-primary">{stats.todayAttendance}%</p>
                </div>
                <div className="flex flex-col items-center">
                  <div className="size-12 rounded-full border-4 border-primary/30 flex items-center justify-center text-primary font-bold text-sm">
                    {stats.todayAttendance}
                  </div>
                </div>
              </div>
            </div>
          </section>

          {/* ========== SECTION 2: LIVE SESSIONS ========== */}
          <section className="space-y-3">
            <div className="flex items-center justify-between px-0">
              <h2 className="text-lg font-bold">Live Sessions</h2>
              <button className="text-primary text-sm font-bold hover:text-primary/80 transition-colors">
                View all
              </button>
            </div>

            {liveSessions.length > 0 ? (
              liveSessions.map((session) => (
                <div
                  key={session.id}
                  className="rounded-xl bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-700 shadow-sm p-4"
                >
                  <div className="flex items-start justify-between">
                    {/* Left Content */}
                    <div className="flex-1">
                      {/* Badge + Room */}
                      <div className="flex items-center gap-2 mb-2">
                        <span className="inline-flex items-center gap-1.5 px-2 py-0.5 rounded-full bg-red-100 dark:bg-red-900/30">
                          <span className="size-1.5 bg-red-500 rounded-full animate-pulse" />
                          <span className="text-[10px] font-bold text-red-600 dark:text-red-400 uppercase">
                            Live
                          </span>
                        </span>
                        <p className="text-xs text-gray-500 dark:text-gray-400">{session.room}</p>
                      </div>

                      {/* Course Title */}
                      <p className="font-bold text-base mb-1">
                        [{session.course_code}] {session.course_title}
                      </p>

                      {/* Meta Info */}
                      <p className="text-xs text-gray-500 dark:text-gray-400 mb-3">
                        {session.instructor}  {session.attendeeCount} attending
                      </p>

                      {/* Action Button */}
                      <button className="w-full px-4 py-2 rounded-lg bg-primary text-white text-xs font-bold hover:bg-primary/90 transition-colors active:scale-95">
                        Join Session
                      </button>
                    </div>

                    {/* Right: Chevron Icon */}
                    <button className="ml-4 p-2 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors">
                      <span className="material-symbols-outlined text-gray-400 dark:text-gray-500">
                        chevron_right
                      </span>
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <div className="rounded-xl bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-700 shadow-sm p-8 text-center">
                <p className="text-gray-500 dark:text-gray-400 text-sm">No active sessions</p>
              </div>
            )}
          </section>

          {/* ========== SECTION 3: USER MANAGEMENT ========== */}
          <section className="space-y-3">
            {/* Header with "View all" */}
            <div className="flex items-center justify-between px-0">
              <h2 className="text-lg font-bold">User Management</h2>
              <button className="text-primary text-sm font-bold hover:text-primary/80 transition-colors">
                View all
              </button>
            </div>

            {/* Role Filter Pills */}
            <div className="flex items-center gap-2 overflow-x-auto pb-1 -mx-4 px-4 no-scrollbar">
              <button
                onClick={() => setUserFilter('all')}
                className={`px-4 py-2 rounded-full text-sm font-bold whitespace-nowrap transition-colors ${
                  userFilter === 'all'
                    ? 'bg-primary text-white'
                    : 'bg-gray-100 dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700'
                }`}
              >
                All Users
              </button>
              <button
                onClick={() => setUserFilter('student')}
                className={`px-4 py-2 rounded-full text-sm font-bold whitespace-nowrap transition-colors ${
                  userFilter === 'student'
                    ? 'bg-green-500 text-white'
                    : 'bg-gray-100 dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700'
                }`}
              >
                Students
              </button>
              <button
                onClick={() => setUserFilter('delegate')}
                className={`px-4 py-2 rounded-full text-sm font-bold whitespace-nowrap transition-colors ${
                  userFilter === 'delegate'
                    ? 'bg-purple-500 text-white'
                    : 'bg-gray-100 dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700'
                }`}
              >
                Delegates
              </button>
              <button
                onClick={() => setUserFilter('admin')}
                className={`px-4 py-2 rounded-full text-sm font-bold whitespace-nowrap transition-colors ${
                  userFilter === 'admin'
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-100 dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700'
                }`}
              >
                Admins
              </button>
            </div>

            {/* User List */}
            <div className="rounded-xl bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-700 shadow-sm overflow-hidden">
              <div className="divide-y divide-gray-200 dark:divide-gray-700">
                {filteredUsers.length > 0 ? (
                  filteredUsers.slice(0, 5).map((user, idx) => (
                    <div key={user.id} className="p-4 flex items-center justify-between hover:bg-gray-50 dark:hover:bg-gray-800/50 transition-colors">
                      {/* Left: Avatar + Info */}
                      <div className="flex items-center gap-3 flex-1 min-w-0">
                        <div className="size-10 rounded-full bg-gradient-to-br from-primary to-primary/70 flex items-center justify-center text-white text-sm font-bold flex-shrink-0">
                          {user.name?.charAt(0).toUpperCase() || 'U'}
                        </div>
                        <div className="min-w-0 flex-1">
                          <p className="font-bold text-sm text-gray-900 dark:text-white truncate">
                            {user.name || 'Unknown'}
                          </p>
                          <div className="flex items-center gap-2 mt-0.5">
                            <span
                              className={`text-[10px] font-bold px-2 py-0.5 rounded-full ${
                                user.role === 'student'
                                  ? 'bg-green-100 dark:bg-green-900/30 text-green-700 dark:text-green-400'
                                  : user.role === 'delegate'
                                  ? 'bg-purple-100 dark:bg-purple-900/30 text-purple-700 dark:text-purple-400'
                                  : 'bg-blue-100 dark:bg-blue-900/30 text-blue-700 dark:text-blue-400'
                              }`}
                            >
                              {user.role?.charAt(0).toUpperCase() + user.role?.slice(1) || 'User'}
                            </span>
                            <p className="text-xs text-gray-500 dark:text-gray-400 truncate">
                              {user.matricule || 'N/A'}
                            </p>
                          </div>
                        </div>
                      </div>

                      {/* Right: Menu Button */}
                      <button className="ml-2 p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors flex-shrink-0">
                        <span className="material-symbols-outlined text-gray-400 dark:text-gray-500 text-lg">
                          more_vert
                        </span>
                      </button>
                    </div>
                  ))
                ) : (
                  <div className="p-8 text-center">
                    <p className="text-gray-500 dark:text-gray-400 text-sm">No users found</p>
                  </div>
                )}
              </div>
            </div>
          </section>
        </div>
      </main>

      {/* ========== FLOATING ACTION BUTTON ========== */}
      <button className="fixed bottom-24 right-6 z-40 size-14 rounded-full bg-primary text-white shadow-2xl flex items-center justify-center hover:bg-primary/90 active:scale-90 transition-all">
        <span className="material-symbols-outlined text-2xl">person_add</span>
      </button>

      {/* ========== BOTTOM NAVIGATION BAR ========== */}
      <nav className="fixed bottom-0 left-0 right-0 z-40 bg-white dark:bg-background-dark border-t border-gray-200 dark:border-gray-700 backdrop-blur-sm">
        <div className="max-w-xl mx-auto px-4 py-3">
          <div className="flex items-center justify-around pb-2">
            {/* Dashboard */}
            <button className="flex flex-col items-center gap-1 py-2 px-4 text-primary transition-colors">
              <span
                className="material-symbols-outlined text-xl"
                style={{ fontVariationSettings: "'FILL' 1" }}
              >
                dashboard
              </span>
              <span className="text-xs font-bold">Dash</span>
            </button>

            {/* Users */}
            <button className="flex flex-col items-center gap-1 py-2 px-4 text-gray-500 dark:text-gray-400 hover:text-primary transition-colors">
              <span className="material-symbols-outlined text-xl">group</span>
              <span className="text-xs font-medium">Users</span>
            </button>

            {/* Schedule */}
            <button className="flex flex-col items-center gap-1 py-2 px-4 text-gray-500 dark:text-gray-400 hover:text-primary transition-colors">
              <span className="material-symbols-outlined text-xl">schedule</span>
              <span className="text-xs font-medium">Schedule</span>
            </button>

            {/* Setup */}
            <button className="flex flex-col items-center gap-1 py-2 px-4 text-gray-500 dark:text-gray-400 hover:text-primary transition-colors">
              <span className="material-symbols-outlined text-xl">settings</span>
              <span className="text-xs font-medium">Setup</span>
            </button>
          </div>
          {/* iOS Home Indicator Spacing */}
          <div className="h-2" />
        </div>
      </nav>
    </div>
  );
};

export default AdminDashboard;
