import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Button from '../components/Button';
import Badge from '../components/Badge';
import Table from '../components/Table';
import StatCard from '../components/StatCard';

const DelegateDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (!loading) {
      if (!currentUser || (userRole && userRole !== 'delegate')) {
        navigate('/delegate');
      }
    }
  }, [userRole, loading, currentUser, navigate]);

  const [assignedClass] = useState('Advanced Web Development');
  const [todaysCourses] = useState([
    { id: 1, name: 'Advanced Web Dev', time: '09:00-11:00' },
    { id: 2, name: 'Database Design', time: '14:00-16:00' },
  ]);

  const [stats] = useState({
    activeSession: 'Advanced Web Dev',
    presentCount: 28,
    totalCount: 35,
  });

  const [recentSessions] = useState([
    { id: 1, course: 'Advanced Web Dev', date: '2025-12-18', time: '09:00', students: 28, status: 'closed' },
    { id: 2, course: 'Database Design', date: '2025-12-17', time: '14:00', students: 32, status: 'closed' },
    { id: 3, course: 'Python Programming', date: '2025-12-17', time: '11:00', students: 30, status: 'closed' },
  ]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-indigo-50 p-4">
      <div className="max-w-3xl mx-auto">
        <div className="mb-6 flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Delegate Dashboard</h1>
            <p className="text-gray-600">üìç Create GPS-Based Attendance Sessions</p>
            {currentUser && <p className="text-sm text-gray-500 mt-1">üë§ {currentUser.email}</p>}
          </div>
          <button
            onClick={handleLogout}
            className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg font-medium transition"
          >
            Logout
          </button>
        </div>

        {message && (
          <div
            className={`rounded-lg p-4 mb-6 text-white whitespace-pre-wrap ${
              messageType === 'success'
                ? 'bg-green-500'
                : messageType === 'error'
                ? 'bg-red-500'
                : 'bg-blue-500'
            }`}
          >
            {message}
          </div>
        )}

        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <h2 className="text-xl font-bold text-gray-900 mb-6">üìÖ Create New Session</h2>

          <form onSubmit={createSession} className="space-y-4">
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Course Name
              </label>
              <input
                type="text"
                value={courseName}
                onChange={(e) => setCourseName(e.target.value)}
                disabled={isLoading}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:bg-gray-100"
                placeholder="e.g., Advanced Web Development"
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Class Start Time
                </label>
                <input
                  type="time"
                  value={classStartTime}
                  onChange={(e) => setClassStartTime(e.target.value)}
                  disabled={isLoading}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:bg-gray-100"
                />
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Class End Time
                </label>
                <input
                  type="time"
                  value={classEndTime}
                  onChange={(e) => setClassEndTime(e.target.value)}
                  disabled={isLoading}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:bg-gray-100"
                />
              </div>
            </div>

            <div className="bg-indigo-50 border border-indigo-200 rounded-lg p-4">
              <h3 className="font-semibold text-indigo-900 mb-2">üìç How GPS Attendance Works:</h3>
              <ul className="text-sm text-indigo-800 space-y-1">
                <li>‚úì Students must enable GPS on their phone</li>
                <li>‚úì Location must be verified at ICT University</li>
                <li>‚úì GPS accuracy must be ‚â§50 meters</li>
                <li>‚úì No QR code scanning needed</li>
              </ul>
            </div>

            <button
              type="submit"
              disabled={isLoading}
              className="w-full bg-indigo-600 hover:bg-indigo-700 disabled:bg-gray-400 text-white font-semibold py-3 rounded-lg transition"
            >
              {isLoading ? '‚è≥ Creating...' : 'üìÖ Create Session'}
            </button>
          </form>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">üìã Active Sessions ({sessions.length})</h2>

          {sessions.length === 0 ? (
            <p className="text-gray-600 text-center py-8">No sessions created yet</p>
          ) : (
            <div className="space-y-3">
              {sessions.map((session) => (
                <div
                  key={session.id}
                  className="border border-gray-200 rounded-lg p-4 hover:bg-gray-50 transition"
                >
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-semibold text-gray-900">{session.class_name || 'Session'}</h3>
                      <p className="text-sm text-gray-600">
                        ‚è∞ {session.class_start_time} - {session.class_end_time}
                      </p>
                      <p className="text-xs text-gray-500 mt-1">
                        üìÖ {new Date(session.session_date).toLocaleDateString()}
                      </p>
                    </div>
                    <span className="px-3 py-1 bg-green-100 text-green-800 text-xs font-semibold rounded-full">
                      Active
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DelegateDashboard;
