import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import AdminSidebar from '../components/AdminSidebar';
import AdminTopBar from '../components/AdminTopBar';
import Modal from '../components/Modal';
import Alert from '../components/Alert';
import Card from '../components/Card';

const AdminDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();

  // State
  const [activeTab, setActiveTab] = useState('overview');
  const [stats, setStats] = useState({
    totalStudents: 0,
    totalInstructors: 0,
    totalCourses: 0,
    totalSessions: 0,
    todayAttendance: 0
  });

  const [students, setStudents] = useState([]);
  const [instructors, setInstructors] = useState([]);
  const [courses, setCourses] = useState([]);
  const [sessions, setSessions] = useState([]);
  const [attendanceData, setAttendanceData] = useState([]);

  // Modals
  const [showAddStudentModal, setShowAddStudentModal] = useState(false);
  const [showAddInstructorModal, setShowAddInstructorModal] = useState(false);
  const [showAddCourseModal, setShowAddCourseModal] = useState(false);
  const [showAddSessionModal, setShowAddSessionModal] = useState(false);

  // Form states
  const [studentForm, setStudentForm] = useState({
    name: '',
    matricule: '',
    email: '',
    password: ''
  });

  const [instructorForm, setInstructorForm] = useState({
    name: '',
    matricule: '',
    email: '',
    password: ''
  });

  const [courseForm, setCourseForm] = useState({
    course_code: '',
    course_title: '',
    program: '',
    year_level: ''
  });

  const [sessionForm, setSessionForm] = useState({
    session_date: '',
    start_time: '',
    end_time: '',
    class_id: '',
    venue: ''
  });

  // UI states
  const [loading2, setLoading2] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [showStudentPassword, setShowStudentPassword] = useState(false);
  const [showInstructorPassword, setShowInstructorPassword] = useState(false);
  const itemsPerPage = 10;

  useEffect(() => {
    if (loading) return;

    if (!currentUser || userRole !== 'admin') {
      navigate('/admin');
      return;
    }

    fetchAllData();
  }, [currentUser, userRole, loading, navigate]);

  const fetchAllData = async () => {
    try {
      setLoading2(true);
      setError(null);

      // Fetch stats
      const [studentsRes, instructorsRes, coursesRes, sessionsRes, attendanceRes] = await Promise.all([
        supabase.from('users').select('id', { count: 'exact' }).eq('role', 'student'),
        supabase.from('users').select('id', { count: 'exact' }).eq('role', 'delegate'),
        supabase.from('courses').select('id', { count: 'exact' }),
        supabase.from('sessions').select('id', { count: 'exact' }),
        supabase
          .from('attendance')
          .select('id', { count: 'exact' })
          .gte('created_at', new Date().toISOString().split('T')[0])
      ]);

      console.log('üìä Fetch stats - Students:', studentsRes.count, 'Instructors:', instructorsRes.count);

      setStats({
        totalStudents: studentsRes.count || 0,
        totalInstructors: instructorsRes.count || 0,
        totalCourses: coursesRes.count || 0,
        totalSessions: sessionsRes.count || 0,
        todayAttendance: attendanceRes.count || 0
      });

      // Fetch detailed data
      const [studentsDataRes, instructorsDataRes, coursesDataRes, sessionsDataRes, attendanceDataRes] = await Promise.all([
        supabase
          .from('users')
          .select('*')
          .eq('role', 'student')
          .order('created_at', { ascending: false }),
        supabase
          .from('users')
          .select('*')
          .eq('role', 'delegate')
          .order('created_at', { ascending: false }),
        supabase
          .from('courses')
          .select('*')
          .order('course_code', { ascending: true }),
        supabase
          .from('sessions')
          .select(`
            *,
            classes (
              class_name,
              courses (
                course_code,
                course_title
              )
            )
          `)
          .order('session_date', { ascending: false }),
        supabase
          .from('attendance')
          .select(`
            *,
            users (id, name, matricule),
            sessions (
              session_date,
              start_time,
              end_time,
              classes (
                courses (
                  course_code,
                  course_title
                )
              )
            )
          `)
          .order('created_at', { ascending: false })
          .limit(100)
      ]);

      console.log('üìã Detailed data - Students:', studentsDataRes.data?.length, 'Instructors:', instructorsDataRes.data?.length);

      if (studentsDataRes.error) console.error('Students fetch error:', studentsDataRes.error);
      if (instructorsDataRes.error) console.error('Instructors fetch error:', instructorsDataRes.error);

      setStudents(studentsDataRes.data || []);
      setInstructors(instructorsDataRes.data || []);
      setCourses(coursesDataRes.data || []);
      setSessions(sessionsDataRes.data || []);
      setAttendanceData(attendanceDataRes.data || []);
    } catch (err) {
      console.error('‚ùå Error fetching data:', err);
      setError('Failed to load dashboard data');
    } finally {
      setLoading2(false);
    }
  };

  const handleAddStudent = async () => {
    if (!studentForm.name || !studentForm.matricule || !studentForm.email || !studentForm.password) {
      setError('‚ùå Please fill in all required fields');
      return;
    }

    try {
      setLoading2(true);
      setError(null);

      // Step 1: Create auth user
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email: studentForm.email,
        password: studentForm.password,
        options: {
          data: {
            role: 'student'
          }
        }
      });

      if (authError) {
        throw new Error(authError.message || 'Failed to create auth user');
      }

      if (!authData.user) {
        throw new Error('No user data returned from auth signup');
      }

      // Step 2: Insert into users table with RLS bypass using service role
      const { error: userError } = await supabase
        .from('users')
        .insert([{
          id: authData.user.id,
          name: studentForm.name,
          matricule: studentForm.matricule,
          email: studentForm.email,
          role: 'student'
        }]);

      if (userError) {
        console.error('User insert error details:', userError);
        throw new Error(userError.message || 'Failed to create user record in database');
      }

      setSuccess('‚úÖ Student added successfully!');
      setShowAddStudentModal(false);
      setStudentForm({ name: '', matricule: '', email: '', password: '' });
      setShowStudentPassword(false);
      
      // Refresh data
      await new Promise(resolve => setTimeout(resolve, 1000)); // Wait 1 second for DB sync
      await fetchAllData();
      
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error adding student:', err);
      setError(`‚ùå Error: ${err.message}`);
      setTimeout(() => setError(null), 5000);
    } finally {
      setLoading2(false);
    }
  };

  const handleAddInstructor = async () => {
    if (!instructorForm.name || !instructorForm.matricule || !instructorForm.email || !instructorForm.password) {
      setError('‚ùå Please fill in all required fields');
      return;
    }

    try {
      setLoading2(true);
      setError(null);

      // Step 1: Create auth user
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email: instructorForm.email,
        password: instructorForm.password,
        options: {
          data: {
            role: 'delegate'
          }
        }
      });

      if (authError) {
        throw new Error(authError.message || 'Failed to create auth user');
      }

      if (!authData.user) {
        throw new Error('No user data returned from auth signup');
      }

      // Step 2: Insert into users table
      const { error: userError } = await supabase
        .from('users')
        .insert([{
          id: authData.user.id,
          name: instructorForm.name,
          matricule: instructorForm.matricule,
          email: instructorForm.email,
          role: 'delegate'
        }]);

      if (userError) {
        console.error('User insert error details:', userError);
        throw new Error(userError.message || 'Failed to create user record in database');
      }

      setSuccess('‚úÖ Instructor added successfully!');
      setShowAddInstructorModal(false);
      setInstructorForm({ name: '', matricule: '', email: '', password: '' });
      setShowInstructorPassword(false);
      
      // Refresh data
      await new Promise(resolve => setTimeout(resolve, 1000)); // Wait 1 second for DB sync
      await fetchAllData();
      
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error adding instructor:', err);
      setError(`‚ùå Error: ${err.message}`);
      setTimeout(() => setError(null), 5000);
    } finally {
      setLoading2(false);
    }
  };

  const handleAddCourse = async () => {
    if (!courseForm.course_code || !courseForm.course_title || !courseForm.program) {
      setError('‚ùå Please fill in all required fields');
      return;
    }

    try {
      setLoading2(true);
      setError(null);

      const { error } = await supabase
        .from('courses')
        .insert({
          course_code: courseForm.course_code,
          course_title: courseForm.course_title,
          program: courseForm.program,
          year_level: courseForm.year_level
        });

      if (error) {
        throw new Error(error.message || 'Failed to add course');
      }

      setSuccess('‚úÖ Course added successfully!');
      setShowAddCourseModal(false);
      setCourseForm({ course_code: '', course_title: '', program: '', year_level: '' });
      
      // Refresh data
      await fetchAllData();
      
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error adding course:', err);
      setError(`‚ùå Error: ${err.message}`);
      setTimeout(() => setError(null), 5000);
    } finally {
      setLoading2(false);
    }
  };

  const handleDeleteStudent = async (studentId) => {
    if (!window.confirm('Are you sure? This action cannot be undone.')) return;

    try {
      const { error } = await supabase
        .from('users')
        .delete()
        .eq('id', studentId);

      if (error) throw error;

      setSuccess('‚úÖ Student deleted successfully!');
      fetchAllData();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error deleting student:', err);
      setError(`‚ùå Error: ${err.message}`);
    }
  };

  const handleDeleteInstructor = async (instructorId) => {
    if (!window.confirm('Are you sure? This action cannot be undone.')) return;

    try {
      const { error } = await supabase
        .from('users')
        .delete()
        .eq('id', instructorId);

      if (error) throw error;

      setSuccess('‚úÖ Instructor deleted successfully!');
      fetchAllData();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error deleting instructor:', err);
      setError(`‚ùå Error: ${err.message}`);
    }
  };

  const handleDeleteCourse = async (courseId) => {
    if (!window.confirm('Are you sure? This action cannot be undone.')) return;

    try {
      const { error } = await supabase
        .from('courses')
        .delete()
        .eq('id', courseId);

      if (error) throw error;

      setSuccess('‚úÖ Course deleted successfully!');
      fetchAllData();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error deleting course:', err);
      setError(`‚ùå Error: ${err.message}`);
    }
  };

  if (loading || loading2) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border border-gray-300 border-t-blue-900 mb-4" />
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  if (!currentUser || userRole !== 'admin') return null;

  // Filter data based on search
  const filteredStudents = students.filter(s =>
    s.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    s.matricule.toLowerCase().includes(searchQuery.toLowerCase()) ||
    s.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const filteredInstructors = instructors.filter(i =>
    i.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    i.matricule.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Pagination
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedStudents = filteredStudents.slice(startIndex, startIndex + itemsPerPage);
  const paginatedInstructors = filteredInstructors.slice(startIndex, startIndex + itemsPerPage);

  return (
    <div className="min-h-screen bg-gray-50">
      <AdminSidebar activeTab={activeTab} onTabChange={setActiveTab} />
      <AdminTopBar />

      <div className="ml-20 mt-16 px-6 sm:px-8 py-8 max-w-7xl">
        {error && (
          <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded text-red-800 text-sm">
            {error}
          </div>
        )}
        {success && (
          <div className="mb-6 p-4 bg-green-50 border border-green-200 rounded text-green-800 text-sm">
            {success}
          </div>
        )}

        {/* ===== OVERVIEW TAB ===== */}
        {activeTab === 'overview' && (
          <div>
            <div className="mb-8">
              <h1 className="text-3xl font-semibold text-gray-900">System Overview</h1>
              <p className="text-gray-600 text-sm mt-2">Central administration dashboard</p>
            </div>

            {/* Stats Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
              {[
                { label: 'Total Students', value: stats.totalStudents, color: 'blue-900' },
                { label: 'Total Instructors', value: stats.totalInstructors, color: 'green-700' },
                { label: 'Total Courses', value: stats.totalCourses, color: 'amber-700' },
                { label: 'Total Sessions', value: stats.totalSessions, color: 'purple-700' }
              ].map((stat, idx) => (
                <div key={idx} className="bg-white border border-gray-200 rounded shadow-sm p-6">
                  <p className="text-xs text-gray-600 font-semibold uppercase tracking-wide mb-2">
                    {stat.label}
                  </p>
                  <p className={`text-3xl font-semibold text-${stat.color}`}>
                    {stat.value}
                  </p>
                </div>
              ))}
            </div>

            {/* Quick Actions */}
            <div className="bg-white border border-gray-200 rounded shadow-sm p-6">
              <h2 className="text-lg font-medium text-gray-900 mb-6">Quick Actions</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {[
                  { label: 'Add Student', action: () => { setActiveTab('students'); setShowAddStudentModal(true); }, color: 'blue-900' },
                  { label: 'Add Instructor', action: () => { setActiveTab('instructors'); setShowAddInstructorModal(true); }, color: 'green-700' },
                  { label: 'Add Course', action: () => { setActiveTab('courses'); setShowAddCourseModal(true); }, color: 'amber-700' },
                  { label: 'View Attendance', action: () => setActiveTab('attendance'), color: 'purple-700' }
                ].map((action, idx) => (
                  <button
                    key={idx}
                    onClick={action.action}
                    style={{ borderColor: `var(--color-${action.color})` }}
                    className={`px-4 py-3 border rounded text-sm font-medium text-${action.color} bg-white hover:bg-gray-50 transition-colors`}
                  >
                    {action.label}
                  </button>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* ===== STUDENTS TAB ===== */}
        {activeTab === 'students' && (
          <div>
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-6">
              <div>
                <h1 className="text-2xl font-semibold text-gray-900">Student Management</h1>
                <p className="text-gray-600 text-sm mt-1">{students.length} students in system</p>
              </div>
              <div className="flex gap-3 mt-4 sm:mt-0">
                <button
                  onClick={() => fetchAllData()}
                  className="px-4 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 transition-colors"
                >
                  Refresh
                </button>
                <button
                  onClick={() => setShowAddStudentModal(true)}
                  className="px-4 py-2 bg-blue-900 text-white rounded text-sm font-medium hover:bg-blue-800 transition-colors"
                >
                  + Add Student
                </button>
              </div>
            </div>

            {/* Search */}
            <div className="mb-6">
              <input
                type="text"
                placeholder="Search by name, matricule, or email..."
                value={searchQuery}
                onChange={(e) => {
                  setSearchQuery(e.target.value);
                  setCurrentPage(1);
                }}
                className="w-full px-4 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
              />
            </div>

            {/* Students Table */}
            <div className="bg-white border border-gray-200 rounded shadow-sm overflow-hidden">
              {paginatedStudents.length > 0 ? (
                <>
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead>
                        <tr className="bg-gray-50 border-b border-gray-200">
                          <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Name</th>
                          <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Matricule</th>
                          <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Email</th>
                          <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Joined</th>
                          <th className="px-6 py-3 text-center text-xs font-semibold text-gray-600 uppercase tracking-wide">Action</th>
                        </tr>
                      </thead>
                      <tbody className="divide-y divide-gray-200">
                        {paginatedStudents.map((student) => (
                          <tr key={student.id} className="hover:bg-gray-50 transition-colors">
                            <td className="px-6 py-4 text-sm font-medium text-gray-900">{student.name}</td>
                            <td className="px-6 py-4 text-sm text-gray-600 font-mono">{student.matricule}</td>
                            <td className="px-6 py-4 text-sm text-gray-600">{student.email}</td>
                            <td className="px-6 py-4 text-sm text-gray-600">{new Date(student.created_at).toLocaleDateString()}</td>
                            <td className="px-6 py-4 text-center">
                              <button
                                onClick={() => handleDeleteStudent(student.id)}
                                className="px-3 py-1 bg-red-50 text-red-700 rounded text-xs font-medium hover:bg-red-100 transition-colors"
                              >
                                Delete
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>

                  {/* Pagination */}
                  <div className="px-6 py-4 border-t border-gray-200 flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
                    <p className="text-xs text-gray-600">
                      Showing {startIndex + 1} to {Math.min(startIndex + itemsPerPage, filteredStudents.length)} of {filteredStudents.length}
                    </p>
                    <div className="flex gap-2">
                      <button
                        disabled={currentPage === 1}
                        onClick={() => setCurrentPage(currentPage - 1)}
                        className="px-3 py-1 border border-gray-300 rounded text-sm disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50"
                      >
                        Previous
                      </button>
                      <button
                        disabled={startIndex + itemsPerPage >= filteredStudents.length}
                        onClick={() => setCurrentPage(currentPage + 1)}
                        className="px-3 py-1 border border-gray-300 rounded text-sm disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50"
                      >
                        Next
                      </button>
                    </div>
                  </div>
                </>
              ) : (
                <div className="px-6 py-12 text-center">
                  <p className="text-gray-600">
                    {searchQuery ? 'No students found' : 'No students registered'}
                  </p>
                </div>
              )}
            </div>
          </div>
        )}

        {/* ===== INSTRUCTORS TAB ===== */}
        {activeTab === 'instructors' && (
          <div>
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-6">
              <div>
                <h1 className="text-2xl font-semibold text-gray-900">Instructor Management</h1>
                <p className="text-gray-600 text-sm mt-1">{instructors.length} instructors in system</p>
              </div>
              <button
                onClick={() => setShowAddInstructorModal(true)}
                className="px-4 py-2 bg-green-700 text-white rounded text-sm font-medium hover:bg-green-800 transition-colors mt-4 sm:mt-0"
              >
                + Add Instructor
              </button>
            </div>

            {/* Search */}
            <div className="mb-6">
              <input
                type="text"
                placeholder="Search by name, matricule, or email..."
                value={searchQuery}
                onChange={(e) => {
                  setSearchQuery(e.target.value);
                  setCurrentPage(1);
                }}
                className="w-full px-4 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
              />
            </div>

            {/* Instructors Table */}
            <div className="bg-white border border-gray-200 rounded shadow-sm overflow-hidden">
              {paginatedInstructors.length > 0 ? (
                <>
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead>
                        <tr className="bg-gray-50 border-b border-gray-200">
                          <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Name</th>
                          <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Matricule</th>
                          <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Email</th>
                          <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Joined</th>
                          <th className="px-6 py-3 text-center text-xs font-semibold text-gray-600 uppercase tracking-wide">Action</th>
                        </tr>
                      </thead>
                      <tbody className="divide-y divide-gray-200">
                        {paginatedInstructors.map((instructor) => (
                          <tr key={instructor.id} className="hover:bg-gray-50 transition-colors">
                            <td className="px-6 py-4 text-sm font-medium text-gray-900">{instructor.name}</td>
                            <td className="px-6 py-4 text-sm text-gray-600 font-mono">{instructor.matricule}</td>
                            <td className="px-6 py-4 text-sm text-gray-600">{instructor.email}</td>
                            <td className="px-6 py-4 text-sm text-gray-600">{new Date(instructor.created_at).toLocaleDateString()}</td>
                            <td className="px-6 py-4 text-center">
                              <button
                                onClick={() => handleDeleteInstructor(instructor.id)}
                                className="px-3 py-1 bg-red-50 text-red-700 rounded text-xs font-medium hover:bg-red-100 transition-colors"
                              >
                                Delete
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>

                  {/* Pagination */}
                  <div className="px-6 py-4 border-t border-gray-200 flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
                    <p className="text-xs text-gray-600">
                      Showing {startIndex + 1} to {Math.min(startIndex + itemsPerPage, filteredInstructors.length)} of {filteredInstructors.length}
                    </p>
                    <div className="flex gap-2">
                      <button
                        disabled={currentPage === 1}
                        onClick={() => setCurrentPage(currentPage - 1)}
                        className="px-3 py-1 border border-gray-300 rounded text-sm disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50"
                      >
                        Previous
                      </button>
                      <button
                        disabled={startIndex + itemsPerPage >= filteredInstructors.length}
                        onClick={() => setCurrentPage(currentPage + 1)}
                        className="px-3 py-1 border border-gray-300 rounded text-sm disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50"
                      >
                        Next
                      </button>
                    </div>
                  </div>
                </>
              ) : (
                <div className="px-6 py-12 text-center">
                  <p className="text-gray-600">
                    {searchQuery ? 'No instructors found' : 'No instructors added'}
                  </p>
                </div>
              )}
            </div>
          </div>
        )}

        {/* ===== COURSES TAB ===== */}
        {activeTab === 'courses' && (
          <div>
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-6">
              <div>
                <h1 className="text-2xl font-semibold text-gray-900">Course Management</h1>
                <p className="text-gray-600 text-sm mt-1">{courses.length} courses in system</p>
              </div>
              <button
                onClick={() => setShowAddCourseModal(true)}
                className="px-4 py-2 bg-amber-700 text-white rounded text-sm font-medium hover:bg-amber-800 transition-colors mt-4 sm:mt-0"
              >
                + Add Course
              </button>
            </div>

            {/* Courses Table */}
            <div className="bg-white border border-gray-200 rounded shadow-sm overflow-hidden">
              {courses.length > 0 ? (
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="bg-gray-50 border-b border-gray-200">
                        <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Code</th>
                        <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Title</th>
                        <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Program</th>
                        <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Year</th>
                        <th className="px-6 py-3 text-center text-xs font-semibold text-gray-600 uppercase tracking-wide">Action</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200">
                      {courses.map((course) => (
                        <tr key={course.id} className="hover:bg-gray-50 transition-colors">
                          <td className="px-6 py-4 text-sm font-semibold text-gray-900 font-mono">{course.course_code}</td>
                          <td className="px-6 py-4 text-sm text-gray-900">{course.course_title}</td>
                          <td className="px-6 py-4 text-sm text-gray-600">{course.program}</td>
                          <td className="px-6 py-4 text-sm text-gray-600">{course.year_level || '-'}</td>
                          <td className="px-6 py-4 text-center">
                            <button
                              onClick={() => handleDeleteCourse(course.id)}
                              className="px-3 py-1 bg-red-50 text-red-700 rounded text-xs font-medium hover:bg-red-100 transition-colors"
                            >
                              Delete
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ) : (
                <div className="px-6 py-12 text-center">
                  <p className="text-gray-600">No courses added</p>
                </div>
              )}
            </div>
          </div>
        )}

        {/* ===== ATTENDANCE TAB ===== */}
        {activeTab === 'attendance' && (
          <div>
            <div className="mb-6">
              <h1 className="text-2xl font-semibold text-gray-900">Attendance Analytics</h1>
              <p className="text-gray-600 text-sm mt-1">View attendance records across sessions</p>
            </div>

            {/* Attendance Stats */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
              <div className="bg-white border border-gray-200 rounded shadow-sm p-6">
                <p className="text-xs text-gray-600 font-semibold uppercase tracking-wide mb-2">Total Records</p>
                <p className="text-3xl font-semibold text-blue-900">{attendanceData.length}</p>
              </div>
              <div className="bg-white border border-gray-200 rounded shadow-sm p-6">
                <p className="text-xs text-gray-600 font-semibold uppercase tracking-wide mb-2">Today's Attendance</p>
                <p className="text-3xl font-semibold text-green-700">{stats.todayAttendance}</p>
              </div>
            </div>

            {/* Attendance Table */}
            <div className="bg-white border border-gray-200 rounded shadow-sm overflow-hidden">
              <div className="px-6 py-4 border-b border-gray-200">
                <h2 className="text-base font-medium text-gray-900">Recent Attendance</h2>
              </div>

              {attendanceData.length > 0 ? (
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="bg-gray-50 border-b border-gray-200">
                        <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Student</th>
                        <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Course</th>
                        <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Date</th>
                        <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Time</th>
                        <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wide">Status</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200">
                      {attendanceData.slice(0, 50).map((record) => (
                        <tr key={record.id} className="hover:bg-gray-50 transition-colors">
                          <td className="px-6 py-4 text-sm font-medium text-gray-900">
                            {record.users?.name || 'Unknown'}
                          </td>
                          <td className="px-6 py-4 text-sm text-gray-600">
                            {record.sessions?.classes?.courses?.course_code || 'N/A'}
                          </td>
                          <td className="px-6 py-4 text-sm text-gray-600">
                            {record.sessions?.session_date || 'N/A'}
                          </td>
                          <td className="px-6 py-4 text-sm text-gray-600">
                            {new Date(record.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                          </td>
                          <td className="px-6 py-4 text-sm">
                            <span className={`px-3 py-1 rounded text-xs font-medium ${
                              record.status === 'present' 
                                ? 'bg-green-50 text-green-700' 
                                : 'bg-amber-50 text-amber-700'
                            }`}>
                              {record.status === 'present' ? 'Present' : 'Late'}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ) : (
                <div className="px-6 py-12 text-center">
                  <p className="text-gray-600">No attendance records</p>
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* ===== MODALS ===== */}

      {/* Add Student Modal */}
      <Modal
        isOpen={showAddStudentModal}
        title="Add New Student"
        onClose={() => setShowAddStudentModal(false)}
        size="md"
        footer={
          <>
            <button
              onClick={() => setShowAddStudentModal(false)}
              className="px-4 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              onClick={handleAddStudent}
              className="px-4 py-2 bg-blue-900 text-white rounded text-sm font-medium hover:bg-blue-800"
            >
              Add Student
            </button>
          </>
        }
      >
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Full Name *</label>
            <input
              type="text"
              value={studentForm.name}
              onChange={(e) => setStudentForm(prev => ({ ...prev, name: e.target.value }))}
              placeholder="Enter full name"
              className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Matricule *</label>
            <input
              type="text"
              value={studentForm.matricule}
              onChange={(e) => setStudentForm(prev => ({ ...prev, matricule: e.target.value }))}
              placeholder="Enter matricule"
              className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Email *</label>
            <input
              type="email"
              value={studentForm.email}
              onChange={(e) => setStudentForm(prev => ({ ...prev, email: e.target.value }))}
              placeholder="student@ictuniversity.edu.cm"
              className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Password (min 8 characters) *</label>
            <div className="relative">
              <input
                type={showStudentPassword ? 'text' : 'password'}
                value={studentForm.password}
                onChange={(e) => setStudentForm(prev => ({ ...prev, password: e.target.value }))}
                placeholder="Enter secure password"
                className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900 pr-10"
              />
              <button
                type="button"
                onClick={() => setShowStudentPassword(!showStudentPassword)}
                className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-500 hover:text-gray-700"
              >
                {showStudentPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
              </button>
            </div>
          </div>
        </div>
      </Modal>

      {/* Add Instructor Modal */}
      <Modal
        isOpen={showAddInstructorModal}
        title="Add New Instructor"
        onClose={() => setShowAddInstructorModal(false)}
        size="md"
        footer={
          <>
            <button
              onClick={() => setShowAddInstructorModal(false)}
              className="px-4 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              onClick={handleAddInstructor}
              className="px-4 py-2 bg-green-700 text-white rounded text-sm font-medium hover:bg-green-800"
            >
              Add Instructor
            </button>
          </>
        }
      >
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Full Name *</label>
            <input
              type="text"
              value={instructorForm.name}
              onChange={(e) => setInstructorForm(prev => ({ ...prev, name: e.target.value }))}
              placeholder="Enter full name"
              className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Matricule *</label>
            <input
              type="text"
              value={instructorForm.matricule}
              onChange={(e) => setInstructorForm(prev => ({ ...prev, matricule: e.target.value }))}
              placeholder="Enter matricule"
              className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Email *</label>
            <input
              type="email"
              value={instructorForm.email}
              onChange={(e) => setInstructorForm(prev => ({ ...prev, email: e.target.value }))}
              placeholder="instructor@ictuniversity.edu.cm"
              className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Password (min 8 characters) *</label>
            <div className="relative">
              <input
                type={showInstructorPassword ? 'text' : 'password'}
                value={instructorForm.password}
                onChange={(e) => setInstructorForm(prev => ({ ...prev, password: e.target.value }))}
                placeholder="Enter secure password"
                className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900 pr-10"
              />
              <button
                type="button"
                onClick={() => setShowInstructorPassword(!showInstructorPassword)}
                className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-500 hover:text-gray-700"
              >
                {showInstructorPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
              </button>
            </div>
          </div>
        </div>
      </Modal>

      {/* Add Course Modal */}
      <Modal
        isOpen={showAddCourseModal}
        title="Add New Course"
        onClose={() => setShowAddCourseModal(false)}
        size="md"
        footer={
          <>
            <button
              onClick={() => setShowAddCourseModal(false)}
              className="px-4 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              onClick={handleAddCourse}
              className="px-4 py-2 bg-amber-700 text-white rounded text-sm font-medium hover:bg-amber-800"
            >
              Add Course
            </button>
          </>
        }
      >
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Course Code *</label>
            <input
              type="text"
              value={courseForm.course_code}
              onChange={(e) => setCourseForm(prev => ({ ...prev, course_code: e.target.value }))}
              placeholder="e.g., ICT101"
              className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Course Title *</label>
            <input
              type="text"
              value={courseForm.course_title}
              onChange={(e) => setCourseForm(prev => ({ ...prev, course_title: e.target.value }))}
              placeholder="Enter course title"
              className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Program *</label>
            <input
              type="text"
              value={courseForm.program}
              onChange={(e) => setCourseForm(prev => ({ ...prev, program: e.target.value }))}
              placeholder="e.g., BSc. Computer Science"
              className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">Year Level</label>
            <input
              type="text"
              value={courseForm.year_level}
              onChange={(e) => setCourseForm(prev => ({ ...prev, year_level: e.target.value }))}
              placeholder="e.g., Year 1"
              className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-900"
            />
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default AdminDashboard;
