              <span className="material-symbols-outlined text-2xl">dashboard</span>
            </div>
            <div>
              <h1 className="font-bold text-lg text-white leading-tight">ICT Admin</h1>
              <p className="text-xs text-blue-200 mt-0.5 font-semibold">Admin Portal</p>
            </div>
          </div>
        </div>
        
        {/* Navigation */}
        <nav className="flex-1 overflow-y-auto px-3 py-6 space-y-2">
          {/* Main Navigation */}
          <div className="space-y-2 mb-6">
            <button onClick={() => window.location.href = '/admin/dashboard'} className="w-full flex items-center gap-3 px-4 py-3 rounded-lg bg-[#135bec] text-white font-semibold hover:bg-blue-700 transition-all duration-200 shadow-md hover:shadow-lg group">
              <span className="material-symbols-outlined text-xl group-hover:scale-110 transition-transform">dashboard</span>
              <span>Dashboard</span>
            </button>
            
            <button onClick={() => window.location.href = '/admin/students'} className="w-full flex items-center gap-3 px-4 py-3 rounded-lg text-slate-200 hover:bg-slate-700/50 transition-all duration-200 font-medium group">
              <span className="material-symbols-outlined text-xl text-blue-300 group-hover:text-blue-200">school</span>
              <span>Students</span>
            </button>
            
            <button onClick={() => window.location.href = '/admin/delegates'} className="w-full flex items-center gap-3 px-4 py-3 rounded-lg text-slate-200 hover:bg-slate-700/50 transition-all duration-200 font-medium group">
              <span className="material-symbols-outlined text-xl text-purple-300 group-hover:text-purple-200">assignment_ind</span>
              <span>Delegates</span>
            </button>
            
            <button onClick={() => window.location.href = '/admin/classes'} className="w-full flex items-center gap-3 px-4 py-3 rounded-lg text-slate-200 hover:bg-slate-700/50 transition-all duration-200 font-medium group">
              <span className="material-symbols-outlined text-xl text-emerald-300 group-hover:text-emerald-200">class</span>
              <span>Classes & Courses</span>
            </button>
            
            <button onClick={() => window.location.href = '/admin/sessions'} className="w-full flex items-center gap-3 px-4 py-3 rounded-lg text-slate-200 hover:bg-slate-700/50 transition-all duration-200 font-medium group">
              <span className="material-symbols-outlined text-xl text-amber-300 group-hover:text-amber-200">how_to_reg</span>
