          <input
            type="email"
            value={instructorForm.email}
            onChange={(e) => setInstructorForm(prev => ({ ...prev, email: e.target.value }))}
            placeholder="instructor@ictuniversity.edu.cm"
            className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-900 mb-2">Password (min 8 characters) *</label>
          <div className="relative">
            <input
              type={showInstructorPassword ? 'text' : 'password'}
              value={instructorForm.password}
              onChange={(e) => setInstructorForm(prev => ({ ...prev, password: e.target.value }))}
              placeholder="Enter secure password"
              className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec] pr-10"
            />
            <button
              type="button"
              onClick={() => setShowInstructorPassword(!showInstructorPassword)}
              className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-500 hover:text-gray-700"
            >
              {showInstructorPassword ? 'ðŸ‘ï¸' : 'ðŸ‘ï¸â€ðŸ—¨ï¸'}
            </button>
          </div>
        </div>
      </div>
    </Modal>

    {/* Add Course Modal */}
    <Modal
      isOpen={showAddCourseModal}
      title="Add New Course"
      onClose={() => setShowAddCourseModal(false)}
      size="md"
      footer={
        <>
          <button
            onClick={() => setShowAddCourseModal(false)}
            className="px-4 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
          >
            Cancel
          </button>
          <button
            onClick={handleAddCourse}
            className="px-4 py-2 bg-[#135bec] text-white rounded text-sm font-medium hover:bg-blue-700"
          >
            Add Course
          </button>
        </>
      }
    >
      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-900 mb-2">Course Code *</label>
          <input
            type="text"
            value={courseForm.course_code}
            onChange={(e) => setCourseForm(prev => ({ ...prev, course_code: e.target.value }))}
            placeholder="e.g., ICT101"
            className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-900 mb-2">Course Title *</label>
          <input
            type="text"
            value={courseForm.course_title}
            onChange={(e) => setCourseForm(prev => ({ ...prev, course_title: e.target.value }))}
            placeholder="Enter course title"
            className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-900 mb-2">Program *</label>
          <input
            type="text"
            value={courseForm.program}
            onChange={(e) => setCourseForm(prev => ({ ...prev, program: e.target.value }))}
            placeholder="e.g., BSc. Computer Science"
            className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-900 mb-2">Year Level</label>
          <input
            type="text"
            value={courseForm.year_level}
            onChange={(e) => setCourseForm(prev => ({ ...prev, year_level: e.target.value }))}
            placeholder="e.g., Year 1"
            className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
          />
        </div>
      </div>
    </Modal>

    {/* Add Session Modal */}
    <Modal
      isOpen={showAddSessionModal}
      title="Create New Session"
      onClose={() => setShowAddSessionModal(false)}
      size="md"
      footer={
        <>
          <button
            onClick={() => setShowAddSessionModal(false)}
            className="px-4 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
          >
            Cancel
          </button>
          <button
            onClick={() => {
              setSuccess('âœ… Session created successfully!');
              setShowAddSessionModal(false);
                setTimeout(() => setSuccess(null), 3000);
            }}
            className="px-4 py-2 bg-[#135bec] text-white rounded text-sm font-medium hover:bg-blue-700"
          >
            Create Session
          </button>
        </>
      }
    >
      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-900 mb-2">Session Date *</label>
          <input
            type="date"
            value={sessionForm.session_date}
            onChange={(e) => setSessionForm(prev => ({ ...prev, session_date: e.target.value }))}
            className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-900 mb-2">Start Time *</label>
          <input
            type="time"
            value={sessionForm.start_time}
            onChange={(e) => setSessionForm(prev => ({ ...prev, start_time: e.target.value }))}
            className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-900 mb-2">End Time *</label>
          <input
            type="time"
            value={sessionForm.end_time}
            onChange={(e) => setSessionForm(prev => ({ ...prev, end_time: e.target.value }))}
            className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-900 mb-2">Venue *</label>
          <input
            type="text"
            value={sessionForm.venue}
            onChange={(e) => setSessionForm(prev => ({ ...prev, venue: e.target.value }))}
            placeholder="Enter venue"
            className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
          />
      </div>
    </div>
    </Modal>

    {/* Import CSV Modal */}
    <Modal
      isOpen={showImportCSVModal}
      title="Import CSV Data"
      onClose={() => setShowImportCSVModal(false)}
      size="md"
      footer={
        <>
          <button
            onClick={() => setShowImportCSVModal(false)}
            className="px-4 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
          >
            Cancel
          </button>
          <button
            onClick={handleImportCSV}
            className="px-4 py-2 bg-[#135bec] text-white rounded text-sm font-medium hover:bg-blue-700"
          >
            Import
          </button>
        </>
      }
    >
      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-900 mb-2">Select CSV File *</label>
          <input
            type="file"
            accept=".csv"
            onChange={(e) => setCSVFile(e.target.files?.[0] || null)}
            className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
          />
        </div>
        <div className="p-3 bg-blue-50 rounded border border-blue-200">
          <p className="text-xs text-blue-800">
            <strong>CSV Format:</strong> course_code, course_title, program, year_level
          </p>
        </div>
      </div>
      </Modal>

    {/* Export Report Modal */}
    <Modal
      isOpen={showExportModal}
      title="Export Attendance Report"
      onClose={() => setShowExportModal(false)}
      size="md"
      footer={
        <>
          <button
            onClick={() => setShowExportModal(false)}
            className="px-4 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
          >
            Cancel
          </button>
          <button
            onClick={handleExportReport}
            className="px-4 py-2 bg-[#135bec] text-white rounded text-sm font-medium hover:bg-blue-700"
          >
            Export
          </button>
        </>
      }
    >
      <div className="space-y-4">
        <p className="text-sm text-gray-600">
          Export attendance data and statistics for {new Date().toLocaleDateString()}
        </p>
        <div className="p-3 bg-green-50 rounded border border-green-200">
          <p className="text-xs text-green-800">
            <strong>Report includes:</strong> Total sessions, attendance records, and system statistics
          </p>
        </div>
      </div>
    </Modal>
    </>
  );
};

export default AdminDashboard;
