import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';

const AdminDashboard = () => {
  const { currentUser, logout, userRole } = useAuth();

  // Redirect if not an admin
  useEffect(() => {
    if (userRole && userRole !== 'admin') {
      window.location.href = '/';
    }
  }, [userRole]);

  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [loading, setLoading] = useState(false);
  
  // Class management
  const [className, setClassName] = useState('');
  const [classes, setClasses] = useState([]);

  // User management
  const [email, setEmail] = useState('');
  const [role, setRole] = useState('student');
  const [selectedClass, setSelectedClass] = useState('');
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchClasses();
    fetchUsers();
  }, []);

  const fetchClasses = async () => {
    try {
      const { data, error } = await supabase
        .from('classes')
        .select('*')
        .order('created_at', { ascending: false });
      if (error) throw error;
      setClasses(data || []);
    } catch (err) {
      console.error('Error fetching classes:', err);
    }
  };

  const fetchUsers = async () => {
    try {
      const { data, error } = await supabase
        .from('users')
        .select('*')
        .order('created_at', { ascending: false });
      if (error) throw error;
      setUsers(data || []);
    } catch (err) {
      console.error('Error fetching users:', err);
    }
  };

  const createClass = async (e) => {
    e.preventDefault();
    if (!className) {
      setMessage('‚ùå Please enter a class name');
      setMessageType('error');
      return;
    }

    setLoading(true);
    try {
      const { error } = await supabase
        .from('classes')
        .insert({ class_name: className });

      if (error) throw error;
      
      setMessage(`‚úÖ Class "${className}" created successfully`);
      setMessageType('success');
      setClassName('');
      fetchClasses();
    } catch (err) {
      console.error('Error creating class:', err);
      setMessage('‚ùå Error creating class');
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  const addUser = async (e) => {
    e.preventDefault();
    
    if (!email || !role) {
      setMessage('‚ùå Please fill in all fields');
      setMessageType('error');
      return;
    }

    if (!email.toLowerCase().endsWith('@ictuniversity.edu.cm')) {
      setMessage('‚ùå Email must be @ictuniversity.edu.cm');
      setMessageType('error');
      return;
    }

    setLoading(true);
    try {
      // First create auth user
      const { data: authData, error: authError } = await supabase.auth.admin.createUser({
        email,
        password: Math.random().toString(36).slice(-12), // temporary password
        email_confirm: true,
      });

      if (authError) throw authError;

      // Then create user profile
      const { error: profileError } = await supabase
        .from('users')
        .insert({
          id: authData.user.id,
          email,
          role,
          class_id: role === 'student' && selectedClass ? selectedClass : null,
        });

      if (profileError) throw profileError;

      setMessage(`‚úÖ ${role.charAt(0).toUpperCase() + role.slice(1)} user created: ${email}`);
      setMessageType('success');
      setEmail('');
      setRole('student');
      setSelectedClass('');
      fetchUsers();
    } catch (err) {
      console.error('Error creating user:', err);
      setMessage('‚ùå Error creating user: ' + (err.message || 'Unknown error'));
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/admin');
    } catch (err) {
      console.error('Logout error:', err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-teal-50 p-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="mb-6 flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Admin Dashboard</h1>
            <p className="text-gray-600">üë®‚Äçüíº Manage Users & Classes</p>
            {currentUser && <p className="text-sm text-gray-500 mt-1">üë§ {currentUser.email}</p>}
          </div>
          <button
            onClick={handleLogout}
            className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg font-medium transition"
          >
            Logout
          </button>
        </div>

        {/* Message Display */}
        {message && (
          <div
            className={`rounded-lg p-4 mb-6 text-white ${
              messageType === 'success'
                ? 'bg-green-500'
                : messageType === 'error'
                ? 'bg-red-500'
                : 'bg-blue-500'
            }`}
          >
            {message}
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-6">
          {/* Create Class Card */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-bold text-gray-900 mb-4">üìö Create New Class</h2>
            <form onSubmit={createClass} className="space-y-4">
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Class Name
                </label>
                <input
                  type="text"
                  value={className}
                  onChange={(e) => setClassName(e.target.value)}
                  disabled={loading}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 disabled:bg-gray-100"
                  placeholder="e.g., Advanced Web Development"
                />
              </div>
              <button
                type="submit"
                disabled={loading}
                className="w-full bg-green-600 hover:bg-green-700 disabled:bg-gray-400 text-white font-semibold py-2 rounded-lg transition"
              >
                {loading ? 'Creating...' : 'Create Class'}
              </button>
            </form>
          </div>

          {/* Add User Card */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-bold text-gray-900 mb-4">üë§ Add New User</h2>
            <form onSubmit={addUser} className="space-y-4">
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">Email</label>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  disabled={loading}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 disabled:bg-gray-100"
                  placeholder="user@ictuniversity.edu.cm"
                />
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">Role</label>
                <select
                  value={role}
                  onChange={(e) => {
                    setRole(e.target.value);
                    if (e.target.value !== 'student') setSelectedClass('');
                  }}
                  disabled={loading}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 disabled:bg-gray-100"
                >
                  <option value="student">Student</option>
                  <option value="delegate">Delegate</option>
                  <option value="admin">Admin</option>
                </select>
              </div>

              {role === 'student' && (
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Class (Optional)
                  </label>
                  <select
                    value={selectedClass}
                    onChange={(e) => setSelectedClass(e.target.value)}
                    disabled={loading}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 disabled:bg-gray-100"
                  >
                    <option value="">No class assigned</option>
                    {classes.map((cls) => (
                      <option key={cls.id} value={cls.id}>
                        {cls.class_name}
                      </option>
                    ))}
                  </select>
                </div>
              )}

              <button
                type="submit"
                disabled={loading}
                className="w-full bg-green-600 hover:bg-green-700 disabled:bg-gray-400 text-white font-semibold py-2 rounded-lg transition"
              >
                {loading ? 'Creating...' : 'Add User'}
              </button>
            </form>
          </div>
        </div>

        {/* Classes List */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">üìö All Classes ({classes.length})</h2>
          {classes.length === 0 ? (
            <p className="text-gray-600">No classes created yet</p>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700">Class Name</th>
                    <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700">Created At</th>
                  </tr>
                </thead>
                <tbody>
                  {classes.map((cls) => (
                    <tr key={cls.id} className="border-t hover:bg-gray-50">
                      <td className="px-4 py-2 text-sm text-gray-900">{cls.class_name}</td>
                      <td className="px-4 py-2 text-sm text-gray-600">
                        {new Date(cls.created_at).toLocaleDateString()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>

        {/* Users List */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">üë• All Users ({users.length})</h2>
          {users.length === 0 ? (
            <p className="text-gray-600">No users created yet</p>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700">Email</th>
                    <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700">Role</th>
                    <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700">Created At</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user.id} className="border-t hover:bg-gray-50">
                      <td className="px-4 py-2 text-sm text-gray-900">{user.email}</td>
                      <td className="px-4 py-2 text-sm">
                        <span className={`px-3 py-1 rounded-full text-xs font-semibold ${
                          user.role === 'admin' ? 'bg-red-100 text-red-800' :
                          user.role === 'delegate' ? 'bg-purple-100 text-purple-800' :
                          'bg-blue-100 text-blue-800'
                        }`}>
                          {user.role.charAt(0).toUpperCase() + user.role.slice(1)}
                        </span>
                      </td>
                      <td className="px-4 py-2 text-sm text-gray-600">
                        {new Date(user.created_at).toLocaleDateString()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
