import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate, useParams } from 'react-router-dom';

const Login = ({ role: propRole }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();
  const params = useParams();
  
  // Get role from route parameter or prop
  const role = propRole || params.role || 'student';

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Validate email format
      if (!email.toLowerCase().endsWith('@ictuniversity.edu.cm')) {
        setError('‚ùå Only ICT University emails (@ictuniversity.edu.cm) are allowed');
        setLoading(false);
        return;
      }

      if (!password) {
        setError('‚ùå Please enter your password');
        setLoading(false);
        return;
      }

      // Attempt login
      await login(email, password);
      
      // Navigate to appropriate dashboard
      if (role === 'admin') {
        navigate('/admin/dashboard');
      } else if (role === 'delegate') {
        navigate('/delegate/dashboard');
      } else {
        navigate('/student/dashboard');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('‚ùå Invalid email or password. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Get emoji and titles based on role
  const getRoleEmoji = () => {
    if (role === 'admin') return 'üë®‚Äçüíº';
    if (role === 'delegate') return 'üë®‚Äçüè´';
    return 'üéì';
  };

  const getRoleTitle = () => {
    if (role === 'admin') return 'Admin';
    if (role === 'delegate') return 'Delegate';
    return 'Student';
  };

  const getRoleDescription = () => {
    if (role === 'admin') return 'Administer users and classes';
    if (role === 'delegate') return 'Manage attendance sessions for your classes';
    return 'Mark attendance using GPS';
  };

  // Simple login view (no role selection)
  return (
    <div className="min-h-screen flex items-center justify-center bg-slate-100 p-4">
      <div className="max-w-md w-full bg-white rounded-xl shadow-lg overflow-hidden border border-slate-200">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-900 to-indigo-700 p-8 text-white">
          <div className="flex items-center justify-between mb-2">
            <div>
              <h1 className="text-3xl font-bold">ICT University</h1>
              <p className="text-blue-100">GPS-Based Attendance</p>
            </div>
            <div className="text-4xl">{getRoleEmoji()}</div>
          </div>
        </div>

        {/* Form Container */}
        <div className="p-8">
          {/* Role Display */}
          <h2 className="text-2xl font-bold text-gray-900 mb-1">
            {getRoleTitle()} Login
          </h2>
          <p className="text-gray-600 text-sm mb-6">
            {getRoleDescription()}
          </p>

          {/* Error Messages */}
          {error && (
            <div className="mb-6 p-4 bg-red-50 border border-red-200 text-red-700 rounded-lg text-sm">
              {error}
            </div>
          )}

          {/* Login Form */}
          <form onSubmit={handleLogin} className="space-y-4">
            {/* Email Input */}
            <div>
              <label className="block text-sm font-semibold text-gray-900 mb-2">
                Email Address
              </label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="your.email@ictuniversity.edu.cm"
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:border-transparent transition text-gray-900 placeholder-gray-500"
                required
              />
              <p className="text-gray-500 text-xs mt-1">Use your institutional email</p>
            </div>

            {/* Password Input with Toggle */}
            <div>
              <label className="block text-sm font-semibold text-gray-900 mb-2">
                Password
              </label>
              <div className="relative">
                <input
                  type={showPassword ? 'text' : 'password'}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:border-transparent transition text-gray-900 placeholder-gray-500"
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-600 hover:text-blue-900 transition"
                >
                  {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
              </div>
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-emerald-600 hover:bg-emerald-700 disabled:bg-gray-400 text-white font-semibold py-3 rounded-lg transition mt-6 shadow-md hover:shadow-lg"
            >
              {loading ? '‚è≥ Logging in...' : 'Sign In'}
            </button>
          </form>

          {/* Info Section - Only for Students */}
          {role === 'student' && (
            <div className="mt-6 bg-blue-50 border border-blue-200 rounded-lg p-4 text-sm text-gray-700">
              <p className="font-semibold text-blue-900 mb-2">üìç GPS Requirements:</p>
              <ul className="text-xs text-gray-700 space-y-1">
                <li>‚úì Enable location services</li>
                <li>‚úì Allow browser access to location</li>
                <li>‚úì GPS accuracy ‚â§50m required</li>
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Login;
