          </div>
          <div>
            <label style={{ display: 'block', fontSize: '13px', fontWeight: '600', color: '#1f2937', marginBottom: '6px' }}>
              Year Level
            </label>
            <input
              type="text"
              value={courseForm.year_level}
              onChange={(e) => setCourseForm(prev => ({ ...prev, year_level: e.target.value }))}
              placeholder="e.g., Year 1"
              style={{
                width: '100%',
                padding: '10px 12px',
                border: '1px solid #d1d5db',
                borderRadius: '6px',
                fontSize: '14px',
                boxSizing: 'border-box'
              }}
            />
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default AdminDashboard;
