import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Button from '../components/Button';
import Badge from '../components/Badge';
import Table from '../components/Table';
import Alert from '../components/Alert';

const DelegateDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  const [sessions, setSessions] = useState([]);
  const [sessionsLoading, setSessionsLoading] = useState(true);
  const [courses, setCourses] = useState([]);
  const [coursesLoading, setCoursesLoading] = useState(false);
  const [showCreateSession, setShowCreateSession] = useState(false);
  const [newSessionData, setNewSessionData] = useState({
    courseId: '',
    date: new Date().toISOString().split('T')[0],
    startTime: '08:00',
    endTime: '10:00'
  });
  const [creating, setCreating] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [showAttendanceList, setShowAttendanceList] = useState(null);

  useEffect(() => {
    if (loading) return;

    if (!currentUser || userRole !== 'delegate') {
      navigate('/delegate');
      return;
    }

    fetchSessions();
  }, [currentUser, userRole, loading, navigate]);

  const fetchCourses = async () => {
    try {
      setCoursesLoading(true);
      const { data, error } = await supabase
        .from('courses')
        .select('id, course_code, course_title')
        .order('course_code', { ascending: true });

      if (error) throw error;
      setCourses(data || []);
    } catch (err) {
      console.error('Error fetching courses:', err);
      setCourses([]);
    } finally {
      setCoursesLoading(false);
    }
  };

  const fetchSessions = async () => {
    try {
      setSessionsLoading(true);

      // Get all sessions created by this delegate
      const { data, error } = await supabase
        .from('sessions')
        .select(`
          id,
          token,
          created_at,
          expires_at,
          class_id,
          classes (
            class_name
          )
        `)
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Get attendance counts for each session
      const sessionsWithCounts = await Promise.all(
        (data || []).map(async (session) => {
          const { data: attendanceData } = await supabase
            .from('attendance')
            .select('id')
            .eq('session_id', session.id);

          return {
            ...session,
            attendanceCount: attendanceData?.length || 0,
            isActive: new Date(session.expires_at) > new Date()
          };
        })
      );

      setSessions(sessionsWithCounts);
    } catch (err) {
      console.error('Error fetching sessions:', err);
      setSessions([]);
    } finally {
      setSessionsLoading(false);
    }
  };

  const handleCreateSession = async () => {
    if (!newSessionData.courseId) {
      setError('Please select a course');
      return;
    }

    if (!newSessionData.startTime || !newSessionData.endTime) {
      setError('Please set start and end times');
      return;
    }

    if (newSessionData.startTime >= newSessionData.endTime) {
      setError('End time must be after start time');
      return;
    }

    try {
      setCreating(true);
      setError(null);

      // Get the selected course
      const selectedCourse = courses.find(c => c.id === newSessionData.courseId);
      if (!selectedCourse) {
        setError('Selected course not found');
        return;
      }

      // Check if class already exists for this course and delegate
      const { data: existingClass } = await supabase
        .from('classes')
        .select('id')
        .eq('course_id', selectedCourse.id)
        .eq('delegate_id', currentUser.id)
        .single();

      let classId = existingClass?.id;

      if (!classId) {
        // Create new class
        const { data: newClass, error: classError } = await supabase
          .from('classes')
          .insert({
            class_name: `${selectedCourse.course_code} - ${selectedCourse.course_title}`,
            course_id: selectedCourse.id,
            delegate_id: currentUser.id
          })
          .select('id')
          .single();

        if (classError) throw classError;
        classId = newClass.id;
      }

      // Create session with start and end times
      const sessionDate = new Date(newSessionData.date);
      const [startHour, startMin] = newSessionData.startTime.split(':');
      const [endHour, endMin] = newSessionData.endTime.split(':');

      const startDateTime = new Date(sessionDate);
      startDateTime.setHours(parseInt(startHour), parseInt(startMin), 0, 0);

      const endDateTime = new Date(sessionDate);
      endDateTime.setHours(parseInt(endHour), parseInt(endMin), 59, 999);

      const { data: session, error: sessionError } = await supabase
        .from('sessions')
        .insert({
          class_id: classId,
          token: `SESSION_${Date.now()}`,
          session_date: newSessionData.date,
          start_time: newSessionData.startTime,
          end_time: newSessionData.endTime,
          expires_at: endDateTime.toISOString()
        })
        .select()
        .single();

      if (sessionError) throw sessionError;

      setSuccess('‚úÖ Attendance session created successfully!');
      setShowCreateSession(false);
      setNewSessionData({ 
        courseId: '', 
        date: new Date().toISOString().split('T')[0],
        startTime: '08:00',
        endTime: '10:00'
      });
      fetchSessions();

      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error creating session:', err);
      setError(`‚ùå Error: ${err.message}`);
    } finally {
      setCreating(false);
    }
  };

  const handleCloseSession = async (sessionId) => {
    try {
      // Set expires_at to now to deactivate
      const { error } = await supabase
        .from('sessions')
        .update({ expires_at: new Date().toISOString() })
        .eq('id', sessionId);

      if (error) throw error;

      setSuccess('‚úÖ Session closed successfully!');
      fetchSessions();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error closing session:', err);
      setError(`‚ùå Error closing session: ${err.message}`);
    }
  };

  const handleShowCreateSession = () => {
    setShowCreateSession(!showCreateSession);
    if (!showCreateSession) {
      fetchCourses(); // Fetch courses when opening the form
    }
  };

  const fetchAttendanceList = async (sessionId) => {
    try {
      const { data, error } = await supabase
        .from('attendance')
        .select(`
          id,
          student_id,
          users (
            name,
            matricule
          ),
          created_at
        `)
        .eq('session_id', sessionId)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setShowAttendanceList({ sessionId, students: data || [] });
    } catch (err) {
      console.error('Error fetching attendance:', err);
      setError('Failed to fetch attendance list');
    }
  };

  const handleMarkOwnAttendance = async (sessionId) => {
    try {
      // Check if already marked
      const { data: existing } = await supabase
        .from('attendance')
        .select('id')
        .eq('session_id', sessionId)
        .eq('student_id', currentUser.id)
        .single();

      if (existing) {
        setError('You have already marked attendance for this session');
        return;
      }

      // Mark attendance
      const { error } = await supabase
        .from('attendance')
        .insert({
          session_id: sessionId,
          student_id: currentUser.id,
          created_at: new Date().toISOString()
        });

      if (error) throw error;

      setSuccess('‚úÖ Your attendance has been marked!');
      fetchSessions();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error marking attendance:', err);
      setError(`‚ùå Error: ${err.message}`);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-blue-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-16 w-16 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600 font-medium">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!currentUser || userRole !== 'delegate') {
    return null;
  }

  return (
    <Layout role="delegate">
      <div className="space-y-8">
        <div>
          <h1 className="text-3xl font-bold mb-2">Delegate Dashboard</h1>
          <p className="text-gray-600">Manage attendance sessions for your courses</p>
        </div>

        {error && (
          <Alert type="error" title="Error" message={error} />
        )}

        {success && (
          <Alert type="success" title="Success" message={success} />
        )}

        {/* Create New Attendance Session */}
        <Card title="‚ûï Create Attendance Session">
          <div className="space-y-4">
            <Button
              variant="primary"
              onClick={handleShowCreateSession}
            >
              {showCreateSession ? '‚úï Cancel' : '+ New Session'}
            </Button>

            {showCreateSession && (
              <div className="space-y-4 p-4 bg-blue-50 rounded-lg border border-blue-200">
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Select Course:
                  </label>
                  {coursesLoading ? (
                    <p className="text-gray-600">Loading courses...</p>
                  ) : courses.length > 0 ? (
                    <select
                      value={newSessionData.courseId}
                      onChange={(e) => setNewSessionData(prev => ({ ...prev, courseId: e.target.value }))}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="">-- Select a course --</option>
                      {courses.map(course => (
                        <option key={course.id} value={course.id}>
                          {course.course_code} - {course.course_title}
                        </option>
                      ))}
                    </select>
                  ) : (
                    <p className="text-gray-600">No courses available</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Date:
                  </label>
                  <input
                    type="date"
                    value={newSessionData.date}
                    onChange={(e) => setNewSessionData(prev => ({ ...prev, date: e.target.value }))}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                      Start Time:
                    </label>
                    <input
                      type="time"
                      value={newSessionData.startTime}
                      onChange={(e) => setNewSessionData(prev => ({ ...prev, startTime: e.target.value }))}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                      End Time:
                    </label>
                    <input
                      type="time"
                      value={newSessionData.endTime}
                      onChange={(e) => setNewSessionData(prev => ({ ...prev, endTime: e.target.value }))}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>
                </div>

                <Button
                  variant="primary"
                  onClick={handleCreateSession}
                  disabled={creating || !newSessionData.courseId}
                >
                  {creating ? '‚è≥ Creating...' : '‚úÖ Create Session'}
                </Button>
              </div>
            )}
          </div>
        </Card>

        {/* Attendance Sessions */}
        <Card title="üìã Attendance Sessions">
          {sessionsLoading ? (
            <p className="text-gray-600">Loading sessions...</p>
          ) : sessions.length > 0 ? (
            <div className="space-y-4">
              {sessions.map((session) => (
                <div key={session.id} className="p-4 border border-gray-200 rounded-lg">
                  <div className="flex justify-between items-start mb-3">
                    <div className="flex-1">
                      <h3 className="font-semibold text-gray-900">{session.classes?.class_name}</h3>
                      <p className="text-sm text-gray-600">
                        üìÖ {new Date(session.created_at).toLocaleDateString()}
                      </p>
                      <p className="text-sm text-gray-600">
                        üïê {session.start_time || 'N/A'} - {session.end_time || 'N/A'}
                      </p>
                    </div>
                    <div className="text-right">
                      <Badge
                        label={session.isActive ? 'ACTIVE' : 'CLOSED'}
                        variant={session.isActive ? 'success' : 'danger'}
                      />
                      <p className="text-sm text-gray-600 mt-1">
                        üë• {session.attendanceCount} students marked
                      </p>
                    </div>
                  </div>

                  <div className="flex flex-wrap gap-2">
                    <Button
                      variant="secondary"
                      size="sm"
                      onClick={() => fetchAttendanceList(session.id)}
                    >
                      üëÅÔ∏è View Attendance
                    </Button>

                    {session.isActive && (
                      <>
                        <Button
                          variant="primary"
                          size="sm"
                          onClick={() => handleMarkOwnAttendance(session.id)}
                        >
                          ‚úì Mark My Attendance
                        </Button>

                        <Button
                          variant="danger"
                          size="sm"
                          onClick={() => handleCloseSession(session.id)}
                        >
                          Close Session
                        </Button>
                      </>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600">No attendance sessions created yet</p>
          )}
        </Card>

        {/* Attendance List Modal */}
        {showAttendanceList && (
          <Card title={`üë• Attendance List (${showAttendanceList.students.length} students)`}>
            <div className="space-y-4">
              <Button
                variant="secondary"
                onClick={() => setShowAttendanceList(null)}
              >
                ‚Üê Back to Sessions
              </Button>

              {showAttendanceList.students.length > 0 ? (
                <div className="overflow-x-auto">
                  <table className="min-w-full border-collapse">
                    <thead>
                      <tr className="bg-gray-100 border-b">
                        <th className="px-4 py-2 text-left font-semibold text-gray-700">Student Name</th>
                        <th className="px-4 py-2 text-left font-semibold text-gray-700">Matricule</th>
                        <th className="px-4 py-2 text-left font-semibold text-gray-700">Time Marked</th>
                      </tr>
                    </thead>
                    <tbody>
                      {showAttendanceList.students.map((record) => (
                        <tr key={record.id} className="border-b hover:bg-gray-50">
                          <td className="px-4 py-2 text-gray-900">{record.users?.name || 'Unknown'}</td>
                          <td className="px-4 py-2 text-gray-600">{record.users?.matricule || 'N/A'}</td>
                          <td className="px-4 py-2 text-gray-600">
                            {new Date(record.created_at).toLocaleTimeString()}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ) : (
                <p className="text-gray-600">No attendance marked yet</p>
              )}
            </div>
          </Card>
        )}
      </div>
    </Layout>
  );
};

export default DelegateDashboard;
