import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Button from '../components/Button';
import Badge from '../components/Badge';
import Table from '../components/Table';
import StatCard from '../components/StatCard';

const DelegateDashboard = () => {
  const { currentUser, userRole, loading, logout } = useAuth();
  const navigate = useNavigate();
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [coursesLoading, setCoursesLoading] = useState(false);
  const [showCreateClass, setShowCreateClass] = useState(false);
  const [newClassName, setNewClassName] = useState('');
  const [coursesFetched, setCoursesFetched] = useState(false);

  useEffect(() => {
    if (loading) {
      console.log('‚è≥ DelegateDashboard: Loading auth...');
      return; // Don't redirect while loading
    }
    
    console.log('üîç DelegateDashboard: Checking auth - currentUser:', !!currentUser, 'userRole:', userRole);
    
    if (!currentUser) {
      console.log('‚ùå DelegateDashboard: No user, redirecting to /delegate');
      navigate('/delegate');
      return;
    }
    
    // Only redirect if we have user data and they're NOT a delegate
    if (userRole && userRole !== 'delegate') {
      console.log('‚ùå DelegateDashboard: User is not delegate, redirecting to /delegate');
      navigate('/delegate');
      return;
    }
    
    console.log('‚úÖ DelegateDashboard: User is delegate, allowing access');
  }, [userRole, loading, currentUser, navigate]);

  // Fetch available courses only once
  useEffect(() => {
    if (currentUser && userRole === 'delegate' && !coursesFetched) {
      fetchAvailableCourses();
      setCoursesFetched(true);
    }
  }, [currentUser, userRole, coursesFetched]);

  const fetchAvailableCourses = async () => {
    try {
      setCoursesLoading(true);
      console.log('üìö Fetching available courses...');

      // Limit to 50 courses and add query optimization
      const { data, error } = await supabase
        .from('courses')
        .select('id, course_code, course_name, year_level')
        .order('year_level', { ascending: true })
        .order('course_code', { ascending: true })
        .limit(50);

      if (error) {
        console.warn('‚ö†Ô∏è  Could not fetch courses:', error.message);
        setCourses([]);
      } else {
        console.log('‚úÖ Fetched', data?.length || 0, 'courses');
        setCourses(data || []);
      }
    } catch (err) {
      console.error('‚ùå Error fetching courses:', err);
      setCourses([]);
    } finally {
      setCoursesLoading(false);
    }
  };

  const handleCreateClass = async () => {
    if (!selectedCourse || !newClassName.trim()) {
      alert('Please select a course and enter a class name');
      return;
    }

    try {
      const { data, error } = await supabase
        .from('classes')
        .insert({
          class_name: newClassName,
          course_id: selectedCourse.id,
          delegate_id: currentUser.id,
        })
        .select();

      if (error) {
        alert('Error creating class: ' + error.message);
      } else {
        alert('‚úÖ Class created successfully!');
        setNewClassName('');
        setSelectedCourse(null);
        setShowCreateClass(false);
        // Refresh the page
        window.location.reload();
      }
    } catch (err) {
      console.error('Error:', err);
      alert('Error creating class');
    }
  };

  const handleStartAttendanceSession = async (courseId, courseName) => {
    try {
      console.log('üîì Starting attendance session for:', courseName);
      
      // Create a session in the database
      const { data: session, error: sessionError } = await supabase
        .from('sessions')
        .insert([
          {
            course_id: courseId,
            delegate_id: currentUser.id,
            status: 'active',
            qr_token: `QR_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`, // Generate unique QR token
          }
        ])
        .select();

      if (sessionError) throw sessionError;

      alert(`‚úÖ Attendance session started for "${courseName}"!\n\nStudents can now mark their attendance.`);
      
      // Navigate to live attendance page
      navigate('/delegate/live', { state: { sessionId: session[0].id, courseName } });
    } catch (err) {
      console.error('Error starting session:', err);
      alert('‚ùå Error starting attendance session: ' + err.message);
    }
  };

  const [assignedClass] = useState('Advanced Web Development');
  const [todaysCourses] = useState([
    { id: 1, name: 'Advanced Web Dev', time: '09:00-11:00' },
    { id: 2, name: 'Database Design', time: '14:00-16:00' },
  ]);

  const [stats] = useState({
    activeSession: 'Advanced Web Dev',
    presentCount: 28,
    totalCount: 35,
  });

  const [recentSessions] = useState([
    { id: 1, course: 'Advanced Web Dev', date: '2025-12-18', time: '09:00', students: 28, status: 'closed' },
    { id: 2, course: 'Database Design', date: '2025-12-17', time: '14:00', students: 32, status: 'closed' },
    { id: 3, course: 'Python Programming', date: '2025-12-17', time: '11:00', students: 30, status: 'closed' },
  ]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-blue-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-16 w-16 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600 font-medium">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!currentUser) {
    console.log('üî¥ DelegateDashboard: Rendering null - no currentUser');
    return <div style={{ padding: '20px', color: 'red' }}>Error: No user logged in</div>;
  }

  if (userRole !== 'delegate') {
    console.log('üî¥ DelegateDashboard: Rendering null - userRole is:', userRole);
    return <div style={{ padding: '20px', color: 'red' }}>Error: Access denied (role: {userRole})</div>;
  }

  return (
    <Layout role="delegate">
      <div className="space-y-8">
        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <StatCard title="Assigned Class" value={assignedClass} icon="üìö" color="blue" />
          <StatCard title="Present Now" value={`${stats.presentCount}/${stats.totalCount}`} icon="‚úÖ" color="green" />
          <Card title="üü¢ Active Session">
            <p className="text-lg font-semibold text-green-600">{stats.activeSession}</p>
            <p className="text-sm text-gray-600 mt-2">Session in progress</p>
          </Card>
        </div>

        {/* Create New Class Section */}
        <Card title="‚ûï Create New Class Section">
          <div className="space-y-4">
            <Button 
              variant="primary" 
              onClick={() => setShowCreateClass(!showCreateClass)}
            >
              {showCreateClass ? '‚úï Cancel' : '+ New Class'}
            </Button>

            {showCreateClass && (
              <div className="space-y-4 p-4 bg-blue-50 rounded-lg border border-blue-200">
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Select Course:
                  </label>
                  {coursesLoading ? (
                    <p className="text-gray-600">Loading courses...</p>
                  ) : courses.length === 0 ? (
                    <div className="p-4 bg-yellow-50 border border-yellow-200 rounded text-yellow-800">
                      <p>üìå <strong>No courses found!</strong></p>
                      <p className="text-sm mt-2">Please follow the setup instructions to populate courses:</p>
                      <ul className="text-sm mt-2 list-disc list-inside space-y-1">
                        <li>Check <strong>COURSES_SETUP.md</strong> for instructions</li>
                        <li>Create the courses table in Supabase SQL Editor</li>
                        <li>Run: <code className="bg-white px-2 py-1 rounded">node populate_courses.js</code></li>
                      </ul>
                    </div>
                  ) : (
                    <select
                      value={selectedCourse?.id || ''}
                      onChange={(e) => {
                        const course = courses.find(c => c.id === e.target.value);
                        setSelectedCourse(course);
                      }}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="">-- Select a course --</option>
                      {courses.map(course => (
                        <option key={course.id} value={course.id}>
                          {course.course_code} - {course.course_title} ({course.program}, Year {course.year_level})
                        </option>
                      ))}
                    </select>
                  )}
                </div>

                {selectedCourse && (
                  <div className="p-3 bg-white rounded border border-blue-300">
                    <h4 className="font-semibold text-gray-900">{selectedCourse.course_code}</h4>
                    <p className="text-sm text-gray-700">{selectedCourse.course_title}</p>
                    <p className="text-xs text-gray-600 mt-1">
                      üë®‚Äçüè´ {selectedCourse.instructor} | {selectedCourse.program} | Year {selectedCourse.year_level}
                    </p>
                    <p className="text-xs text-gray-600">
                      Status: <Badge label={selectedCourse.course_status} variant="info" />
                    </p>
                  </div>
                )}

                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Class Section Name:
                  </label>
                  <input
                    type="text"
                    value={newClassName}
                    onChange={(e) => setNewClassName(e.target.value)}
                    placeholder="e.g., Section A, Section B, Batch 2025"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                <div className="flex gap-2">
                  <Button 
                    variant="primary" 
                    onClick={handleCreateClass}
                    disabled={!selectedCourse || !newClassName.trim()}
                  >
                    Create Class
                  </Button>
                  <Button 
                    variant="secondary" 
                    onClick={() => {
                      setShowCreateClass(false);
                      setSelectedCourse(null);
                      setNewClassName('');
                    }}
                  >
                    Cancel
                  </Button>
                </div>
              </div>
            )}
          </div>
        </Card>

        {/* Today's Courses */}
        <Card title="üìö Today's Courses">
          <div className="space-y-3">
            {todaysCourses.map((course) => (
              <div
                key={course.id}
                className="flex items-center justify-between p-4 border border-gray-200 rounded-lg hover:bg-blue-50 transition"
              >
                <div>
                  <p className="font-semibold text-gray-900">{course.name}</p>
                  <p className="text-sm text-gray-600">üïê {course.time}</p>
                </div>
                <Button variant="primary" size="sm" onClick={() => handleStartAttendanceSession(course.id, course.name)}>
                  Start Session
                </Button>
              </div>
            ))}
          </div>
        </Card>

        {/* Recent Sessions */}
        <Card title="üìã Recent Attendance Sessions">
          <Table
            columns={[
              { key: 'course', label: 'Course' },
              { key: 'date', label: 'Date' },
              { key: 'time', label: 'Time' },
              { key: 'students', label: 'Students' },
              {
                key: 'status',
                label: 'Status',
                render: (status) => <Badge label={status.toUpperCase()} variant={status} />,
              },
            ]}
            data={recentSessions}
            searchable
            paginated
            itemsPerPage={5}
          />
        </Card>
      </div>
    </Layout>
  );
};

export default DelegateDashboard;
