import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Button from '../components/Button';
import Badge from '../components/Badge';
import Alert from '../components/Alert';
import ProgressCircle from '../components/ProgressCircle';
import Table from '../components/Table';

const StudentDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  const [courseAttendance, setCourseAttendance] = useState([]);
  const [attendanceLoading, setAttendanceLoading] = useState(true);
  const [todayClasses, setTodayClasses] = useState([]);
  const [todayClassesLoading, setTodayClassesLoading] = useState(true);
  const [activeSessions, setActiveSessions] = useState([]);
  const [sessionsLoading, setSessionsLoading] = useState(true);
  const [attendanceHistory, setAttendanceHistory] = useState([]);
  const [historyLoading, setHistoryLoading] = useState(true);

  // Protect route - only allow students
  useEffect(() => {
    if (loading) {
      console.log('‚è≥ StudentDashboard: Loading auth...');
      return; // Don't redirect while loading
    }

    console.log('üîç StudentDashboard: Checking auth - currentUser:', !!currentUser, 'userRole:', userRole);

    if (!currentUser || userRole === null) {
      console.log('‚ùå StudentDashboard: No user or role, redirecting to /student');
      navigate('/student');
      return;
    }

    // Only redirect if we have user data and they're NOT a student
    if (userRole !== 'student') {
      console.log('‚ùå StudentDashboard: User is not a student, redirecting to /student');
      navigate('/student');
      return;
    }

    console.log('‚úÖ StudentDashboard: User is student, allowing access');
    fetchCourseAttendance();
    fetchTodayClasses();
    fetchActiveSessions();
    fetchAttendanceHistory();
  }, [currentUser, userRole, loading, navigate]);

  const fetchCourseAttendance = async () => {
    try {
      setAttendanceLoading(true);
      console.log('üìä Fetching course attendance summary...');

      // Get student's class (enrollment)
      const { data: userData, error: userError } = await supabase
        .from('users')
        .select('class_id')
        .eq('id', currentUser.id)
        .single();

      if (userError || !userData?.class_id) {
        setCourseAttendance([]);
        return;
      }

      // Get all sessions for this class (attendance_sessions)
      const { data: sessions, error: sessionsError } = await supabase
        .from('sessions')
        .select('id')
        .eq('class_id', userData.class_id);

      if (sessionsError) {
        setCourseAttendance([]);
        return;
      }

      const totalSessions = sessions?.length || 0;

      if (totalSessions === 0) {
        setCourseAttendance([]);
        return;
      }

      // Get attendance records for this student (attendance_records)
      const { data: attendanceRecords, error: attendanceError } = await supabase
        .from('attendance')
        .select('status')
        .eq('student_id', currentUser.id)
        .in('session_id', sessions.map(s => s.id));

      if (attendanceError) {
        setCourseAttendance([]);
        return;
      }

      // Calculate attendance
      let presentCount = 0;
      let lateCount = 0;

      attendanceRecords?.forEach(record => {
        if (record.status === 'present') presentCount++;
        else if (record.status === 'late') lateCount++;
      });

      const attendedSessions = presentCount + lateCount;
      const percentage = Math.round((attendedSessions / totalSessions) * 100);

      // Get class name
      const { data: classData } = await supabase
        .from('classes')
        .select('class_name')
        .eq('id', userData.class_id)
        .single();

      const attendanceData = [{
        id: userData.class_id,
        courseName: classData?.class_name || 'Unknown Course',
        totalSessions,
        attendedSessions,
        percentage
      }];

      console.log('‚úÖ Course attendance summary retrieved');
      setCourseAttendance(attendanceData);
    } catch (err) {
      console.error('Error fetching course attendance:', err);
      setCourseAttendance([]);
    } finally {
      setAttendanceLoading(false);
    }
  };

  const fetchTodayClasses = async () => {
    try {
      setTodayClassesLoading(true);
      console.log('üìÖ Fetching today\'s classes...');

      // Get student's class
      const { data: userData } = await supabase
        .from('users')
        .select('class_id')
        .eq('id', currentUser.id)
        .single();

      if (!userData?.class_id) {
        setTodayClasses([]);
        return;
      }

      // For now, show the enrolled class as today's class
      // In a real system, this would come from course schedules
      const { data: classData } = await supabase
        .from('classes')
        .select('class_name')
        .eq('id', userData.class_id)
        .single();

      if (classData) {
        // Mock today's schedule - in real system from schedules table
        setTodayClasses([{
          id: classData.id,
          name: classData.class_name,
          time: '09:00 - 11:00', // Mock time
          location: 'Lab 1' // Mock location
        }]);
      } else {
        setTodayClasses([]);
      }
    } catch (err) {
      console.error('Error fetching today\'s classes:', err);
      setTodayClasses([]);
    } finally {
      setTodayClassesLoading(false);
    }
  };

  const fetchActiveSessions = async () => {
    try {
      setSessionsLoading(true);

      // Get student's class
      const { data: userData } = await supabase
        .from('users')
        .select('class_id')
        .eq('id', currentUser.id)
        .single();

      if (!userData?.class_id) {
        setActiveSessions([]);
        return;
      }

      const { data, error } = await supabase
        .from('sessions')
        .select(`
          id,
          token,
          created_at,
          expires_at,
          class_id,
          classes (
            class_name
          )
        `)
        .eq('class_id', userData.class_id)
        .gt('expires_at', new Date().toISOString());

      if (error) throw error;
      setActiveSessions(data || []);
    } catch (err) {
      console.error('Error fetching active sessions:', err);
      setActiveSessions([]);
    } finally {
      setSessionsLoading(false);
    }
  };

  const fetchAttendanceHistory = async () => {
    try {
      setHistoryLoading(true);
      console.log('üìã Fetching attendance history...');

      const { data, error } = await supabase
        .from('attendance')
        .select(`
          id,
          timestamp,
          status,
          sessions (
            classes (
              class_name
            )
          )
        `)
        .eq('student_id', currentUser.id)
        .order('timestamp', { ascending: false });

      if (error) throw error;

      const history = data.map(record => ({
        id: record.id,
        class: record.sessions?.classes?.class_name || 'Unknown Class',
        date: new Date(record.timestamp).toLocaleDateString(),
        time: new Date(record.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
        status: record.status || 'absent'
      }));

      console.log('‚úÖ Attendance history retrieved:', history.length, 'records');
      setAttendanceHistory(history);
    } catch (err) {
      console.error('Error fetching attendance history:', err);
      setAttendanceHistory([]);
    } finally {
      setHistoryLoading(false);
    }
  };

  const handleMarkAttendance = useCallback((sessionId) => {
    navigate('/student/mark', { state: { sessionId } });
  }, [navigate]);

  // Memoize table columns to prevent unnecessary re-renders
  const tableColumns = useMemo(() => [
    { key: 'class', label: 'Class' },
    { key: 'date', label: 'Date' },
    { key: 'time', label: 'Time Marked' },
    {
      key: 'status',
      label: 'Status',
      render: (status) => <Badge label={status.toUpperCase()} variant={status === 'present' ? 'success' : status === 'late' ? 'warning' : 'danger'} />,
    },
  ], []);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-blue-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-16 w-16 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600 font-medium">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!currentUser) {
    console.log('üî¥ StudentDashboard: Rendering null - no currentUser');
    return <div style={{ padding: '20px', color: 'red' }}>Error: No user logged in</div>;
  }

  if (userRole !== 'student') {
    console.log('üî¥ StudentDashboard: Rendering null - userRole is:', userRole);
    return <div style={{ padding: '20px', color: 'red' }}>Error: Access denied (role: {userRole})</div>;
  }

  return (
    <Layout role="student">
      <div className="space-y-8">
        {/* Course Attendance Summary */}
        <Card title="üìä Course Attendance Summary">
          {attendanceLoading ? (
            <p className="text-gray-600">Loading attendance data...</p>
          ) : courseAttendance.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {courseAttendance.map((course) => (
                <div key={course.id} className="p-4 bg-white rounded-lg border border-gray-200">
                  <h3 className="font-semibold text-gray-900 mb-2">{course.courseName}</h3>
                  <ProgressCircle
                    percentage={course.percentage}
                    label={`${course.percentage}%`}
                    color={course.percentage >= 75 ? 'green' : 'red'}
                  />
                  <p className="text-sm text-gray-600 text-center mt-2">
                    {course.attendedSessions}/{course.totalSessions} sessions attended
                  </p>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600">No enrollment data available</p>
          )}
        </Card>

        {/* Today's Classes */}
        <Card title="üìÖ Today's Classes">
          {todayClassesLoading ? (
            <p className="text-gray-600">Loading today's classes...</p>
          ) : todayClasses.length > 0 ? (
            <div className="space-y-2">
              {todayClasses.map((cls) => (
                <div key={cls.id} className="p-3 bg-blue-50 rounded-lg border border-blue-200">
                  <p className="font-semibold text-gray-900">{cls.name}</p>
                  <p className="text-xs text-gray-600">
                    üïê {cls.time} | üìç {cls.location}
                  </p>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600">No classes scheduled for today</p>
          )}
        </Card>

        {/* Active Sessions */}
        <Card title="üü¢ Active Sessions">
          {sessionsLoading ? (
            <p className="text-gray-600">Loading active sessions...</p>
          ) : activeSessions.length > 0 ? (
            <div className="space-y-2">
              {activeSessions.map(session => (
                <div key={session.id} className="p-2 bg-green-50 rounded border border-green-200">
                  <p className="font-semibold text-green-900 text-sm">{session.classes?.class_name || 'Class Session'}</p>
                  <p className="text-xs text-green-700">üü¢ Active Now</p>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600">No active sessions</p>
          )}
        </Card>

        {/* Mark Attendance */}
        {activeSessions.length > 0 && (
          <Card title="‚úÖ Mark Attendance">
            <div className="space-y-3">
              <p className="text-gray-600 text-sm">
                {activeSessions.length} active session{activeSessions.length > 1 ? 's' : ''} available
              </p>
              <div className="space-y-2">
                {activeSessions.map(session => (
                  <Button
                    key={session.id}
                    variant="primary"
                    onClick={() => handleMarkAttendance(session.id)}
                    className="w-full"
                  >
                    Mark Attendance for {session.classes?.class_name || 'Session'}
                  </Button>
                ))}
              </div>
            </div>
          </Card>
        )}

        {/* Recent Attendance History */}
        <Card title="üìã Recent Attendance History">
          {historyLoading ? (
            <p className="text-gray-600">Loading attendance history...</p>
          ) : attendanceHistory.length > 0 ? (
            <Table
              columns={tableColumns}
              data={attendanceHistory}
              searchable
              paginated
              itemsPerPage={10}
            />
          ) : (
            <p className="text-gray-600">No attendance history available</p>
          )}
        </Card>
      </div>
    </Layout>
  );
};

export default StudentDashboard;
