import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';

const DelegateDashboard = () => {
  const { currentUser, logout, userRole } = useAuth();

  useEffect(() => {
    if (userRole && userRole !== 'delegate') {
      window.location.href = '/';
    }
  }, [userRole]);

  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [loading, setLoading] = useState(false);
  const [sessions, setSessions] = useState([]);
  const [classStartTime, setClassStartTime] = useState('14:00');
  const [classEndTime, setClassEndTime] = useState('16:00');
  const [courseName, setCourseName] = useState('');

  useEffect(() => {
    fetchSessions();
  }, []);

  const fetchSessions = async () => {
    try {
      const { data, error } = await supabase
        .from('sessions')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;
      setSessions(data || []);
    } catch (err) {
      console.error('Error fetching sessions:', err);
    }
  };

  const createSession = async (e) => {
    e.preventDefault();

    if (!courseName) {
      setMessage('‚ùå Please enter a course name');
      setMessageType('error');
      return;
    }

    if (!currentUser) {
      setMessage('‚ùå Error: Not authenticated');
      setMessageType('error');
      return;
    }

    setLoading(true);

    try {
      setMessage('‚è≥ Creating session...');
      setMessageType('info');

      const { data: classData, error: classError } = await supabase
        .from('classes')
        .insert({
          class_name: courseName,
          delegate_id: currentUser.id,
        })
        .select()
        .single();

      if (classError && classError.code !== '23505') throw classError;

      const classId = classData?.id || currentUser.class_id;

      const sessionStartTime = `${classStartTime}:00`;
      const sessionEndTime = `${classEndTime}:00`;
      const today = new Date().toISOString().split('T')[0];

      const { error: sessionError } = await supabase
        .from('sessions')
        .insert({
          class_id: classId,
          class_start_time: sessionStartTime,
          class_end_time: sessionEndTime,
          session_date: today,
          created_at: new Date().toISOString(),
        });

      if (sessionError) throw sessionError;

      setMessage(
        `‚úÖ Session Created!\nüìö Course: ${courseName}\n‚è∞ Time: ${classStartTime} - ${classEndTime}\nüåç GPS Required for attendance`
      );
      setMessageType('success');

      setCourseName('');
      setClassStartTime('14:00');
      setClassEndTime('16:00');

      setTimeout(fetchSessions, 1000);
    } catch (err) {
      console.error('Error creating session:', err);
      setMessage('‚ùå Error creating session. Please try again.');
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/delegate');
    } catch (err) {
      console.error('Logout error:', err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-indigo-50 p-4">
      <div className="max-w-3xl mx-auto">
        <div className="mb-6 flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Delegate Dashboard</h1>
            <p className="text-gray-600">üìç Create GPS-Based Attendance Sessions</p>
            {currentUser && <p className="text-sm text-gray-500 mt-1">üë§ {currentUser.email}</p>}
          </div>
          <button
            onClick={handleLogout}
            className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg font-medium transition"
          >
            Logout
          </button>
        </div>

        {message && (
          <div
            className={`rounded-lg p-4 mb-6 text-white whitespace-pre-wrap ${
              messageType === 'success'
                ? 'bg-green-500'
                : messageType === 'error'
                ? 'bg-red-500'
                : 'bg-blue-500'
            }`}
          >
            {message}
          </div>
        )}

        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <h2 className="text-xl font-bold text-gray-900 mb-6">üìÖ Create New Session</h2>

          <form onSubmit={createSession} className="space-y-4">
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Course Name
              </label>
              <input
                type="text"
                value={courseName}
                onChange={(e) => setCourseName(e.target.value)}
                disabled={loading}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:bg-gray-100"
                placeholder="e.g., Advanced Web Development"
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Class Start Time
                </label>
                <input
                  type="time"
                  value={classStartTime}
                  onChange={(e) => setClassStartTime(e.target.value)}
                  disabled={loading}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:bg-gray-100"
                />
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Class End Time
                </label>
                <input
                  type="time"
                  value={classEndTime}
                  onChange={(e) => setClassEndTime(e.target.value)}
                  disabled={loading}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:bg-gray-100"
                />
              </div>
            </div>

            <div className="bg-indigo-50 border border-indigo-200 rounded-lg p-4">
              <h3 className="font-semibold text-indigo-900 mb-2">üìç How GPS Attendance Works:</h3>
              <ul className="text-sm text-indigo-800 space-y-1">
                <li>‚úì Students must enable GPS on their phone</li>
                <li>‚úì Location must be verified at ICT University</li>
                <li>‚úì GPS accuracy must be ‚â§50 meters</li>
                <li>‚úì No QR code scanning needed</li>
              </ul>
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full bg-indigo-600 hover:bg-indigo-700 disabled:bg-gray-400 text-white font-semibold py-3 rounded-lg transition"
            >
              {loading ? '‚è≥ Creating...' : 'üìÖ Create Session'}
            </button>
          </form>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">üìã Active Sessions ({sessions.length})</h2>

          {sessions.length === 0 ? (
            <p className="text-gray-600 text-center py-8">No sessions created yet</p>
          ) : (
            <div className="space-y-3">
              {sessions.map((session) => (
                <div
                  key={session.id}
                  className="border border-gray-200 rounded-lg p-4 hover:bg-gray-50 transition"
                >
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-semibold text-gray-900">{session.class_name || 'Session'}</h3>
                      <p className="text-sm text-gray-600">
                        ‚è∞ {session.class_start_time} - {session.class_end_time}
                      </p>
                      <p className="text-xs text-gray-500 mt-1">
                        üìÖ {new Date(session.session_date).toLocaleDateString()}
                      </p>
                    </div>
                    <span className="px-3 py-1 bg-green-100 text-green-800 text-xs font-semibold rounded-full">
                      Active
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DelegateDashboard;
