import React, { createContext, useContext, useEffect, useState } from 'react';
import { supabase } from '../supabase/supabase';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [userRole, setUserRole] = useState(null); // 'student', 'delegate', 'admin'
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Helper function to fetch and update user data
  const updateUserFromDatabase = async (userId) => {
    try {
      const startTime = Date.now();
      console.log('ðŸ“± AuthContext: Fetching user data from database for ID:', userId);
      
      // Query with timeout
      const queryStart = Date.now();
      const { data: userData, error: userError } = await supabase
        .from('users')
        .select('id, role, name, email')
        .eq('id', userId)
        .single();
      
      const queryTime = Date.now() - queryStart;
      console.log(`ðŸ“Š AuthContext: Database query took ${queryTime}ms`);
      
      if (userError) {
        console.warn('âš ï¸ AuthContext: User data fetch failed:', userError.message, 'Code:', userError.code);
        return null;
      }
      
      const totalTime = Date.now() - startTime;
      console.log(`âœ… AuthContext: User data retrieved in ${totalTime}ms - role: ${userData?.role}`);
      return userData;
    } catch (err) {
      console.error('âŒ AuthContext: Error fetching user data:', err.message);
      return null;
    }
  };

  useEffect(() => {
    // Check for existing session on mount
    let unsubscribed = false;

    const initAuth = async () => {
      try {
        console.log('ðŸ“± AuthContext: Initializing auth on mount...');
        
        // Try to get session but don't wait - let the listener handle it
        supabase.auth.getSession().then(({ data: { session }, error: sessionError }) => {
          if (unsubscribed) return;
          
          if (sessionError) {
            console.warn('âš ï¸ AuthContext: Session retrieval error:', sessionError.message);
          }

          if (session?.user) {
            console.log('ðŸ“± AuthContext: Existing session found for user:', session.user.email);
            updateUserFromDatabase(session.user.id).then(userData => {
              if (!unsubscribed) {
                setCurrentUser({ ...session.user, ...(userData || {}) });
                setUserRole(userData?.role || null);
              }
            });
          }
        }).catch(err => {
          console.warn('âš ï¸ AuthContext: Session check failed:', err.message);
        });

        // Set loading to false immediately - don't wait for session check
        if (!unsubscribed) {
          console.log('âœ… AuthContext: Loading complete (no wait)');
          setLoading(false);
        }
      } catch (err) {
        console.error('âŒ AuthContext: Auth initialization error:', err.message);
        setError(err.message);
        if (!unsubscribed) {
          setLoading(false);
        }
      }
    };

    initAuth();

    // Subscribe to auth changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange(async (event, session) => {
      if (unsubscribed) return;
      
      console.log('ðŸ“± AuthContext: Auth state changed - Event type:', event, '| Session exists:', !!session?.user);
      
      try {
        if (session?.user) {
          console.log('ðŸ“± AuthContext: Processing auth state change for user:', session.user.email);
          const userData = await updateUserFromDatabase(session.user.id);
          
          if (!unsubscribed) {
            setCurrentUser({ ...session.user, ...(userData || {}) });
            setUserRole(userData?.role || null);
            console.log('âœ… AuthContext: User state updated - Role:', userData?.role);
          }
        } else {
          console.log('ðŸ“± AuthContext: Auth state change - no session, clearing user');
          if (!unsubscribed) {
            setCurrentUser(null);
            setUserRole(null);
          }
        }
      } catch (err) {
        console.error('âŒ AuthContext: Error processing auth state change:', err.message);
        setError(err.message);
      }
    });

    return () => {
      unsubscribed = true;
      if (subscription) {
        subscription.unsubscribe();
      }
    };
  }, []);

  const login = async (email, password) => {
    try {
      console.log('ðŸ“± AuthContext.login: Starting login process for:', email);
      setError(null);
      
      const { data, error } = await supabase.auth.signInWithPassword({ email, password });
      
      if (error) {
        console.error('âŒ AuthContext.login: Supabase auth error:', error.message);
        throw error;
      }
      
      console.log('âœ… AuthContext.login: Auth successful for user:', data.user?.email);
      
      // Set user immediately WITHOUT waiting for database - NON-BLOCKING LOGIN
      setCurrentUser(data.user);
      console.log('âš¡ AuthContext.login: User set immediately (role will load in background)');
      
      // Fetch role data in background - don't wait
      if (data.user?.id) {
        updateUserFromDatabase(data.user.id).then(userData => {
          if (userData?.role) {
            setUserRole(userData.role);
            console.log('âœ… AuthContext.login: Role loaded in background - role:', userData.role);
          }
        }).catch(err => {
          console.warn('âš ï¸ AuthContext.login: Background role fetch failed:', err.message);
        });
      }
      
      return data.user;
    } catch (err) {
      console.error('âŒ AuthContext.login: Exception during login:', err.message);
      setError(err.message);
      throw err;
    }
  };

  const logout = async () => {
    const { error } = await supabase.auth.signOut();
    if (error) throw error;
  };

  const value = {
    currentUser,
    userRole,
    login,
    logout,
    loading,
    error,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
