import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import StatCard from '../components/StatCard';
import Card from '../components/Card';
import Table from '../components/Table';
import Badge from '../components/Badge';
import Button from '../components/Button';
import Alert from '../components/Alert';

const AdminDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  
  // State for stats
  const [stats, setStats] = useState({
    totalStudents: 0,
    totalDelegates: 0,
    totalCourses: 0,
    totalSessions: 0,
  });
  
  // State for user management
  const [students, setStudents] = useState([]);
  const [delegates, setDelegates] = useState([]);
  const [showAddStudent, setShowAddStudent] = useState(false);
  const [showAddDelegate, setShowAddDelegate] = useState(false);
  const [newStudent, setNewStudent] = useState({
    name: '',
    matricule: '',
    email: '',
    password: ''
  });
  const [newDelegate, setNewDelegate] = useState({
    name: '',
    matricule: '',
    email: '',
    password: ''
  });
  
  // State for course management
  const [courses, setCourses] = useState([]);
  const [showAddCourse, setShowAddCourse] = useState(false);
  const [newCourse, setNewCourse] = useState({
    course_code: '',
    course_title: '',
    program: '',
    year_level: ''
  });
  
  // State for attendance reports
  const [attendanceReports, setAttendanceReports] = useState([]);
  const [filterByStudent, setFilterByStudent] = useState('');
  const [filterByCourse, setFilterByCourse] = useState('');
  
  const [loadingData, setLoadingData] = useState(true);
  const [addingUser, setAddingUser] = useState(false);
  const [addingCourse, setAddingCourse] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    if (loading) return;

    if (!currentUser || userRole !== 'admin') {
      navigate('/admin');
      return;
    }

    fetchData();
  }, [currentUser, userRole, loading, navigate]);

  const fetchData = async () => {
    try {
      setLoadingData(true);

      // Fetch stats
      const [studentsRes, delegatesRes, coursesRes, sessionsRes] = await Promise.all([
        supabase.from('users').select('id', { count: 'exact' }).eq('role', 'student'),
        supabase.from('users').select('id', { count: 'exact' }).eq('role', 'delegate'),
        supabase.from('courses').select('id', { count: 'exact' }),
        supabase.from('sessions').select('id', { count: 'exact' }),
      ]);

      setStats({
        totalStudents: studentsRes.count || 0,
        totalDelegates: delegatesRes.count || 0,
        totalCourses: coursesRes.count || 0,
        totalSessions: sessionsRes.count || 0,
      });

      // Fetch students
      const { data: studentsData } = await supabase
        .from('users')
        .select('id, name, matricule, email, created_at')
        .eq('role', 'student')
        .order('created_at', { ascending: false });

      setStudents(studentsData || []);

      // Fetch delegates
      const { data: delegatesData } = await supabase
        .from('users')
        .select('id, name, matricule, email, created_at')
        .eq('role', 'delegate')
        .order('created_at', { ascending: false });

      setDelegates(delegatesData || []);

      // Fetch courses
      const { data: coursesData } = await supabase
        .from('courses')
        .select('*')
        .order('course_code', { ascending: true });

      setCourses(coursesData || []);

      // Fetch attendance reports
      const { data: attendanceData } = await supabase
        .from('attendance')
        .select(`
          id,
          student_id,
          created_at,
          users (
            name,
            matricule
          ),
          sessions (
            session_date,
            classes (
              courses (
                course_code,
                course_title
              )
            )
          )
        `)
        .order('created_at', { ascending: false })
        .limit(100);

      setAttendanceReports(attendanceData || []);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load dashboard data');
    } finally {
      setLoadingData(false);
    }
  };

  const handleAddStudent = async () => {
    if (!newStudent.name || !newStudent.matricule || !newStudent.email || !newStudent.password) {
      setError('Please fill in all fields');
      return;
    }

    if (newStudent.password.length < 8) {
      setError('Password must be at least 8 characters');
      return;
    }

    try {
      setAddingUser(true);
      setError(null);

      // Create auth user
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email: newStudent.email,
        password: newStudent.password,
      });

      if (authError) throw authError;

      // Create user record
      const { error: userError } = await supabase
        .from('users')
        .insert({
          id: authData.user.id,
          name: newStudent.name,
          matricule: newStudent.matricule,
          email: newStudent.email,
          role: 'student'
        });

      if (userError) throw userError;

      setSuccess('✅ Student added successfully!');
      setShowAddStudent(false);
      setNewStudent({ name: '', matricule: '', email: '', password: '' });
      fetchData();

      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error adding student:', err);
      setError(`❌ Error: ${err.message}`);
    } finally {
      setAddingUser(false);
    }
  };

  const handleAddDelegate = async () => {
    if (!newDelegate.name || !newDelegate.matricule || !newDelegate.email || !newDelegate.password) {
      setError('Please fill in all fields');
      return;
    }

    if (newDelegate.password.length < 8) {
      setError('Password must be at least 8 characters');
      return;
    }

    try {
      setAddingUser(true);
      setError(null);

      // Create auth user
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email: newDelegate.email,
        password: newDelegate.password,
      });

      if (authError) throw authError;

      // Create user record
      const { error: userError } = await supabase
        .from('users')
        .insert({
          id: authData.user.id,
          name: newDelegate.name,
          matricule: newDelegate.matricule,
          email: newDelegate.email,
          role: 'delegate'
        });

      if (userError) throw userError;

      setSuccess('✅ Delegate added successfully!');
      setShowAddDelegate(false);
      setNewDelegate({ name: '', matricule: '', email: '', password: '' });
      fetchData();

      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error adding delegate:', err);
      setError(`❌ Error: ${err.message}`);
    } finally {
      setAddingUser(false);
    }
  };

  const handleAddCourse = async () => {
    if (!newCourse.course_code || !newCourse.course_title || !newCourse.program) {
      setError('Please fill in all required fields');
      return;
    }

    try {
      setAddingCourse(true);
      setError(null);

      const { error } = await supabase
        .from('courses')
        .insert({
          course_code: newCourse.course_code,
          course_title: newCourse.course_title,
          program: newCourse.program,
          year_level: newCourse.year_level || ''
        });

      if (error) throw error;

      setSuccess('✅ Course added successfully!');
      setShowAddCourse(false);
      setNewCourse({ course_code: '', course_title: '', program: '', year_level: '' });
      fetchData();

      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error adding course:', err);
      setError(`❌ Error: ${err.message}`);
    } finally {
      setAddingCourse(false);
    }
  };

  const handleDeleteStudent = async (studentId) => {
    if (!window.confirm('Are you sure you want to delete this student?')) return;

    try {
      const { error } = await supabase
        .from('users')
        .delete()
        .eq('id', studentId);

      if (error) throw error;

      setSuccess('✅ Student deleted successfully!');
      fetchData();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error deleting student:', err);
      setError(`❌ Error: ${err.message}`);
    }
  };

  const handleDeleteDelegate = async (delegateId) => {
    if (!window.confirm('Are you sure you want to delete this delegate?')) return;

    try {
      const { error } = await supabase
        .from('users')
        .delete()
        .eq('id', delegateId);

      if (error) throw error;

      setSuccess('✅ Delegate deleted successfully!');
      fetchData();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error deleting delegate:', err);
      setError(`❌ Error: ${err.message}`);
    }
  };

  const handleDeleteCourse = async (courseId) => {
    if (!window.confirm('Are you sure you want to delete this course?')) return;

    try {
      const { error } = await supabase
        .from('courses')
        .delete()
        .eq('id', courseId);

      if (error) throw error;

      setSuccess('✅ Course deleted successfully!');
      fetchData();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error deleting course:', err);
      setError(`❌ Error: ${err.message}`);
    }
  };

  if (loading || loadingData) {
    return (
      <div className="flex items-center justify-center h-screen bg-blue-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-16 w-16 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600 font-medium">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!currentUser || userRole !== 'admin') {
    return null;
  }

  return (
    <Layout role="admin">
      <div className="space-y-8">
        <div>
          <h1 className="text-3xl font-bold mb-2">Admin Dashboard</h1>
          <p className="text-gray-600">System management and attendance overview</p>
        </div>

        {error && (
          <Alert type="error" title="Error" message={error} />
        )}

        {success && (
          <Alert type="success" title="Success" message={success} />
        )}

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <StatCard title="Total Students" value={stats.totalStudents} icon="👥" color="blue" />
          <StatCard title="Total Delegates" value={stats.totalDelegates} icon="🧑‍🏫" color="green" />
          <StatCard title="Total Courses" value={stats.totalCourses} icon="📚" color="amber" />
          <StatCard title="Total Sessions" value={stats.totalSessions} icon="📋" color="purple" />
        </div>

        {/* User Management Section */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Add Student */}
          <Card title="➕ Add New Student">
            <div className="space-y-4">
              <Button
                variant="primary"
                onClick={() => setShowAddStudent(!showAddStudent)}
              >
                {showAddStudent ? '✕ Cancel' : '+ Add Student'}
              </Button>

              {showAddStudent && (
                <div className="space-y-4 p-4 bg-blue-50 rounded-lg border border-blue-200">
                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">Full Name</label>
                    <input
                      type="text"
                      value={newStudent.name}
                      onChange={(e) => setNewStudent(prev => ({ ...prev, name: e.target.value }))}
                      placeholder="Enter full name"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">Matricule</label>
                    <input
                      type="text"
                      value={newStudent.matricule}
                      onChange={(e) => setNewStudent(prev => ({ ...prev, matricule: e.target.value }))}
                      placeholder="Enter matricule"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">Email</label>
                    <input
                      type="email"
                      value={newStudent.email}
                      onChange={(e) => setNewStudent(prev => ({ ...prev, email: e.target.value }))}
                      placeholder="student@ictuniversity.edu.cm"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">Password (min 8 characters)</label>
                    <input
                      type="password"
                      value={newStudent.password}
                      onChange={(e) => setNewStudent(prev => ({ ...prev, password: e.target.value }))}
                      placeholder="Enter password"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <Button
                    variant="primary"
                    onClick={handleAddStudent}
                    disabled={addingUser}
                  >
                    {addingUser ? '⏳ Adding...' : '✅ Add Student'}
                  </Button>
                </div>
              )}
            </div>
          </Card>

          {/* Add Delegate */}
          <Card title="➕ Add New Delegate">
            <div className="space-y-4">
              <Button
                variant="primary"
                onClick={() => setShowAddDelegate(!showAddDelegate)}
              >
                {showAddDelegate ? '✕ Cancel' : '+ Add Delegate'}
              </Button>

              {showAddDelegate && (
                <div className="space-y-4 p-4 bg-green-50 rounded-lg border border-green-200">
                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">Full Name</label>
                    <input
                      type="text"
                      value={newDelegate.name}
                      onChange={(e) => setNewDelegate(prev => ({ ...prev, name: e.target.value }))}
                      placeholder="Enter full name"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">Matricule</label>
                    <input
                      type="text"
                      value={newDelegate.matricule}
                      onChange={(e) => setNewDelegate(prev => ({ ...prev, matricule: e.target.value }))}
                      placeholder="Enter matricule"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">Email</label>
                    <input
                      type="email"
                      value={newDelegate.email}
                      onChange={(e) => setNewDelegate(prev => ({ ...prev, email: e.target.value }))}
                      placeholder="delegate@ictuniversity.edu.cm"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">Password (min 8 characters)</label>
                    <input
                      type="password"
                      value={newDelegate.password}
                      onChange={(e) => setNewDelegate(prev => ({ ...prev, password: e.target.value }))}
                      placeholder="Enter password"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                    />
                  </div>

                  <Button
                    variant="primary"
                    onClick={handleAddDelegate}
                    disabled={addingUser}
                  >
                    {addingUser ? '⏳ Adding...' : '✅ Add Delegate'}
                  </Button>
                </div>
              )}
            </div>
          </Card>
        </div>

        {/* Course Management */}
        <Card title="📚 Course Management">
          <div className="space-y-4">
            <Button
              variant="primary"
              onClick={() => setShowAddCourse(!showAddCourse)}
            >
              {showAddCourse ? '✕ Cancel' : '+ Add Course'}
            </Button>

            {showAddCourse && (
              <div className="space-y-4 p-4 bg-amber-50 rounded-lg border border-amber-200">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">Course Code</label>
                    <input
                      type="text"
                      value={newCourse.course_code}
                      onChange={(e) => setNewCourse(prev => ({ ...prev, course_code: e.target.value }))}
                      placeholder="e.g., ICT101"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-amber-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">Program</label>
                    <input
                      type="text"
                      value={newCourse.program}
                      onChange={(e) => setNewCourse(prev => ({ ...prev, program: e.target.value }))}
                      placeholder="e.g., Bachelor of Science"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-amber-500"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">Course Title</label>
                  <input
                    type="text"
                    value={newCourse.course_title}
                    onChange={(e) => setNewCourse(prev => ({ ...prev, course_title: e.target.value }))}
                    placeholder="Enter course title"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-amber-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">Year Level</label>
                  <input
                    type="text"
                    value={newCourse.year_level}
                    onChange={(e) => setNewCourse(prev => ({ ...prev, year_level: e.target.value }))}
                    placeholder="e.g., Year 1"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-amber-500"
                  />
                </div>

                <Button
                  variant="primary"
                  onClick={handleAddCourse}
                  disabled={addingCourse}
                >
                  {addingCourse ? '⏳ Adding...' : '✅ Add Course'}
                </Button>
              </div>
            )}

            {courses.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="min-w-full border-collapse">
                  <thead>
                    <tr className="bg-gray-100 border-b">
                      <th className="px-4 py-2 text-left font-semibold text-gray-700">Code</th>
                      <th className="px-4 py-2 text-left font-semibold text-gray-700">Title</th>
                      <th className="px-4 py-2 text-left font-semibold text-gray-700">Program</th>
                      <th className="px-4 py-2 text-left font-semibold text-gray-700">Year</th>
                      <th className="px-4 py-2 text-center font-semibold text-gray-700">Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {courses.map((course) => (
                      <tr key={course.id} className="border-b hover:bg-gray-50">
                        <td className="px-4 py-2 text-gray-900 font-semibold">{course.course_code}</td>
                        <td className="px-4 py-2 text-gray-600">{course.course_title}</td>
                        <td className="px-4 py-2 text-gray-600">{course.program}</td>
                        <td className="px-4 py-2 text-gray-600">{course.year_level || '-'}</td>
                        <td className="px-4 py-2 text-center">
                          <Button
                            variant="danger"
                            size="sm"
                            onClick={() => handleDeleteCourse(course.id)}
                          >
                            Delete
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p className="text-gray-600">No courses added yet</p>
            )}
          </div>
        </Card>

        {/* Student Management */}
        <Card title="👥 Student Management">
          {students.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="min-w-full border-collapse">
                <thead>
                  <tr className="bg-gray-100 border-b">
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Name</th>
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Matricule</th>
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Email</th>
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Registered</th>
                    <th className="px-4 py-2 text-center font-semibold text-gray-700">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {students.map((student) => (
                    <tr key={student.id} className="border-b hover:bg-gray-50">
                      <td className="px-4 py-2 text-gray-900 font-semibold">{student.name}</td>
                      <td className="px-4 py-2 text-gray-600">{student.matricule}</td>
                      <td className="px-4 py-2 text-gray-600 text-sm">{student.email}</td>
                      <td className="px-4 py-2 text-gray-600 text-sm">{new Date(student.created_at).toLocaleDateString()}</td>
                      <td className="px-4 py-2 text-center">
                        <Button
                          variant="danger"
                          size="sm"
                          onClick={() => handleDeleteStudent(student.id)}
                        >
                          Delete
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-gray-600">No students registered</p>
          )}
        </Card>

        {/* Delegate Management */}
        <Card title="🧑‍🏫 Delegate Management">
          {delegates.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="min-w-full border-collapse">
                <thead>
                  <tr className="bg-gray-100 border-b">
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Name</th>
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Matricule</th>
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Email</th>
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Registered</th>
                    <th className="px-4 py-2 text-center font-semibold text-gray-700">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {delegates.map((delegate) => (
                    <tr key={delegate.id} className="border-b hover:bg-gray-50">
                      <td className="px-4 py-2 text-gray-900 font-semibold">{delegate.name}</td>
                      <td className="px-4 py-2 text-gray-600">{delegate.matricule}</td>
                      <td className="px-4 py-2 text-gray-600 text-sm">{delegate.email}</td>
                      <td className="px-4 py-2 text-gray-600 text-sm">{new Date(delegate.created_at).toLocaleDateString()}</td>
                      <td className="px-4 py-2 text-center">
                        <Button
                          variant="danger"
                          size="sm"
                          onClick={() => handleDeleteDelegate(delegate.id)}
                        >
                          Delete
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-gray-600">No delegates assigned</p>
          )}
        </Card>

        {/* Attendance Reports */}
        <Card title="📊 Attendance Reports">
          {attendanceReports.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="min-w-full border-collapse">
                <thead>
                  <tr className="bg-gray-100 border-b">
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Student</th>
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Matricule</th>
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Course</th>
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Date</th>
                    <th className="px-4 py-2 text-left font-semibold text-gray-700">Time</th>
                  </tr>
                </thead>
                <tbody>
                  {attendanceReports.map((record) => (
                    <tr key={record.id} className="border-b hover:bg-gray-50">
                      <td className="px-4 py-2 text-gray-900 font-semibold">{record.users?.name}</td>
                      <td className="px-4 py-2 text-gray-600">{record.users?.matricule}</td>
                      <td className="px-4 py-2 text-gray-600">
                        {record.sessions?.classes?.courses?.course_code}
                      </td>
                      <td className="px-4 py-2 text-gray-600 text-sm">{record.sessions?.session_date}</td>
                      <td className="px-4 py-2 text-gray-600 text-sm">
                        {new Date(record.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-gray-600">No attendance records yet</p>
          )}
        </Card>
      </div>
    </Layout>
  );
};

export default AdminDashboard;
