import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import { calculateHaversineDistance } from '../utils/gpsUtils';

const StudentDashboard = () => {
  const { currentUser, logout, userRole } = useAuth();
  
  useEffect(() => {
    if (userRole && userRole !== 'student') {
      window.location.href = '/';
    }
  }, [userRole]);

  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [gpsStatus, setGpsStatus] = useState('disabled');
  const [gpsData, setGpsData] = useState(null);
  const [distance, setDistance] = useState(null);
  const [loading, setLoading] = useState(false);
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('');

  const CAMPUS_LAT = 3.8621;
  const CAMPUS_LON = 11.5021;
  const GEOFENCE_RADIUS = 200;
  const MAX_GPS_ACCURACY = 50;

  useEffect(() => {
    requestGPSPermission();
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const { data, error } = await supabase
        .from('classes')
        .select('id, class_name')
        .limit(10);
      if (error) throw error;
      setCourses(data || []);
    } catch (err) {
      console.error('Error fetching courses:', err);
    }
  };

  const requestGPSPermission = () => {
    if (!navigator.geolocation) {
      setGpsStatus('error');
      setMessage('‚ùå Geolocation is not supported by this device');
      setMessageType('error');
      return;
    }

    setGpsStatus('acquiring');
    setMessage('üìç Acquiring GPS location...');
    setMessageType('info');

    const watchId = navigator.geolocation.watchPosition(
      (position) => {
        const { latitude, longitude, accuracy } = position.coords;
        setGpsData({ latitude, longitude, accuracy });

        const dist = calculateHaversineDistance(latitude, longitude, CAMPUS_LAT, CAMPUS_LON);
        setDistance(dist);

        if (accuracy <= MAX_GPS_ACCURACY && dist <= GEOFENCE_RADIUS) {
          setGpsStatus('ready');
          setMessage(
            `‚úÖ GPS Ready | Distance: ${dist.toFixed(0)}m | Accuracy: ${accuracy.toFixed(0)}m`
          );
          setMessageType('info');
        } else if (accuracy > MAX_GPS_ACCURACY) {
          setGpsStatus('acquiring');
          setMessage(
            `üìç GPS signal weak | Accuracy: ${accuracy.toFixed(0)}m (need ‚â§${MAX_GPS_ACCURACY}m) | Distance: ${dist.toFixed(0)}m`
          );
          setMessageType('info');
        } else if (dist > GEOFENCE_RADIUS) {
          setGpsStatus('error');
          setMessage(
            `‚ùå Outside campus | Distance: ${dist.toFixed(0)}m away (allowed: ‚â§${GEOFENCE_RADIUS}m)`
          );
          setMessageType('error');
        }
      },
      (error) => {
        setGpsStatus('error');
        let errorMsg = '‚ùå GPS Error: ';
        switch (error.code) {
          case error.PERMISSION_DENIED:
            errorMsg += 'Permission denied. Please enable location access.';
            break;
          case error.POSITION_UNAVAILABLE:
            errorMsg += 'Position unavailable. Check GPS settings.';
            break;
          case error.TIMEOUT:
            errorMsg += 'GPS acquisition timeout. Try again.';
            break;
          default:
            errorMsg += 'Unknown GPS error.';
        }
        setMessage(errorMsg);
        setMessageType('error');
      },
      {
        enableHighAccuracy: true,
        maximumAge: 0,
        timeout: 10000,
      }
    );

    return () => navigator.geolocation.clearWatch(watchId);
  };

  const markAttendance = async () => {
    setLoading(true);

    try {
      if (!gpsData) {
        setMessage('‚ùå GPS data not available. Please wait...');
        setMessageType('error');
        setLoading(false);
        return;
      }

      if (gpsData.accuracy > MAX_GPS_ACCURACY) {
        setMessage(`‚ùå GPS accuracy too low: ${gpsData.accuracy.toFixed(0)}m (required: ‚â§${MAX_GPS_ACCURACY}m). Move to open area.`);
        setMessageType('error');
        setLoading(false);
        return;
      }

      if (distance > GEOFENCE_RADIUS) {
        setMessage(`‚ùå Outside campus boundary: ${distance.toFixed(0)}m away (allowed: ‚â§${GEOFENCE_RADIUS}m). Enter campus.`);
        setMessageType('error');
        setLoading(false);
        return;
      }

      if (!selectedCourse) {
        setMessage('‚ùå Please select a course');
        setMessageType('error');
        setLoading(false);
        return;
      }

      setMessage('‚è≥ Processing attendance...');
      setMessageType('info');

      const { error } = await supabase.from('attendance').insert({
        class_id: selectedCourse,
        student_id: currentUser.id,
        latitude: gpsData.latitude,
        longitude: gpsData.longitude,
        accuracy: gpsData.accuracy,
        distance_from_campus: distance,
        gps_validated_at: new Date().toISOString(),
      });

      if (error) {
        if (error.code === '23505') {
          setMessage('‚ö†Ô∏è Attendance already marked for this course today');
          setMessageType('info');
        } else {
          throw error;
        }
      } else {
        setMessage(
          `‚úÖ Attendance Recorded!\nüìç Distance: ${distance.toFixed(0)}m\nüì∂ Accuracy: ${gpsData.accuracy.toFixed(0)}m`
        );
        setMessageType('success');
      }
    } catch (err) {
      console.error('Attendance error:', err);
      setMessage('‚ùå Error recording attendance. Please try again.');
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/student');
    } catch (err) {
      console.error('Logout error:', err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 p-4">
      <div className="max-w-2xl mx-auto">
        <div className="mb-6 flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Student Dashboard</h1>
            <p className="text-gray-600">üìç GPS-Based Attendance</p>
            {currentUser && <p className="text-sm text-gray-500 mt-1">üë§ {currentUser.email}</p>}
          </div>
          <button
            onClick={handleLogout}
            className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg font-medium transition"
          >
            Logout
          </button>
        </div>

        {message && (
          <div
            className={`rounded-lg p-4 mb-6 text-white whitespace-pre-wrap ${
              messageType === 'success'
                ? 'bg-green-500'
                : messageType === 'error'
                ? 'bg-red-500'
                : 'bg-blue-500'
            }`}
          >
            {message}
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-bold text-gray-900 mb-4">üìç GPS Status</h2>
            <div
              className={`rounded-lg p-4 mb-4 text-white text-center font-semibold ${
                gpsStatus === 'ready'
                  ? 'bg-green-500'
                  : gpsStatus === 'acquiring'
                  ? 'bg-yellow-500'
                  : 'bg-red-500'
              }`}
            >
              {gpsStatus === 'ready' ? '‚úÖ Ready' : gpsStatus === 'acquiring' ? '‚è≥ Acquiring...' : '‚ùå Error'}
            </div>

            {gpsData && (
              <div className="text-sm text-gray-700 space-y-2">
                <p><strong>Latitude:</strong> {gpsData.latitude.toFixed(6)}¬∞</p>
                <p><strong>Longitude:</strong> {gpsData.longitude.toFixed(6)}¬∞</p>
                <p>
                  <strong>Accuracy:</strong> <span className={gpsData.accuracy <= MAX_GPS_ACCURACY ? 'text-green-600' : 'text-red-600'}>
                    {gpsData.accuracy.toFixed(1)}m {gpsData.accuracy <= MAX_GPS_ACCURACY ? '‚úì' : '‚úó'}
                  </span>
                </p>
                <p>
                  <strong>Distance:</strong> <span className={distance <= GEOFENCE_RADIUS ? 'text-green-600' : 'text-red-600'}>
                    {distance?.toFixed(0)}m {distance <= GEOFENCE_RADIUS ? '‚úì' : '‚úó'}
                  </span>
                </p>
              </div>
            )}
          </div>

          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-bold text-gray-900 mb-4">üìö Mark Attendance</h2>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Select Course
                </label>
                <select
                  value={selectedCourse}
                  onChange={(e) => setSelectedCourse(e.target.value)}
                  disabled={loading}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:bg-gray-100"
                >
                  <option value="">Choose a course...</option>
                  {courses.map((course) => (
                    <option key={course.id} value={course.id}>
                      {course.class_name}
                    </option>
                  ))}
                </select>
              </div>

              <button
                onClick={markAttendance}
                disabled={loading || gpsStatus !== 'ready' || !selectedCourse}
                className="w-full bg-indigo-600 hover:bg-indigo-700 disabled:bg-gray-400 text-white font-semibold py-3 rounded-lg transition"
              >
                {loading ? '‚è≥ Processing...' : 'üìç Mark Attendance'}
              </button>
            </div>

            <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-lg text-xs text-blue-800">
              <p className="font-semibold mb-1">Requirements:</p>
              <ul className="space-y-1">
                <li>‚úì GPS accuracy ‚â§ 50m</li>
                <li>‚úì Within 200m of campus</li>
                <li>‚úì Course selected</li>
              </ul>
            </div>
          </div>
        </div>

        <div className="mt-6 bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-bold text-gray-900 mb-3">üìç Campus Information</h3>
          <p className="text-gray-700 mb-2">
            <strong>Location:</strong> ICT University Yaound√©, Cameroon
          </p>
          <p className="text-gray-700 mb-2">
            <strong>Coordinates:</strong> 3.8621¬∞N, 11.5021¬∞E
          </p>
          <p className="text-gray-700">
            <strong>Geofence Radius:</strong> 200 meters
          </p>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
