import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Button from '../components/Button';
import Badge from '../components/Badge';
import Alert from '../components/Alert';

const StudentDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  
  // Active session state
  const [activeSession, setActiveSession] = useState(null);
  const [sessionsLoading, setSessionsLoading] = useState(true);
  const [marking, setMarking] = useState(false);
  const [timeRemaining, setTimeRemaining] = useState(null);
  
  // Attendance summary
  const [attendanceSummary, setAttendanceSummary] = useState({
    totalAttended: 0,
    totalSessions: 0,
    percentage: 0
  });
  const [summaryLoading, setSummaryLoading] = useState(true);
  
  // Course attendance
  const [courseAttendance, setCourseAttendance] = useState([]);
  const [courseLoading, setCourseLoading] = useState(true);
  
  // UI state
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  // Protect route - only allow students
  useEffect(() => {
    if (loading) return;

    if (!currentUser || userRole !== 'student') {
      navigate('/student');
      return;
    }

    fetchActiveSession();
    fetchAttendanceSummary();
    fetchCourseAttendance();
  }, [currentUser, userRole, loading, navigate]);

  // Timer for session countdown
  useEffect(() => {
    if (!activeSession) return;

    const timer = setInterval(() => {
      const now = new Date();
      const [endHour, endMin] = activeSession.end_time.split(':');
      
      const sessionDate = new Date(activeSession.session_date);
      const endTime = new Date(sessionDate);
      endTime.setHours(parseInt(endHour), parseInt(endMin), 0);
      
      const diffMs = endTime - now;
      
      if (diffMs <= 0) {
        setTimeRemaining(null);
        setActiveSession(null);
        clearInterval(timer);
      } else {
        const minutes = Math.floor(diffMs / 60000);
        const seconds = Math.floor((diffMs % 60000) / 1000);
        setTimeRemaining(`${minutes} min ${seconds} sec remaining`);
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [activeSession]);

  const fetchActiveSessions = async () => {
    try {
      setSessionsLoading(true);
      const now = new Date();

      // Get active sessions where student can mark attendance
      const { data, error } = await supabase
        .from('sessions')
        .select(`
          id,
          session_date,
          start_time,
          end_time,
          classes (
            id,
            class_name,
            courses (
              course_code,
              course_title
            )
          )
        `)
        .order('start_time', { ascending: false });

      if (error) throw error;
      
      // Filter to show only sessions that haven't passed
      const filteredSessions = (data || []).filter(session => {
        if (!session.session_date || !session.end_time) return false;
        
        // Parse the session date and end time
        const sessionDate = new Date(session.session_date);
        const [endHour, endMin] = session.end_time.split(':');
        
        // Create the session end time
        const sessionEndTime = new Date(sessionDate);
        sessionEndTime.setHours(parseInt(endHour), parseInt(endMin), 0);
        
        // Only show sessions that haven't ended yet
        return now <= sessionEndTime;
      });
      
      setActiveSessions(filteredSessions);
    } catch (err) {
      console.error('Error fetching active sessions:', err);
      setActiveSessions([]);
    } finally {
      setSessionsLoading(false);
    }
  };

  const fetchCourseAttendance = async () => {
    try {
      setAttendanceLoading(true);

      // Get all attendance records for this student
      const { data: attendanceRecords, error } = await supabase
        .from('attendance')
        .select(`
          id,
          created_at,
          sessions (
            id,
            session_date,
            start_time,
            end_time,
            classes (
              id,
              class_name,
              courses (
                id,
                course_code,
                course_title
              )
            )
          )
        `)
        .eq('student_id', currentUser.id)
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Group by course
      const courseMap = {};
      (attendanceRecords || []).forEach(record => {
        const course = record.sessions?.classes?.courses;
        const courseKey = course?.id;
        
        if (courseKey) {
          if (!courseMap[courseKey]) {
            courseMap[courseKey] = {
              id: courseKey,
              code: course.course_code,
              title: course.course_title,
              totalSessions: 0,
              attendedSessions: 0,
              records: []
            };
          }
          courseMap[courseKey].attendedSessions++;
          courseMap[courseKey].records.push({
            id: record.id,
            date: record.sessions.session_date,
            time: new Date(record.created_at).toLocaleTimeString([], { 
              hour: '2-digit', 
              minute: '2-digit' 
            })
          });
        }
      });

      // Get total sessions per course
      const { data: allSessions } = await supabase
        .from('sessions')
        .select(`
          classes (
            courses (
              id
            )
          )
        `);

      Object.values(courseMap).forEach(course => {
        const sessionsForCourse = (allSessions || []).filter(
          s => s.classes?.courses?.id === course.id
        ).length;
        course.totalSessions = sessionsForCourse || course.attendedSessions;
        course.percentage = course.totalSessions > 0 
          ? Math.round((course.attendedSessions / course.totalSessions) * 100)
          : 0;
        course.eligible = course.percentage >= 75;
      });

      const courseAttendanceArray = Object.values(courseMap);
      setCourseAttendance(courseAttendanceArray);

      // Calculate summary
      const totalAttended = courseAttendanceArray.reduce((sum, course) => sum + course.attendedSessions, 0);
      const totalSessions = courseAttendanceArray.reduce((sum, course) => sum + course.totalSessions, 0);
      const percentage = totalSessions > 0 ? Math.round((totalAttended / totalSessions) * 100) : 0;
      
      setAttendanceSummary({
        totalAttended,
        totalSessions,
        percentage
      });
    } catch (err) {
      console.error('Error fetching attendance:', err);
      setCourseAttendance([]);
    } finally {
      setAttendanceLoading(false);
    }
  };

  const fetchEnrolledCourses = async () => {
    try {
      setCoursesLoading(true);

      // Get all sessions this student attended to determine enrolled courses
      const { data: attendanceRecords } = await supabase
        .from('attendance')
        .select(`
          sessions (
            classes (
              courses (
                id,
                course_code,
                course_title,
                program,
                year_level
              )
            )
          )
        `)
        .eq('student_id', currentUser.id);

      // Get unique courses
      const courseMap = {};
      (attendanceRecords || []).forEach(record => {
        const course = record.sessions?.classes?.courses;
        if (course && !courseMap[course.id]) {
          courseMap[course.id] = course;
        }
      });

      setEnrolledCourses(Object.values(courseMap));
    } catch (err) {
      console.error('Error fetching enrolled courses:', err);
      setEnrolledCourses([]);
    } finally {
      setCoursesLoading(false);
    }
  };

  const handleMarkAttendance = async () => {
    if (!selectedSession) {
      setError('Please select a session first');
      return;
    }

    try {
      setMarking(true);
      setError(null);

      // Check current time
      const now = new Date();
      const [startHour, startMin] = selectedSession.start_time.split(':');
      const [endHour, endMin] = selectedSession.end_time.split(':');

      const startTime = new Date();
      startTime.setHours(parseInt(startHour), parseInt(startMin), 0);

      const endTime = new Date();
      endTime.setHours(parseInt(endHour), parseInt(endMin), 59);

      if (now < startTime || now > endTime) {
        setError('❌ You can only mark attendance during the session time window');
        return;
      }

      // Check if already marked
      const { data: existing } = await supabase
        .from('attendance')
        .select('id')
        .eq('session_id', selectedSession.id)
        .eq('student_id', currentUser.id)
        .single();

      if (existing) {
        setError('❌ You have already marked attendance for this session');
        return;
      }

      // Mark attendance
      const { error: insertError } = await supabase
        .from('attendance')
        .insert({
          session_id: selectedSession.id,
          student_id: currentUser.id,
          created_at: new Date().toISOString()
        });

      if (insertError) throw insertError;

      setSuccess('✅ Attendance marked successfully!');
      setSelectedSession(null);
      fetchActiveSessions();
      fetchCourseAttendance();

      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error marking attendance:', err);
      setError(`❌ Error: ${err.message}`);
    } finally {
      setMarking(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-blue-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-16 w-16 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600 font-medium">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!currentUser || userRole !== 'student') {
    return null;
  }

  return (
    <Layout role="student">
      <div className="max-w-6xl mx-auto space-y-8">
        {/* Alerts */}
        {error && (
          <Alert type="error" title="Error" message={error} />
        )}

        {success && (
          <Alert type="success" title="Success" message={success} />
        )}

        {/* ===== SECTION 1: MARK ATTENDANCE ===== */}
        <div className="bg-gradient-to-br from-blue-50 to-indigo-50 rounded-2xl shadow-lg border border-blue-100 overflow-hidden">
          <Card title="✅ Mark Attendance" className="border-0 shadow-none bg-transparent">
            {sessionsLoading ? (
              <div className="flex items-center justify-center py-8">
                <div className="text-center">
                  <div className="inline-block animate-spin rounded-full h-10 w-10 border-2 border-blue-200 border-t-blue-600 mb-2" />
                  <p className="text-gray-600">Loading active sessions...</p>
                </div>
              </div>
            ) : activeSessions.length > 0 ? (
              <div className="space-y-6">
                {/* Session Selector */}
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">
                    Select Active Session
                  </label>
                  <select
                    value={selectedSession?.id || ''}
                    onChange={(e) => {
                      const session = activeSessions.find(s => s.id === e.target.value);
                      setSelectedSession(session);
                      setTimeRemaining(null);
                    }}
                    className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent text-gray-900 bg-white"
                  >
                    <option value="">-- Select a session --</option>
                    {activeSessions.map(session => (
                      <option key={session.id} value={session.id}>
                        {session.classes?.courses?.course_code} - {session.classes?.class_name} ({session.start_time} - {session.end_time})
                      </option>
                    ))}
                  </select>
                </div>

                {/* Session Details Card */}
                {selectedSession && (
                  <div className="bg-white rounded-xl shadow-md border-2 border-green-100 p-6">
                    <div className="space-y-4">
                      {/* Course Info */}
                      <div>
                        <h3 className="text-xl font-bold text-gray-900">
                          {selectedSession.classes?.courses?.course_code}
                        </h3>
                        <p className="text-gray-600 mt-1">
                          {selectedSession.classes?.courses?.course_title}
                        </p>
                      </div>

                      {/* Session Details Grid */}
                      <div className="grid grid-cols-2 gap-4 py-4 border-y border-gray-200">
                        <div className="flex items-center space-x-2">
                          <span className="text-2xl">📅</span>
                          <div>
                            <p className="text-xs text-gray-500 uppercase font-semibold">Date</p>
                            <p className="font-semibold text-gray-900">{selectedSession.session_date}</p>
                          </div>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-2xl">🕐</span>
                          <div>
                            <p className="text-xs text-gray-500 uppercase font-semibold">Time</p>
                            <p className="font-semibold text-gray-900">{selectedSession.start_time} - {selectedSession.end_time}</p>
                          </div>
                        </div>
                      </div>

                      {/* Time Remaining */}
                      {timeRemaining && (
                        <div className="bg-blue-50 rounded-lg p-3 text-center border border-blue-200">
                          <p className="text-sm text-blue-700 font-semibold">⏱️ {timeRemaining}</p>
                        </div>
                      )}

                      {/* Mark Attendance Button */}
                      <Button
                        variant="primary"
                        onClick={handleMarkAttendance}
                        disabled={marking}
                        className="w-full py-3 text-base font-semibold"
                      >
                        {marking ? '⏳ Marking...' : '✅ Mark Attendance'}
                      </Button>
                    </div>
                  </div>
                )}
              </div>
            ) : (
              <div className="py-12 text-center">
                <div className="text-5xl mb-3">📭</div>
                <p className="text-gray-600 text-lg">No active sessions available at this time</p>
                <p className="text-gray-500 text-sm mt-2">Check back later when a session is live</p>
              </div>
            )}
          </Card>
        </div>

        {/* ===== SECTION 2: ATTENDANCE SUMMARY ===== */}
        <div>
          <h2 className="text-xl font-bold text-gray-900 mb-4">📊 Attendance Overview</h2>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <StatCard
              title="Sessions Attended"
              value={attendanceSummary.totalAttended}
              icon="✅"
              color="green"
            />
            <StatCard
              title="Total Sessions"
              value={attendanceSummary.totalSessions}
              icon="📅"
              color="blue"
            />
            <StatCard
              title="Attendance Rate"
              value={`${attendanceSummary.percentage}%`}
              icon="📈"
              color={attendanceSummary.percentage >= 75 ? 'green' : 'red'}
            />
            <div className="bg-white rounded-lg shadow p-6 border-l-4 border-blue-500">
              <p className="text-sm text-gray-600 font-semibold uppercase mb-2">Status</p>
              <p className="text-2xl font-bold">
                {attendanceSummary.percentage >= 75 ? (
                  <span className="text-green-600">✅ On Track</span>
                ) : (
                  <span className="text-red-600">⚠️ At Risk</span>
                )}
              </p>
            </div>
          </div>
        </div>

        {/* ===== SECTION 3: COURSE-WISE ATTENDANCE ===== */}
        <Card title="📚 Course Attendance Details">
          {attendanceLoading ? (
            <div className="text-center py-8">
              <p className="text-gray-600">Loading attendance details...</p>
            </div>
          ) : courseAttendance.length > 0 ? (
            <div className="space-y-4">
              {courseAttendance.map((course) => (
                <div key={course.id} className="bg-white border border-gray-200 rounded-lg p-5 hover:shadow-md transition-shadow">
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex-1">
                      <h3 className="font-bold text-gray-900">
                        {course.code} - {course.title}
                      </h3>
                      <p className="text-sm text-gray-500 mt-1">
                        {course.attendedSessions} of {course.totalSessions} sessions attended
                      </p>
                    </div>
                    <Badge
                      label={course.eligible ? 'ELIGIBLE' : 'AT RISK'}
                      variant={course.eligible ? 'success' : 'danger'}
                    />
                  </div>

                  {/* Progress Bar */}
                  <div className="mb-4">
                    <div className="flex justify-between items-center mb-2">
                      <span className="text-sm font-semibold text-gray-700">Attendance: {course.percentage}%</span>
                      <span className="text-sm text-gray-500">{course.eligible ? '✅ 75%+' : '⚠️ Below 75%'}</span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2.5">
                      <div
                        className={`h-2.5 rounded-full transition-all ${course.eligible ? 'bg-green-500' : 'bg-red-500'}`}
                        style={{ width: `${Math.min(course.percentage, 100)}%` }}
                      ></div>
                    </div>
                  </div>

                  {/* View History Button */}
                  <Button
                    variant="secondary"
                    size="sm"
                    onClick={() => navigate(`/student/history?course=${course.id}`)}
                  >
                    📋 View Full History
                  </Button>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8">
              <p className="text-gray-600">No attendance records yet</p>
            </div>
          )}
        </Card>

        {/* ===== SECTION 4: MY COURSES ===== */}
        <Card title="📖 My Courses">
          {coursesLoading ? (
            <div className="text-center py-8">
              <p className="text-gray-600">Loading courses...</p>
            </div>
          ) : enrolledCourses.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {enrolledCourses.map((course) => (
                <div key={course.id} className="bg-gradient-to-br from-blue-50 to-indigo-50 rounded-lg p-5 border border-blue-200">
                  <h3 className="font-bold text-gray-900 mb-2">
                    {course.course_code}
                  </h3>
                  <p className="text-sm text-gray-700 mb-3">
                    {course.course_title}
                  </p>
                  <div className="space-y-1 text-sm text-gray-600">
                    {course.program && <p>📍 {course.program}</p>}
                    {course.year_level && <p>📊 Level: {course.year_level}</p>}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8">
              <p className="text-gray-600">No courses enrolled yet</p>
            </div>
          )}
        </Card>

        {/* ===== QUICK ACTIONS ===== */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <Button
            variant="secondary"
            onClick={() => navigate('/student/history')}
            className="py-3 text-base"
          >
            📋 Attendance History
          </Button>
          <Button
            variant="secondary"
            onClick={() => navigate('/student/courses')}
            className="py-3 text-base"
          >
            📚 All Courses
          </Button>
          <Button
            variant="secondary"
            onClick={() => navigate('/student/profile')}
            className="py-3 text-base"
          >
            👤 My Profile
          </Button>
        </div>
      </div>
    </Layout>
  );
};

export default StudentDashboard;
