import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Button from '../components/Button';
import Badge from '../components/Badge';
import Alert from '../components/Alert';

const StudentDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  
  // State for attendance marking
  const [activeSessions, setActiveSessions] = useState([]);
  const [sessionsLoading, setSessionsLoading] = useState(true);
  const [selectedSession, setSelectedSession] = useState(null);
  const [marking, setMarking] = useState(false);
  
  // State for attendance records
  const [courseAttendance, setCourseAttendance] = useState([]);
  const [attendanceLoading, setAttendanceLoading] = useState(true);
  const [showAttendanceHistory, setShowAttendanceHistory] = useState(null);
  

  
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  // Protect route - only allow students
  useEffect(() => {
    if (loading) return;

    if (!currentUser || userRole !== 'student') {
      navigate('/student');
      return;
    }

    fetchActiveSessions();
    fetchCourseAttendance();
  }, [currentUser, userRole, loading, navigate]);

  const fetchProfile = async () => {
    try {
      setProfileLoading(true);
      const { data, error } = await supabase
        .from('users')
        .select('id, name, matricule, email, role')
        .eq('id', currentUser.id)
        .single();

      if (error) throw error;
      setProfile(data);
      setEditedProfile(data);
    } catch (err) {
      console.error('Error fetching profile:', err);
      setError('Failed to load profile');
    } finally {
      setProfileLoading(false);
    }
  };

  const fetchActiveSessions = async () => {
    try {
      setSessionsLoading(true);
      const now = new Date().toISOString();

      // Get active sessions where student can mark attendance
      const { data, error } = await supabase
        .from('sessions')
        .select(`
          id,
          session_date,
          start_time,
          end_time,
          classes (
            id,
            class_name,
            courses (
              course_code,
              course_title
            )
          )
        `)
        .lte('start_time', now.split('T')[1])
        .gte('end_time', now.split('T')[1])
        .order('start_time', { ascending: false });

      if (error) throw error;
      setActiveSessions(data || []);
    } catch (err) {
      console.error('Error fetching active sessions:', err);
      setActiveSessions([]);
    } finally {
      setSessionsLoading(false);
    }
  };

  const fetchCourseAttendance = async () => {
    try {
      setAttendanceLoading(true);

      // Get all attendance records for this student
      const { data: attendanceRecords, error } = await supabase
        .from('attendance')
        .select(`
          id,
          created_at,
          sessions (
            id,
            session_date,
            start_time,
            end_time,
            classes (
              id,
              class_name,
              courses (
                id,
                course_code,
                course_title
              )
            )
          )
        `)
        .eq('student_id', currentUser.id)
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Group by course
      const courseMap = {};
      (attendanceRecords || []).forEach(record => {
        const course = record.sessions?.classes?.courses;
        const courseKey = course?.id;
        
        if (courseKey) {
          if (!courseMap[courseKey]) {
            courseMap[courseKey] = {
              id: courseKey,
              code: course.course_code,
              title: course.course_title,
              totalSessions: 0,
              attendedSessions: 0,
              records: []
            };
          }
          courseMap[courseKey].attendedSessions++;
          courseMap[courseKey].records.push({
            id: record.id,
            date: record.sessions.session_date,
            time: new Date(record.created_at).toLocaleTimeString([], { 
              hour: '2-digit', 
              minute: '2-digit' 
            })
          });
        }
      });

      // Get total sessions per course
      const { data: allSessions } = await supabase
        .from('sessions')
        .select(`
          classes (
            courses (
              id
            )
          )
        `);

      Object.values(courseMap).forEach(course => {
        const sessionsForCourse = (allSessions || []).filter(
          s => s.classes?.courses?.id === course.id
        ).length;
        course.totalSessions = sessionsForCourse || course.attendedSessions;
        course.percentage = course.totalSessions > 0 
          ? Math.round((course.attendedSessions / course.totalSessions) * 100)
          : 0;
        course.eligible = course.percentage >= 75;
      });

      setCourseAttendance(Object.values(courseMap));
    } catch (err) {
      console.error('Error fetching attendance:', err);
      setCourseAttendance([]);
    } finally {
      setAttendanceLoading(false);
    }
  };

  const handleMarkAttendance = async () => {
    if (!selectedSession) {
      setError('Please select a session first');
      return;
    }

    try {
      setMarking(true);
      setError(null);

      // Check current time
      const now = new Date();
      const [startHour, startMin] = selectedSession.start_time.split(':');
      const [endHour, endMin] = selectedSession.end_time.split(':');

      const startTime = new Date();
      startTime.setHours(parseInt(startHour), parseInt(startMin), 0);

      const endTime = new Date();
      endTime.setHours(parseInt(endHour), parseInt(endMin), 59);

      if (now < startTime || now > endTime) {
        setError('âŒ You can only mark attendance during the session time window');
        return;
      }

      // Check if already marked
      const { data: existing } = await supabase
        .from('attendance')
        .select('id')
        .eq('session_id', selectedSession.id)
        .eq('student_id', currentUser.id)
        .single();

      if (existing) {
        setError('âŒ You have already marked attendance for this session');
        return;
      }

      // Mark attendance
      const { error: insertError } = await supabase
        .from('attendance')
        .insert({
          session_id: selectedSession.id,
          student_id: currentUser.id,
          created_at: new Date().toISOString()
        });

      if (insertError) throw insertError;

      setSuccess('âœ… Attendance marked successfully!');
      setSelectedSession(null);
      fetchActiveSessions();
      fetchCourseAttendance();

      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error marking attendance:', err);
      setError(`âŒ Error: ${err.message}`);
    } finally {
      setMarking(false);
    }
  };

  const handleSaveProfile = async () => {
    if (!editedProfile.name || !editedProfile.name.trim()) {
      setError('Name cannot be empty');
      return;
    }

    try {
      setSavingProfile(true);
      setError(null);

      const { error } = await supabase
        .from('users')
        .update({
          name: editedProfile.name
        })
        .eq('id', currentUser.id);

      if (error) throw error;

      setProfile(editedProfile);
      setEditingProfile(false);
      setSuccess('âœ… Profile updated successfully!');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error updating profile:', err);
      setError(`âŒ Error: ${err.message}`);
    } finally {
      setSavingProfile(false);
    }
  };

  const handleChangePassword = async () => {
    if (!passwordForm.currentPassword || !passwordForm.newPassword || !passwordForm.confirmPassword) {
      setError('All password fields are required');
      return;
    }

    if (passwordForm.newPassword.length < 8) {
      setError('New password must be at least 8 characters');
      return;
    }

    if (passwordForm.newPassword !== passwordForm.confirmPassword) {
      setError('New password and confirmation do not match');
      return;
    }

    try {
      setChangingPassword(true);
      setError(null);

      const { error } = await supabase.auth.updateUser({
        password: passwordForm.newPassword
      });

      if (error) throw error;

      setSuccess('âœ… Password changed successfully!');
      setShowPasswordForm(false);
      setPasswordForm({ currentPassword: '', newPassword: '', confirmPassword: '' });
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error changing password:', err);
      setError(`âŒ Error: ${err.message}`);
    } finally {
      setChangingPassword(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-blue-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-16 w-16 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600 font-medium">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!currentUser || userRole !== 'student') {
    return null;
  }

  return (
    <Layout role="student">
      <div className="space-y-8">
        <div>
          <h1 className="text-3xl font-bold mb-2">Student Dashboard</h1>
          <p className="text-gray-600">Mark your attendance, view records, and manage your profile</p>
        </div>

        {error && (
          <Alert type="error" title="Error" message={error} />
        )}

        {success && (
          <Alert type="success" title="Success" message={success} />
        )}

        {/* Mark Attendance */}
        <Card title="âœ… Mark Attendance">
          {sessionsLoading ? (
            <p className="text-gray-600">Loading active sessions...</p>
          ) : activeSessions.length > 0 ? (
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Select Active Session
                </label>
                <select
                  value={selectedSession?.id || ''}
                  onChange={(e) => {
                    const session = activeSessions.find(s => s.id === e.target.value);
                    setSelectedSession(session);
                  }}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">-- Select a session --</option>
                  {activeSessions.map(session => (
                    <option key={session.id} value={session.id}>
                      {session.classes?.courses?.course_code} - {session.classes?.class_name}
                      ({session.start_time} - {session.end_time})
                    </option>
                  ))}
                </select>
              </div>

              {selectedSession && (
                <div className="bg-blue-50 p-4 rounded-lg border border-blue-200">
                  <h3 className="font-semibold text-gray-900">
                    {selectedSession.classes?.courses?.course_code} - {selectedSession.classes?.courses?.course_title}
                  </h3>
                  <p className="text-sm text-gray-600 mt-2">
                    ðŸ“… {selectedSession.session_date}
                  </p>
                  <p className="text-sm text-gray-600">
                    ðŸ• {selectedSession.start_time} - {selectedSession.end_time}
                  </p>
                  <Button
                    variant="primary"
                    onClick={handleMarkAttendance}
                    disabled={marking}
                    className="mt-4"
                  >
                    {marking ? 'â³ Marking...' : 'âœ… Mark Present'}
                  </Button>
                </div>
              )}
            </div>
          ) : (
            <p className="text-gray-600">No active sessions available at this time</p>
          )}
        </Card>

        {/* Attendance Records */}
        <Card title="ðŸ“Š Attendance Records">
          {attendanceLoading ? (
            <p className="text-gray-600">Loading attendance records...</p>
          ) : courseAttendance.length > 0 ? (
            <div className="space-y-4">
              {courseAttendance.map((course) => (
                <div key={course.id} className="p-4 border border-gray-200 rounded-lg">
                  <div className="flex justify-between items-start mb-3">
                    <div className="flex-1">
                      <h3 className="font-semibold text-gray-900">
                        {course.code} - {course.title}
                      </h3>
                      <div className="mt-2 grid grid-cols-3 gap-4">
                        <div>
                          <p className="text-xs text-gray-600">Attendance</p>
                          <p className="text-lg font-bold text-blue-600">
                            {course.attendedSessions}/{course.totalSessions}
                          </p>
                        </div>
                        <div>
                          <p className="text-xs text-gray-600">Percentage</p>
                          <p className="text-lg font-bold text-blue-600">{course.percentage}%</p>
                        </div>
                        <div>
                          <p className="text-xs text-gray-600">Status</p>
                          <Badge
                            label={course.eligible ? 'ELIGIBLE' : 'NOT ELIGIBLE'}
                            variant={course.eligible ? 'success' : 'danger'}
                          />
                        </div>
                      </div>
                    </div>
                  </div>

                  <Button
                    variant="secondary"
                    size="sm"
                    onClick={() => setShowAttendanceHistory(
                      showAttendanceHistory === course.id ? null : course.id
                    )}
                  >
                    {showAttendanceHistory === course.id ? 'â–¼ Hide History' : 'â–¶ View History'}
                  </Button>

                  {showAttendanceHistory === course.id && (
                    <div className="mt-4 border-t pt-4">
                      {course.records.length > 0 ? (
                        <div className="space-y-2">
                          {course.records.map((record) => (
                            <div key={record.id} className="flex justify-between text-sm bg-gray-50 p-2 rounded">
                              <span className="text-gray-700">ðŸ“… {record.date}</span>
                              <span className="text-gray-600">ðŸ• {record.time}</span>
                              <Badge label="PRESENT" variant="success" />
                            </div>
                          ))}
                        </div>
                      ) : (
                        <p className="text-gray-600">No attendance records</p>
                      )}
                    </div>
                  )}
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600">No attendance records yet</p>
          )}
        </Card>

        {/* Profile Management */}
        <Card title="ðŸ‘¤ Profile Management">
          {profileLoading ? (
            <p className="text-gray-600">Loading profile...</p>
          ) : profile ? (
            <div className="space-y-4">
              {!editingProfile && !showPasswordForm && (
                <div className="space-y-3">
                  <div>
                    <p className="text-xs text-gray-600">Full Name</p>
                    <p className="text-lg font-semibold text-gray-900">{profile.name}</p>
                  </div>
                  <div>
                    <p className="text-xs text-gray-600">Matricule Number</p>
                    <p className="text-lg font-semibold text-gray-900">{profile.matricule}</p>
                  </div>
                  <div>
                    <p className="text-xs text-gray-600">Email</p>
                    <p className="text-lg font-semibold text-gray-900">{profile.email}</p>
                  </div>

                  <div className="flex gap-2 pt-4">
                    <Button
                      variant="primary"
                      size="sm"
                      onClick={() => setEditingProfile(true)}
                    >
                      âœï¸ Edit Profile
                    </Button>
                    <Button
                      variant="secondary"
                      size="sm"
                      onClick={() => setShowPasswordForm(true)}
                    >
                      ðŸ”‘ Change Password
                    </Button>
                  </div>
                </div>
              )}

              {editingProfile && (
                <div className="space-y-4 bg-gray-50 p-4 rounded-lg">
                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                      Full Name
                    </label>
                    <input
                      type="text"
                      value={editedProfile.name || ''}
                      onChange={(e) => setEditedProfile(prev => ({ ...prev, name: e.target.value }))}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div className="flex gap-2">
                    <Button
                      variant="primary"
                      size="sm"
                      onClick={handleSaveProfile}
                      disabled={savingProfile}
                    >
                      {savingProfile ? 'ðŸ’¾ Saving...' : 'ðŸ’¾ Save Changes'}
                    </Button>
                    <Button
                      variant="secondary"
                      size="sm"
                      onClick={() => {
                        setEditingProfile(false);
                        setEditedProfile(profile);
                      }}
                    >
                      âœ• Cancel
                    </Button>
                  </div>
                </div>
              )}

              {showPasswordForm && (
                <div className="space-y-4 bg-gray-50 p-4 rounded-lg">
                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                      Current Password
                    </label>
                    <input
                      type="password"
                      value={passwordForm.currentPassword}
                      onChange={(e) => setPasswordForm(prev => ({ ...prev, currentPassword: e.target.value }))}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                      New Password (min 8 characters)
                    </label>
                    <input
                      type="password"
                      value={passwordForm.newPassword}
                      onChange={(e) => setPasswordForm(prev => ({ ...prev, newPassword: e.target.value }))}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                      Confirm Password
                    </label>
                    <input
                      type="password"
                      value={passwordForm.confirmPassword}
                      onChange={(e) => setPasswordForm(prev => ({ ...prev, confirmPassword: e.target.value }))}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div className="flex gap-2">
                    <Button
                      variant="primary"
                      size="sm"
                      onClick={handleChangePassword}
                      disabled={changingPassword}
                    >
                      {changingPassword ? 'â³ Updating...' : 'âœ… Update Password'}
                    </Button>
                    <Button
                      variant="secondary"
                      size="sm"
                      onClick={() => {
                        setShowPasswordForm(false);
                        setPasswordForm({ currentPassword: '', newPassword: '', confirmPassword: '' });
                      }}
                    >
                      âœ• Cancel
                    </Button>
                  </div>
                </div>
              )}
            </div>
          ) : (
            <p className="text-gray-600">Could not load profile</p>
          )}
        </Card>
      </div>
    </Layout>
  );
};

export default StudentDashboard;
