import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Button from '../components/Button';
import Badge from '../components/Badge';
import Table from '../components/Table';
import StatCard from '../components/StatCard';

const DelegateDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (!loading) {
      if (!currentUser || (userRole && userRole !== 'delegate')) {
        navigate('/delegate');
      }
    }
  }, [userRole, loading, currentUser, navigate]);

  const [assignedClass] = useState('Advanced Web Development');
  const [todaysCourses] = useState([
    { id: 1, name: 'Advanced Web Dev', time: '09:00-11:00' },
    { id: 2, name: 'Database Design', time: '14:00-16:00' },
  ]);

  const [stats] = useState({
    activeSession: 'Advanced Web Dev',
    presentCount: 28,
    totalCount: 35,
  });

  const [recentSessions] = useState([
    { id: 1, course: 'Advanced Web Dev', date: '2025-12-18', time: '09:00', students: 28, status: 'closed' },
    { id: 2, course: 'Database Design', date: '2025-12-17', time: '14:00', students: 32, status: 'closed' },
    { id: 3, course: 'Python Programming', date: '2025-12-17', time: '11:00', students: 30, status: 'closed' },
  ]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-green-600 mb-4" />
          <p className="text-gray-600">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!currentUser || userRole !== 'delegate') {
    return null;
  }

  return (
    <Layout role="delegate">
    </Layout>
  );
};

export default DelegateDashboard;
