import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
  StatCard,
  DataCard,
  SectionCard,
  StatusBadge,
  ProgressBar,
  Button,
  AlertBox,
  LoadingSpinner,
  SkeletonLoader,
  EmptyState,
  Table,
  Modal,
  TimelineItem,
} from '../components/UIComponents';
import {
  studentFetchEnrolledCourses,
  studentFetchAttendanceSummary,
  studentFetchCourseAttendance,
  studentFetchAlerts,
  studentFetchAttendanceHistory,
} from '../utils/dashboardQueries';
import {
  SESSION_STATUS,
  ATTENDANCE_STATUS,
  markAttendanceWithStatus,
} from '../utils/sessionLifecycle';
import { useSessionLifecycle, useSessionCountdown } from '../hooks/useSessionLifecycle';
import Sidebar from '../components/Sidebar';
import AdminTopBar from '../components/AdminTopBar';

/**
 * StudentDashboard - Friendly, reassuring interface for students
 * Focus: Attendance progress tracking, course overview, session participation
 * Aesthetic: Green accent, encouraging messages, progress visualization, achievements
 * 
 * Session Lifecycle Integration:
 * - Sessions automatically appear when created (Scheduled)
 * - Sessions become active at start time (Open)
 * - Sessions auto-close at end time (Closed)
 * - Attendance can only be marked during Open status
 * - Late attendance is tracked (first 10 minutes)
 */
const StudentDashboard = () => {
  const navigate = useNavigate();
  const { currentUser, userRole } = useAuth();

  // State management
  const [courses, setCourses] = useState([]);
  const [attendanceSummary, setAttendanceSummary] = useState(null);
  const [courseAttendance, setCourseAttendance] = useState([]);
  const [attendanceHistory, setAttendanceHistory] = useState([]);
  const [courseStats, setCourseStats] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');
  const [markingAttendance, setMarkingAttendance] = useState(false);
  const [successMessage, setSuccessMessage] = useState(null);

  // Modal states
  const [showSessionModal, setShowSessionModal] = useState(false);
  const [selectedSession, setSelectedSession] = useState(null);

  // Use session lifecycle hook for real-time session management
  const {
    sessions,
    activeSessions,
    upcomingSessions,
    pastSessions,
    sessionsNeedingAttention,
    loading: sessionsLoading,
    error: sessionsError,
    refresh: refreshSessions,
    markAttendance: markSessionAttendance,
    getSessionTimeRemaining
  } = useSessionLifecycle(currentUser?.id, 'student');

  // Fetch data on component mount
  useEffect(() => {
    if (userRole !== 'student') {
      navigate('/admin/dashboard');
      return;
    }
    fetchStudentData();
  }, [userRole, navigate]);

  // Auto-refresh every 30 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      fetchStudentData();
    }, 30000);
    return () => clearInterval(interval);
  }, []);

  // Clear success message after 5 seconds
  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => setSuccessMessage(null), 5000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const fetchStudentData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Fetch all data in parallel
      const [
        coursesData,
        summaryData,
        attendanceData,
        alertsData,
        historyData,
      ] = await Promise.all([
        studentFetchEnrolledCourses(currentUser.id),
        studentFetchAttendanceSummary(currentUser.id),
        studentFetchCourseAttendance(currentUser.id),
        studentFetchAlerts(currentUser.id),
        studentFetchAttendanceHistory(currentUser.id),
      ]);

      setCourses(coursesData.courses || []);
      setAttendanceSummary(summaryData.summary || null);
      setCourseAttendance(attendanceData.courseAttendance || []);
      setAlerts(alertsData.alerts || []);
      setAttendanceHistory(historyData.history || []);
      setCourseStats(historyData.courseStats || []);
    } catch (err) {
      console.error('Error fetching student data:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleMarkAttendance = async (sessionId) => {
    try {
      setMarkingAttendance(true);
      setError(null);

      const result = await markSessionAttendance(sessionId);

      if (!result.success) {
        setError(result.error);
        return;
      }

      // Show success message
      setSuccessMessage(result.message);
      setShowSessionModal(false);
      setSelectedSession(null);

      // Refresh data
      await fetchStudentData();
      refreshSessions();
    } catch (err) {
      setError('Failed to mark attendance: ' + err.message);
    } finally {
      setMarkingAttendance(false);
    }
  };

  const handleSessionClick = (session) => {
    setSelectedSession(session);
    setShowSessionModal(true);
  };

  const getAttendanceStatus = (percentage) => {
    if (percentage >= 80) return 'excellent';
    if (percentage >= 70) return 'good';
    if (percentage >= 60) return 'fair';
    return 'low';
  };

  const getMotivationalMessage = () => {
    if (!attendanceSummary) return '';
    const percentage = attendanceSummary.attendancePercentage || 0;

    if (percentage >= 95) return 'ðŸŒŸ Perfect attendance! Keep it up!';
    if (percentage >= 80) return 'âœ¨ Great job! You\'re doing well!';
    if (percentage >= 70) return 'ðŸ‘ Good progress! Stay focused!';
    if (percentage >= 60) return 'ðŸ’ª You can do better! Keep attending!';
    return 'âš ï¸ Attendance needs attention. Join more sessions!';
  };

  const getStatusBadgeVariant = (status) => {
    switch (status) {
      case SESSION_STATUS.SCHEDULED:
        return 'upcoming';
      case SESSION_STATUS.OPEN:
        return 'open';
      case SESSION_STATUS.CLOSED:
        return 'closed';
      case ATTENDANCE_STATUS.PRESENT:
        return 'good';
      case ATTENDANCE_STATUS.LATE:
        return 'fair';
      case ATTENDANCE_STATUS.ABSENT:
        return 'low';
      default:
        return 'default';
    }
  };

  const isAlertResolved = (alert) => alert.resolved_at !== null;

  if (!currentUser) return <LoadingSpinner />;

  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-50 to-gray-100 dark:from-slate-900 dark:to-slate-800">
      <Sidebar role={userRole} />

      <div className="flex-1 flex flex-col overflow-hidden">
        <AdminTopBar title="My Attendance" userRole={userRole} />

        {/* Main Content */}
        <main className="flex-1 overflow-auto">
          <div className="p-4 sm:p-6 lg:p-8">
            {/* Success Message */}
            {successMessage && (
              <div className="mb-6">
                <AlertBox type="success" message={successMessage} />
              </div>
            )}

            {/* Error Alert */}
            {(error || sessionsError) && (
              <div className="mb-6">
                <AlertBox type="error" message={error || sessionsError} />
              </div>
            )}

            {/* Sessions Needing Attention Banner */}
            {sessionsNeedingAttention.length > 0 && (
              <div className="mb-6 p-4 bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 rounded-xl">
                <div className="flex items-center gap-3">
                  <span className="material-symbols-outlined text-green-600 dark:text-green-400 text-2xl">
                    notification_important
                  </span>
                  <div>
                    <p className="font-semibold text-green-900 dark:text-green-300">
                      {sessionsNeedingAttention.length} Active Session{sessionsNeedingAttention.length > 1 ? 's' : ''} - Mark Your Attendance!
                    </p>
                    <p className="text-sm text-green-700 dark:text-green-400">
                      Click on a session below to mark your attendance before it closes.
                    </p>
                  </div>
                </div>
              </div>
            )}

            {/* Tab Navigation */}
            <div className="flex gap-2 mb-6 border-b border-gray-200 dark:border-slate-700 overflow-x-auto">
              <button
                onClick={() => setActiveTab('overview')}
                className={`px-4 py-3 font-medium text-sm transition-all whitespace-nowrap ${activeTab === 'overview'
                    ? 'text-green-600 dark:text-green-400 border-b-2 border-green-600 dark:border-green-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-300'
                  }`}
              >
                Overview
              </button>
              <button
                onClick={() => setActiveTab('active')}
                className={`px-4 py-3 font-medium text-sm transition-all whitespace-nowrap flex items-center gap-2 ${activeTab === 'active'
                    ? 'text-green-600 dark:text-green-400 border-b-2 border-green-600 dark:border-green-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-300'
                  }`}
              >
                Active Sessions
                {activeSessions.length > 0 && (
                  <span className="px-2 py-0.5 bg-green-500 text-white text-xs rounded-full">
                    {activeSessions.length}
                  </span>
                )}
              </button>
              <button
                onClick={() => setActiveTab('upcoming')}
                className={`px-4 py-3 font-medium text-sm transition-all whitespace-nowrap ${activeTab === 'upcoming'
                    ? 'text-green-600 dark:text-green-400 border-b-2 border-green-600 dark:border-green-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-300'
                  }`}
              >
                Upcoming ({upcomingSessions.length})
              </button>
              <button
                onClick={() => setActiveTab('courses')}
                className={`px-4 py-3 font-medium text-sm transition-all whitespace-nowrap ${activeTab === 'courses'
                    ? 'text-green-600 dark:text-green-400 border-b-2 border-green-600 dark:border-green-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-300'
                  }`}
              >
                My Courses
              </button>
              <button
                onClick={() => setActiveTab('history')}
                className={`px-4 py-3 font-medium text-sm transition-all whitespace-nowrap ${activeTab === 'history'
                    ? 'text-green-600 dark:text-green-400 border-b-2 border-green-600 dark:border-green-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-300'
                  }`}
              >
                Attendance History
              </button>
            </div>

            {/* Overview Tab */}
            {activeTab === 'overview' && (
              <div className="space-y-6">
                {/* Motivational Banner */}
                <div className="p-6 bg-gradient-to-r from-green-50 to-emerald-50 dark:from-green-900/20 dark:to-emerald-900/20 rounded-xl border border-green-200 dark:border-green-800">
                  <p className="text-lg font-semibold text-green-900 dark:text-green-300">
                    {getMotivationalMessage()}
                  </p>
                </div>

                {/* Attendance Summary Card */}
                {loading ? (
                  <SkeletonLoader height="150px" />
                ) : attendanceSummary ? (
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <DataCard
                      title="Overall Attendance"
                      stats={[
                        {
                          label: 'Percentage',
                          value: `${attendanceSummary.attendancePercentage}%`,
                        },
                      ]}
                    >
                      <div className="mt-4">
                        <ProgressBar
                          percentage={attendanceSummary.attendancePercentage}
                          status={getAttendanceStatus(
                            attendanceSummary.attendancePercentage
                          )}
                          showLabel={true}
                        />
                      </div>
                    </DataCard>

                    <DataCard
                      title="Session Stats"
                      stats={[
                        {
                          label: 'Attended',
                          value: attendanceSummary.totalAttended || 0,
                        },
                        {
                          label: 'Total',
                          value: attendanceSummary.totalSessions || 0,
                        },
                      ]}
                    >
                      <div className="mt-4 text-sm text-gray-600 dark:text-gray-400">
                        {activeSessions.length > 0 && (
                          <span className="text-green-600 dark:text-green-400 font-medium">
                            {activeSessions.length} session(s) active now!
                          </span>
                        )}
                      </div>
                    </DataCard>

                    <DataCard
                      title="Enrolled Courses"
                      stats={[
                        {
                          label: 'Courses',
                          value: courses.length,
                        },
                      ]}
                    >
                      <div className="mt-4 text-sm text-gray-600 dark:text-gray-400">
                        Active in {Math.max(1, courses.length)} course(s)
                      </div>
                    </DataCard>
                  </div>
                ) : null}

                {/* Active Sessions for Today */}
                <SectionCard title="Sessions Available Now" icon="event_available">
                  {sessionsLoading ? (
                    <SkeletonLoader height="200px" />
                  ) : activeSessions.length > 0 ? (
                    <div className="space-y-3">
                      {activeSessions.map((session) => (
                        <SessionCard
                          key={session.id}
                          session={session}
                          onMarkAttendance={() => handleSessionClick(session)}
                          getTimeRemaining={getSessionTimeRemaining}
                        />
                      ))}
                    </div>
                  ) : (
                    <EmptyState
                      title="No Active Sessions"
                      message="There are no sessions happening right now. Check back later!"
                      icon="schedule"
                    />
                  )}
                </SectionCard>

                {/* Upcoming Sessions Preview */}
                {upcomingSessions.length > 0 && (
                  <SectionCard title="Upcoming Sessions" icon="upcoming">
                    <div className="space-y-3">
                      {upcomingSessions.slice(0, 3).map((session) => (
                        <div
                          key={session.id}
                          className="flex items-center justify-between p-4 bg-gray-50 dark:bg-slate-800 rounded-lg border border-gray-200 dark:border-slate-700"
                        >
                          <div className="flex-1">
                            <p className="font-semibold text-gray-900 dark:text-white">
                              {session.courseTitle}
                            </p>
                            <p className="text-sm text-gray-600 dark:text-gray-400">
                              {session.className} â€¢ {session.date} at {session.startTime}
                            </p>
                          </div>
                          <StatusBadge status="upcoming" />
                        </div>
                      ))}
                      {upcomingSessions.length > 3 && (
                        <button
                          onClick={() => setActiveTab('upcoming')}
                          className="w-full py-2 text-sm text-green-600 dark:text-green-400 hover:underline"
                        >
                          View all {upcomingSessions.length} upcoming sessions â†’
                        </button>
                      )}
                    </div>
                  </SectionCard>
                )}

                {/* Alerts & Notifications */}
                {alerts.length > 0 && (
                  <SectionCard title="Important Notices" icon="notifications">
                    <div className="space-y-2">
                      {alerts.slice(0, 5).map((alert, index) => (
                        <div
                          key={alert.id || index}
                          className={`p-3 rounded-lg border ${isAlertResolved(alert)
                              ? 'bg-gray-50 dark:bg-slate-800 border-gray-200 dark:border-slate-700'
                              : alert.severity === 'critical'
                                ? 'bg-red-50 dark:bg-red-900/20 border-red-200 dark:border-red-800'
                                : 'bg-yellow-50 dark:bg-yellow-900/20 border-yellow-200 dark:border-yellow-800'
                            }`}
                        >
                          <p className="text-sm text-gray-900 dark:text-white">
                            {alert.message}
                          </p>
                        </div>
                      ))}
                    </div>
                  </SectionCard>
                )}

                {/* Quick Stats */}
                <SectionCard title="Attendance Summary by Course" icon="bar_chart">
                  {loading ? (
                    <SkeletonLoader height="200px" />
                  ) : courseAttendance.length > 0 ? (
                    <div className="space-y-4">
                      {courseAttendance.map((course) => (
                        <div key={course.courseId} className="pb-4 border-b border-gray-200 dark:border-slate-700 last:border-0">
                          <div className="flex items-center justify-between mb-2">
                            <div className="flex-1">
                              <p className="font-medium text-gray-900 dark:text-white">
                                {course.courseTitle || course.courseName}
                              </p>
                              <p className="text-xs text-gray-500 dark:text-gray-400">
                                {course.attended}/{course.totalSessions} sessions attended
                              </p>
                            </div>
                            <StatusBadge
                              status={getAttendanceStatus(course.attendancePercentage || course.percentage)}
                            />
                          </div>
                          <ProgressBar
                            percentage={course.attendancePercentage || course.percentage}
                            status={getAttendanceStatus(course.attendancePercentage || course.percentage)}
                            label={`${course.attendancePercentage || course.percentage}% attendance`}
                          />
                        </div>
                      ))}
                    </div>
                  ) : (
                    <EmptyState
                      title="No Courses Yet"
                      message="You haven't enrolled in any courses yet"
                      icon="school"
                    />
                  )}
                </SectionCard>
              </div>
            )}

            {/* Active Sessions Tab */}
            {activeTab === 'active' && (
              <div className="space-y-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                  Active Sessions - Mark Your Attendance
                </h3>

                {sessionsLoading ? (
                  <SkeletonLoader height="300px" />
                ) : activeSessions.length > 0 ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {activeSessions.map((session) => (
                      <SessionCard
                        key={session.id}
                        session={session}
                        onMarkAttendance={() => handleSessionClick(session)}
                        getTimeRemaining={getSessionTimeRemaining}
                        expanded
                      />
                    ))}
                  </div>
                ) : (
                  <EmptyState
                    title="No Active Sessions"
                    message="There are no sessions happening right now. Check the Upcoming tab for scheduled sessions."
                    icon="event_busy"
                  />
                )}
              </div>
            )}

            {/* Upcoming Sessions Tab */}
            {activeTab === 'upcoming' && (
              <div className="space-y-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                  Upcoming Sessions
                </h3>

                {sessionsLoading ? (
                  <SkeletonLoader height="300px" />
                ) : upcomingSessions.length > 0 ? (
                  <div className="space-y-3">
                    {upcomingSessions.map((session) => (
                      <div
                        key={session.id}
                        className="flex items-center justify-between p-4 bg-white dark:bg-slate-800 rounded-lg border border-gray-200 dark:border-slate-700 shadow-sm"
                      >
                        <div className="flex-1">
                          <p className="font-semibold text-gray-900 dark:text-white">
                            {session.courseTitle}
                          </p>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            {session.className}
                          </p>
                          <p className="text-xs text-gray-500 dark:text-gray-500 mt-1">
                            {session.date} â€¢ {session.startTime} - {session.endTime}
                          </p>
                        </div>
                        <div className="text-right">
                          <StatusBadge status="upcoming" />
                          <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
                            {getSessionTimeRemaining(session)?.formatted}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <EmptyState
                    title="No Upcoming Sessions"
                    message="No sessions are scheduled. Check back later!"
                    icon="event"
                  />
                )}
              </div>
            )}

            {/* Courses Tab */}
            {activeTab === 'courses' && (
              <div className="space-y-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                  My Enrolled Courses
                </h3>

                {loading ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {[1, 2, 3, 4].map((i) => (
                      <SkeletonLoader key={i} height="200px" />
                    ))}
                  </div>
                ) : courses.length > 0 ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {courses.map((course) => {
                      const stats = courseAttendance.find(
                        (c) => c.courseId === course.id
                      );
                      return (
                        <DataCard
                          key={course.id}
                          title={course.courseTitle}
                          subtitle={course.courseCode}
                          stats={[
                            {
                              label: 'Attendance',
                              value: `${stats?.attendancePercentage || 0}%`,
                            },
                            {
                              label: 'Sessions',
                              value: stats?.totalSessions || 0,
                            },
                          ]}
                        >
                          <div className="mt-4">
                            <ProgressBar
                              percentage={stats?.attendancePercentage || 0}
                              status={getAttendanceStatus(
                                stats?.attendancePercentage || 0
                              )}
                            />
                            <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
                              {stats?.attended || 0} attended /{' '}
                              {stats?.totalSessions || 0} total
                            </p>
                          </div>
                        </DataCard>
                      );
                    })}
                  </div>
                ) : (
                  <EmptyState
                    title="No Courses Enrolled"
                    message="You haven't enrolled in any courses yet. Contact your administrator to enroll."
                    icon="school"
                  />
                )}
              </div>
            )}

            {/* Attendance History Tab */}
            {activeTab === 'history' && (
              <div className="space-y-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                  Attendance History
                </h3>

                {/* Course Statistics Summary */}
                {courseStats.length > 0 && (
                  <SectionCard title="Summary by Course" icon="summarize">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {courseStats.map((stat) => (
                        <div
                          key={stat.courseCode}
                          className="p-4 bg-green-50 dark:bg-green-900/20 rounded-lg border border-green-200 dark:border-green-800"
                        >
                          <div className="flex items-start justify-between mb-2">
                            <div>
                              <p className="font-semibold text-gray-900 dark:text-white">
                                {stat.courseName}
                              </p>
                              <p className="text-xs text-gray-600 dark:text-gray-400">
                                {stat.courseCode}
                              </p>
                            </div>
                            <StatusBadge
                              status={getAttendanceStatus(stat.percentage)}
                            />
                          </div>
                          <div className="space-y-2">
                            <ProgressBar
                              percentage={stat.percentage}
                              status={getAttendanceStatus(stat.percentage)}
                              label={`${stat.percentage}% attendance`}
                            />
                            <div className="grid grid-cols-3 gap-2 text-xs">
                              <div className="text-center">
                                <p className="text-gray-600 dark:text-gray-400">Total</p>
                                <p className="font-semibold text-gray-900 dark:text-white">
                                  {stat.totalSessions}
                                </p>
                              </div>
                              <div className="text-center">
                                <p className="text-gray-600 dark:text-gray-400">Attended</p>
                                <p className="font-semibold text-green-600 dark:text-green-400">
                                  {stat.attended}
                                </p>
                              </div>
                              <div className="text-center">
                                <p className="text-gray-600 dark:text-gray-400">Missed</p>
                                <p className="font-semibold text-red-600 dark:text-red-400">
                                  {stat.totalSessions - stat.attended}
                                </p>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </SectionCard>
                )}

                {/* Past Sessions (Closed) */}
                <SectionCard title="Past Sessions" icon="history">
                  {sessionsLoading ? (
                    <SkeletonLoader height="300px" />
                  ) : pastSessions.length > 0 ? (
                    <div className="space-y-2">
                      {pastSessions.slice(0, 20).map((session) => (
                        <div
                          key={session.id}
                          className="flex items-center justify-between p-3 bg-gray-50 dark:bg-slate-800 rounded-lg"
                        >
                          <div className="flex-1">
                            <p className="font-medium text-gray-900 dark:text-white">
                              {session.courseTitle}
                            </p>
                            <p className="text-xs text-gray-600 dark:text-gray-400">
                              {session.className} â€¢ {session.date} â€¢ {session.startTime}
                            </p>
                          </div>
                          <StatusBadge
                            status={
                              session.isMarked
                                ? session.attendanceStatus === ATTENDANCE_STATUS.LATE
                                  ? 'fair'
                                  : 'good'
                                : 'low'
                            }
                          />
                        </div>
                      ))}
                    </div>
                  ) : (
                    <EmptyState
                      title="No Past Sessions"
                      message="Your attendance history will appear here"
                      icon="history"
                    />
                  )}
                </SectionCard>

                {/* Detailed Attendance Records */}
                {loading ? (
                  <SkeletonLoader height="300px" />
                ) : attendanceHistory.length > 0 ? (
                  <Table
                    columns={[
                      { key: 'course', label: 'Course' },
                      { key: 'class', label: 'Class' },
                      { key: 'date', label: 'Date & Time' },
                      { key: 'status', label: 'Status' },
                    ]}
                    data={attendanceHistory.map((record) => ({
                      id: record.sessionId,
                      course: (
                        <div>
                          <p className="font-medium text-gray-900 dark:text-white">
                            {record.courseName}
                          </p>
                          <p className="text-xs text-gray-600 dark:text-gray-400">
                            {record.courseCode}
                          </p>
                        </div>
                      ),
                      class: (
                        <p className="text-gray-900 dark:text-white">
                          {record.className}
                        </p>
                      ),
                      date: (
                        <div>
                          <p className="text-sm font-medium text-gray-900 dark:text-white">
                            {record.sessionDate}
                          </p>
                          <p className="text-xs text-gray-600 dark:text-gray-400">
                            {record.startTime} - {record.endTime}
                          </p>
                        </div>
                      ),
                      status: (
                        <StatusBadge
                          status={record.status === 'Present' ? 'good' : 'low'}
                        />
                      ),
                    }))}
                  />
                ) : (
                  <EmptyState
                    title="No Attendance Records"
                    message="Your attendance history will appear here as you attend sessions"
                    icon="history"
                  />
                )}
              </div>
            )}
          </div>
        </main>
      </div>

      {/* Session Details Modal */}
      {selectedSession && (
        <Modal
          isOpen={showSessionModal}
          title="Mark Attendance"
          onClose={() => {
            setShowSessionModal(false);
            setSelectedSession(null);
          }}
          onSubmit={() => handleMarkAttendance(selectedSession.id)}
          submitText={selectedSession.isMarked ? 'Already Marked' : 'Confirm Attendance'}
          submitVariant="success"
          submitDisabled={selectedSession.isMarked || markingAttendance}
        >
          <div className="space-y-6">
            {/* Session Details */}
            <div className="p-4 bg-green-50 dark:bg-green-900/20 rounded-lg border border-green-200 dark:border-green-800">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-xs text-gray-600 dark:text-gray-400">Course</p>
                  <p className="font-semibold text-gray-900 dark:text-white">
                    {selectedSession.courseTitle}
                  </p>
                </div>
                <div>
                  <p className="text-xs text-gray-600 dark:text-gray-400">Class</p>
                  <p className="font-semibold text-gray-900 dark:text-white">
                    {selectedSession.className}
                  </p>
                </div>
                <div>
                  <p className="text-xs text-gray-600 dark:text-gray-400">Date</p>
                  <p className="font-semibold text-gray-900 dark:text-white">
                    {selectedSession.date}
                  </p>
                </div>
                <div>
                  <p className="text-xs text-gray-600 dark:text-gray-400">Time</p>
                  <p className="font-semibold text-gray-900 dark:text-white">
                    {selectedSession.startTime} - {selectedSession.endTime}
                  </p>
                </div>
              </div>
            </div>

            {/* Time Remaining */}
            <div className="text-center p-4 bg-gray-50 dark:bg-slate-800 rounded-lg">
              <p className="text-sm text-gray-600 dark:text-gray-400">Time Remaining</p>
              <p className="text-2xl font-bold text-green-600 dark:text-green-400">
                {getSessionTimeRemaining(selectedSession)?.formatted || 'Calculating...'}
              </p>
            </div>

            {/* Status Message */}
            {selectedSession.isMarked ? (
              <AlertBox
                type="success"
                message={`You have already marked attendance for this session${selectedSession.attendanceStatus === ATTENDANCE_STATUS.LATE ? ' (Late)' : ''
                  }.`}
              />
            ) : (
              <AlertBox
                type="info"
                message="Click 'Confirm Attendance' below to mark yourself as present in this session."
              />
            )}

            {markingAttendance && (
              <div className="flex items-center justify-center gap-2 text-green-600 dark:text-green-400">
                <div className="animate-spin rounded-full h-5 w-5 border-2 border-green-600 border-t-transparent" />
                <span>Marking attendance...</span>
              </div>
            )}
          </div>
        </Modal>
      )}
    </div>
  );
};

/**
 * Session Card Component for displaying active sessions
 */
const SessionCard = ({ session, onMarkAttendance, getTimeRemaining, expanded = false }) => {
  const timeRemaining = getTimeRemaining(session);

  return (
    <div
      className={`flex items-center justify-between p-4 bg-green-50 dark:bg-green-900/20 rounded-lg border border-green-200 dark:border-green-800 hover:shadow-md transition-shadow ${expanded ? 'flex-col items-stretch gap-4' : ''
        }`}
    >
      <div className={`flex-1 ${expanded ? 'text-center' : ''}`}>
        <p className="font-semibold text-gray-900 dark:text-white">
          {session.courseTitle}
        </p>
        <p className="text-sm text-gray-600 dark:text-gray-400">
          {session.className} â€¢ {session.startTime} - {session.endTime}
        </p>
        {expanded && (
          <p className="text-xs text-gray-500 dark:text-gray-500 mt-1">
            {session.date}
          </p>
        )}
      </div>

      <div className={`flex items-center gap-3 ${expanded ? 'flex-col' : ''}`}>
        {timeRemaining && (
          <span className="text-sm font-medium text-green-600 dark:text-green-400">
            {timeRemaining.formatted}
          </span>
        )}

        {session.isMarked ? (
          <span className="px-4 py-2 bg-gray-200 dark:bg-gray-700 text-gray-600 dark:text-gray-400 rounded-lg text-sm font-medium">
            âœ“ Already Marked
            {session.attendanceStatus === ATTENDANCE_STATUS.LATE && ' (Late)'}
          </span>
        ) : (
          <Button
            variant="primary"
            size="sm"
            onClick={onMarkAttendance}
          >
            Mark Attendance
          </Button>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
