import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Table from '../components/Table';

const StudentCourses = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  const [courses, setCourses] = useState([]);
  const [loadingData, setLoadingData] = useState(true);

  useEffect(() => {
    if (loading) return;

    if (!currentUser || userRole !== 'student') {
      navigate('/student');
      return;
    }

    fetchCourses();
  }, [currentUser, userRole, loading, navigate]);

  const fetchCourses = async () => {
    try {
      setLoadingData(true);

      // Get student's class
      const { data: userData } = await supabase
        .from('users')
        .select('class_id')
        .eq('id', currentUser.id)
        .single();

      if (!userData?.class_id) {
        setCourses([]);
        return;
      }

      // Get class details with delegate
      const { data: classData } = await supabase
        .from('classes')
        .select(`
          id,
          class_name,
          users!classes_delegate_id_fkey (
            name
          )
        `)
        .eq('id', userData.class_id)
        .single();

      if (classData) {
        const course = {
          id: classData.id,
          name: classData.class_name,
          code: classData.class_name.replace(/\s+/g, '').toUpperCase(),
          delegate: classData.users?.name || 'Unknown'
        };
        setCourses([course]);
      } else {
        setCourses([]);
      }
    } catch (err) {
      console.error('Error fetching courses:', err);
      setCourses([]);
    } finally {
      setLoadingData(false);
    }
  };

  if (loading || loadingData) return (
    <Layout role="student">
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600">Loading courses...</p>
        </div>
      </div>
    </Layout>
  );

  if (!currentUser || userRole !== 'student') return null;

  return (
    <Layout role="student">
      <div className="space-y-8">
        <div>
          <h1 className="text-3xl font-bold mb-2">My Courses</h1>
          <p className="text-gray-600">View your enrolled courses</p>
        </div>

        {/* Courses Grid */}
        {courses.length > 0 ? (
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {courses.map((course) => (
              <Card key={course.id}>
                <div className="space-y-3">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-bold text-lg text-gray-900">{course.name}</h3>
                      <p className="text-sm text-gray-500">{course.code}</p>
                    </div>
                  </div>

                  <div className="border-t pt-3 space-y-2">
                    <p className="text-sm flex items-center">
                      <span className="mr-2">üë®‚Äçüè´</span>
                      <span className="text-gray-700">{course.delegate}</span>
                    </p>
                  </div>
                </div>
              </Card>
            ))}
          </div>
        ) : (
          <Card>
            <p className="text-gray-600">No courses enrolled</p>
          </Card>
        )}

        {/* Course Details Table */}
        {courses.length > 0 && (
          <Card title="üìö Course Details">
            <Table
              columns={[
                { key: 'code', label: 'Code' },
                { key: 'name', label: 'Course Name' },
                { key: 'delegate', label: 'Delegate' },
              ]}
              data={courses}
              searchable
              paginated
              itemsPerPage={10}
            />
          </Card>
        )}
      </div>
    </Layout>
  );
};

export default StudentCourses;
