import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Button from '../components/Button';
import Alert from '../components/Alert';

const StudentProfile = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  
  const [profileData, setProfileData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [showPasswordForm, setShowPasswordForm] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [formData, setFormData] = useState({
    name: '',
    matricule: ''
  });

  // Protect route - only allow students
  useEffect(() => {
    if (loading) return;

    if (!currentUser || userRole !== 'student') {
      navigate('/student');
      return;
    }

    fetchStudentProfile();
  }, [currentUser, userRole, loading, navigate]);

  const fetchStudentProfile = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const { data, error: fetchError } = await supabase
        .from('users')
        .select('id, name, matricule, role')
        .eq('id', currentUser.id)
        .single();

      if (fetchError) {
        console.error('Supabase error:', fetchError);
        throw fetchError;
      }

      if (!data) {
        throw new Error('No profile data found');
      }

      setProfileData(data);
      setFormData({
        name: data.name || '',
        matricule: data.matricule || ''
      });
    } catch (err) {
      console.error('Error fetching profile:', err);
      setError(err.message || 'Failed to load profile');
      setProfileData(null);
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSaveProfile = async () => {
    try {
      setError(null);

      const { error: updateError } = await supabase
        .from('users')
        .update({
          name: formData.name
        })
        .eq('id', currentUser.id);

      if (updateError) throw updateError;

      setProfileData(prev => ({
        ...prev,
        name: formData.name
      }));
      setIsEditing(false);
      setSuccess('Profile updated successfully!');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(err.message);
    }
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();
    setError(null);

    // Validate password fields
    if (!passwordData.currentPassword) {
      setError('Please enter your current password');
      return;
    }
    if (!passwordData.newPassword) {
      setError('Please enter a new password');
      return;
    }
    if (passwordData.newPassword.length < 6) {
      setError('New password must be at least 6 characters');
      return;
    }
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      setError('New password and confirmation do not match');
      return;
    }

    try {
      // Update password in Supabase Auth
      const { error: updateError } = await supabase.auth.updateUser({
        password: passwordData.newPassword
      });

      if (updateError) throw updateError;

      setSuccess('Password changed successfully!');
      setPasswordData({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      });
      setShowPasswordForm(false);
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(err.message || 'Failed to change password');
    }
  };

  if (isLoading) {
    return (
      <Layout role="student">
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '400px' }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{ marginBottom: '15px' }}>Loading profile...</div>
          </div>
        </div>
      </Layout>
    );
  }

  return (
    <Layout role="student">
      <div style={{ maxWidth: '600px', margin: '0 auto' }}>
        <h1>Student Profile</h1>

        {error && <Alert type="error" message={error} />}
        {success && <Alert type="success" message={success} />}

        {!profileData && !error && (
          <Alert type="error" message="Failed to load profile data. Please refresh the page." />
        )}

        {profileData ? (
          <div>
            {/* Personal Information Card */}
            <Card>
              <div style={{ padding: '20px' }}>
                {!isEditing ? (
                  <div>
                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Name
                    </label>
                    <p style={{ margin: 0, fontSize: '16px', fontWeight: '500' }}>
                      {profileData.name}
                    </p>
                  </div>

                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Matricule
                    </label>
                    <p style={{ margin: 0, fontSize: '16px', fontWeight: '500' }}>
                      {profileData.matricule}
                    </p>
                  </div>

                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Email
                    </label>
                    <p style={{ margin: 0, fontSize: '16px', fontWeight: '500' }}>
                      {profileData.email}
                    </p>
                  </div>

                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Role
                    </label>
                    <p style={{ margin: 0, fontSize: '16px', fontWeight: '500', textTransform: 'capitalize' }}>
                      {profileData.role}
                    </p>
                  </div>

                  <Button 
                    onClick={() => setIsEditing(true)}
                    style={{ marginTop: '20px' }}
                  >
                    Edit Profile
                  </Button>
                </div>
              ) : (
                <div>
                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Name
                    </label>
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      style={{
                        width: '100%',
                        padding: '8px',
                        border: '1px solid #ddd',
                        borderRadius: '4px',
                        fontSize: '14px',
                        boxSizing: 'border-box'
                      }}
                    />
                  </div>

                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Matricule (Read-only)
                    </label>
                    <p style={{ margin: 0, padding: '8px', backgroundColor: '#f5f5f5', borderRadius: '4px' }}>
                      {formData.matricule}
                    </p>
                  </div>

                  <div style={{ display: 'flex', gap: '10px', marginTop: '20px' }}>
                    <Button onClick={handleSaveProfile} style={{ flex: 1 }}>
                      Save Changes
                    </Button>
                    <Button 
                      onClick={() => {
                        setIsEditing(false);
                        setFormData({
                          name: profileData.name,
                          matricule: profileData.matricule
                        });
                      }}
                      style={{ flex: 1, backgroundColor: '#6c757d' }}
                    >
                      Cancel
                    </Button>
                  </div>
                  </div>
                )}
              </div>
            </Card>

            {/* Password Change Card */}
            <Card style={{ marginTop: '20px' }}>
              <div style={{ padding: '20px' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
                  <h2 style={{ margin: 0, fontSize: '18px', fontWeight: '600' }}>Change Password</h2>
                  {!showPasswordForm && (
                    <Button 
                      onClick={() => setShowPasswordForm(true)}
                      style={{ fontSize: '14px', padding: '6px 12px' }}
                    >
                      Change Password
                    </Button>
                  )}
                </div>

                {showPasswordForm && (
                  <form onSubmit={handlePasswordChange}>
                    <div style={{ marginBottom: '15px' }}>
                      <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                        Current Password
                      </label>
                      <input
                        type="password"
                        value={passwordData.currentPassword}
                        onChange={(e) => setPasswordData(prev => ({ ...prev, currentPassword: e.target.value }))}
                        style={{
                          width: '100%',
                          padding: '8px',
                          border: '1px solid #ddd',
                          borderRadius: '4px',
                          fontSize: '14px',
                          boxSizing: 'border-box'
                        }}
                        placeholder="Enter current password"
                      />
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                      <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                        New Password
                      </label>
                      <input
                        type="password"
                        value={passwordData.newPassword}
                        onChange={(e) => setPasswordData(prev => ({ ...prev, newPassword: e.target.value }))}
                        style={{
                          width: '100%',
                          padding: '8px',
                          border: '1px solid #ddd',
                          borderRadius: '4px',
                          fontSize: '14px',
                          boxSizing: 'border-box'
                        }}
                        placeholder="Enter new password (min 6 characters)"
                      />
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                      <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                        Confirm New Password
                      </label>
                      <input
                        type="password"
                        value={passwordData.confirmPassword}
                        onChange={(e) => setPasswordData(prev => ({ ...prev, confirmPassword: e.target.value }))}
                        style={{
                          width: '100%',
                          padding: '8px',
                          border: '1px solid #ddd',
                          borderRadius: '4px',
                          fontSize: '14px',
                          boxSizing: 'border-box'
                        }}
                        placeholder="Confirm new password"
                      />
                    </div>

                    <div style={{ display: 'flex', gap: '10px', marginTop: '20px' }}>
                      <Button type="submit" style={{ flex: 1 }}>
                        Update Password
                      </Button>
                      <Button 
                        type="button"
                        onClick={() => {
                          setShowPasswordForm(false);
                          setPasswordData({
                            currentPassword: '',
                            newPassword: '',
                            confirmPassword: ''
                          });
                        }}
                        style={{ flex: 1, backgroundColor: '#6c757d' }}
                      >
                        Cancel
                      </Button>
                    </div>
                  </form>
                )}
              </div>
            </Card>
          </div>
        ) : (
          <Card>
            <div style={{ padding: '20px', textAlign: 'center' }}>
              <p style={{ color: '#666' }}>Loading your profile information...</p>
            </div>
          </Card>
        )}
      </div>
    </Layout>
  );
};

export default StudentProfile;
