import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Alert from '../components/Alert';
import Badge from '../components/Badge';

const StudentMarkAttendance = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [activeSessions, setActiveSessions] = useState([]);
  const [sessionsLoading, setSessionsLoading] = useState(true);
  const [submittedAttendance, setSubmittedAttendance] = useState({});
  const [marking, setMarking] = useState({});
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    if (loading) return;

    if (!currentUser || userRole !== 'student') {
      navigate('/student');
      return;
    }

    fetchActiveSessions();

    // Refresh sessions every 10 seconds
    const interval = setInterval(fetchActiveSessions, 10000);
    return () => clearInterval(interval);
  }, [currentUser, userRole, loading, navigate]);

  const fetchActiveSessions = async () => {
    try {
      setSessionsLoading(true);
      setError(null);

      // Get student's class
      const { data: userData, error: userError } = await supabase
        .from('users')
        .select('class_id')
        .eq('id', currentUser.id)
        .single();

      if (userError) throw userError;

      if (!userData.class_id) {
        setActiveSessions([]);
        return;
      }

      // Get active sessions for student's class
      const { data: sessions, error: sessionsError } = await supabase
        .from('sessions')
        .select(`
          id,
          token,
          created_at,
          expires_at,
          class_id,
          classes (
            class_name
          )
        `)
        .eq('class_id', userData.class_id)
        .gt('expires_at', new Date().toISOString())
        .order('created_at', { ascending: false });

      if (sessionsError) throw sessionsError;

      setActiveSessions(sessions || []);

      // Check which sessions the student has already submitted for
      if (currentUser?.id && sessions && sessions.length > 0) {
        const { data: submissions, error: submissionsError } = await supabase
          .from('attendance')
          .select('session_id, status')
          .eq('student_id', currentUser.id)
          .in('session_id', sessions.map(s => s.id));

        if (!submissionsError && submissions) {
          const submitted = {};
          submissions.forEach(sub => {
            submitted[sub.session_id] = sub.status;
          });
          setSubmittedAttendance(submitted);
        }
      }
    } catch (err) {
      console.error('Error fetching sessions:', err);
      setError(err.message);
    } finally {
      setSessionsLoading(false);
    }
  };

  const determineAttendanceStatus = (sessionCreatedAt) => {
    const sessionStart = new Date(sessionCreatedAt);
    const now = new Date();
    const minutesElapsed = (now - sessionStart) / (1000 * 60);

    // If more than 15 minutes have passed since session started, mark as LATE
    return minutesElapsed > 15 ? 'late' : 'present';
  };

  const handleMarkAttendance = async (sessionId) => {
    try {
      setMarking(prev => ({ ...prev, [sessionId]: true }));
      setError(null);
      setSuccess(null);

      // Check if already submitted
      if (submittedAttendance[sessionId]) {
        setError(`You have already marked attendance for this session`);
        setMarking(prev => ({ ...prev, [sessionId]: false }));
        return;
      }

      // Find the session to determine status
      const session = activeSessions.find(s => s.id === sessionId);
      if (!session) {
        setError('Session not found');
        setMarking(prev => ({ ...prev, [sessionId]: false }));
        return;
      }

      const status = determineAttendanceStatus(session.created_at);

      // Insert attendance record
      const { error: insertError } = await supabase
        .from('attendance')
        .insert([
          {
            session_id: sessionId,
            student_id: currentUser.id,
            status: status,
            timestamp: new Date().toISOString(),
          }
        ]);

      if (insertError) throw insertError;

      // Update local state
      setSubmittedAttendance(prev => ({
        ...prev,
        [sessionId]: status
      }));

      // Show success message
      setSuccess(`‚úÖ Attendance marked as ${status.toUpperCase()}`);

      // Clear success message after 3 seconds
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error marking attendance:', err);
      setError(`‚ùå Error: ${err.message}`);
    } finally {
      setMarking(prev => ({ ...prev, [sessionId]: false }));
    }
  };

  if (loading) return <div className="p-4">Loading...</div>;
  if (!currentUser || userRole !== 'student') return null;

  return (
    <Layout role="student">
      <div className="space-y-6">
        <div>
          <h1 className="text-3xl font-bold mb-2">Mark Attendance</h1>
          <p className="text-gray-600">Mark your attendance for active sessions</p>
        </div>

        {error && (
          <Alert type="error" title="Error" message={error} />
        )}

        {success && (
          <Alert type="success" title="Success" message={success} />
        )}

        {sessionsLoading ? (
          <Card title="Loading Sessions...">
            <p className="text-gray-600">Fetching active attendance sessions...</p>
          </Card>
        ) : activeSessions.length === 0 ? (
          <Card title="No Active Sessions">
            <div className="text-center py-8">
              <p className="text-gray-600 mb-2">üì≠ No active attendance sessions for your courses at the moment</p>
              <p className="text-sm text-gray-500">Check back when your instructor starts a session</p>
            </div>
          </Card>
        ) : (
          <div className="grid grid-cols-1 gap-4">
            {activeSessions.map(session => {
              const isSubmitted = submittedAttendance[session.id];
              const isMarking = marking[session.id];
              const status = determineAttendanceStatus(session.created_at);

              return (
                <Card
                  key={session.id}
                  title={
                    <div className="flex justify-between items-start">
                      <div>
                        <p className="font-bold text-lg">{session.classes?.class_name || 'Unknown Class'}</p>
                        <p className="text-sm text-gray-600">Session ID: {session.token.substring(0, 8)}</p>
                      </div>
                      {isSubmitted && (
                        <Badge text={`Marked: ${isSubmitted}`} variant={isSubmitted === 'present' ? 'success' : 'warning'} />
                      )}
                    </div>
                  }
                >
                  <div className="space-y-3">
                    <p className="text-sm text-gray-600">
                      Started: {new Date(session.created_at).toLocaleTimeString()}
                    </p>
                    {!isSubmitted && (
                      <p className="text-sm text-blue-600">
                        Will be marked as: <strong>{status.toUpperCase()}</strong>
                      </p>
                    )}

                    <button
                      onClick={() => handleMarkAttendance(session.id)}
                      disabled={isSubmitted || isMarking}
                      className="w-full bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 text-white font-semibold py-3 px-4 rounded-lg transition"
                    >
                      {isMarking ? '‚è≥ Marking...' : `‚úÖ Mark Attendance (${status.toUpperCase()})`}
                    </button>

                    {isSubmitted && (
                      <p className="text-sm text-center text-gray-600 py-2">
                        You have already marked attendance for this session
                      </p>
                    )}
                  </div>
                </Card>
              );
            })}
          </div>
        )}

        <Card title="üìã How It Works">
          <div className="space-y-3">
            <p className="flex items-start">
              <span className="text-xl mr-3">1Ô∏è‚É£</span>
              <span>Your instructor will start an attendance session in their dashboard</span>
            </p>
            <p className="flex items-start">
              <span className="text-xl mr-3">2Ô∏è‚É£</span>
              <span>Active sessions for your courses will appear on this page automatically</span>
            </p>
            <p className="flex items-start">
              <span className="text-xl mr-3">3Ô∏è‚É£</span>
              <span>Click "Mark Attendance" to submit your attendance</span>
            </p>
            <p className="flex items-start">
