import React, { useState } from 'react';

const AdminSidebar = ({ activeTab, onTabChange }) => {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const menuItems = [
    { id: 'overview', label: 'Dashboard', icon: 'üìä', color: '#3b82f6' },
    { id: 'students', label: 'Students', icon: 'üë•', color: '#3b82f6' },
    { id: 'instructors', label: 'Instructors', icon: 'üßë‚Äçüè´', color: '#10b981' },
    { id: 'courses', label: 'Courses', icon: 'üìö', color: '#f59e0b' },
    { id: 'sessions', label: 'Sessions', icon: 'üìÖ', color: '#8b5cf6' },
    { id: 'attendance', label: 'Attendance', icon: '‚úÖ', color: '#ec4899' },
    { id: 'reports', label: 'Reports', icon: 'üìà', color: '#06b6d4' },
    { id: 'logs', label: 'Audit Logs', icon: 'üîç', color: '#6b7280' },
  ];

  return (
    <div style={{
      width: isCollapsed ? '80px' : '250px',
      height: '100vh',
      backgroundColor: '#1e293b',
      color: 'white',
      padding: '20px',
      position: 'fixed',
      left: 0,
      top: 0,
      overflowY: 'auto',
      borderRight: '1px solid #334155',
      transition: 'width 0.3s ease',
      zIndex: 1000
    }}>
      {/* Logo */}
      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: isCollapsed ? 'center' : 'space-between',
        marginBottom: '32px',
        paddingBottom: '16px',
        borderBottom: '1px solid #334155'
      }}>
        <div style={{
          fontSize: isCollapsed ? '24px' : '32px',
          fontWeight: 'bold'
        }}>
          üëë
        </div>
        {!isCollapsed && (
          <div style={{ fontSize: '12px', color: '#94a3b8', fontWeight: '600' }}>
            ADMIN
          </div>
        )}
      </div>

      {/* Toggle Button */}
      <button
        onClick={() => setIsCollapsed(!isCollapsed)}
        style={{
          width: '100%',
          padding: '8px',
          backgroundColor: '#334155',
          border: 'none',
          borderRadius: '6px',
          color: 'white',
          cursor: 'pointer',
          marginBottom: '24px',
          fontSize: '16px',
          transition: 'background-color 0.2s'
        }}
        onMouseEnter={(e) => e.target.style.backgroundColor = '#475569'}
        onMouseLeave={(e) => e.target.style.backgroundColor = '#334155'}
      >
        {isCollapsed ? '‚Üí' : '‚Üê'}
      </button>

      {/* Menu Items */}
      <nav style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
        {menuItems.map((item) => (
          <button
            key={item.id}
            onClick={() => onTabChange(item.id)}
            style={{
              width: '100%',
              padding: isCollapsed ? '12px 8px' : '12px 16px',
              backgroundColor: activeTab === item.id ? '#0f172a' : 'transparent',
              border: `1px solid ${activeTab === item.id ? item.color : 'transparent'}`,
              borderRadius: '8px',
              color: activeTab === item.id ? item.color : '#cbd5e1',
              cursor: 'pointer',
              fontWeight: activeTab === item.id ? '600' : '500',
              display: 'flex',
              alignItems: 'center',
              justifyContent: isCollapsed ? 'center' : 'flex-start',
              gap: isCollapsed ? '0' : '12px',
              fontSize: '14px',
              transition: 'all 0.2s',
              whiteSpace: 'nowrap'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.backgroundColor = activeTab === item.id ? '#0f172a' : '#334155';
              e.currentTarget.style.color = item.color;
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.backgroundColor = activeTab === item.id ? '#0f172a' : 'transparent';
              e.currentTarget.style.color = activeTab === item.id ? item.color : '#cbd5e1';
            }}
            title={isCollapsed ? item.label : ''}
          >
            <span style={{ fontSize: '18px' }}>{item.icon}</span>
            {!isCollapsed && <span>{item.label}</span>}
          </button>
        ))}
      </nav>
    </div>
  );
};

export default AdminSidebar;
