import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
  StatCard,
  DataCard,
  SectionCard,
  StatusBadge,
  ProgressBar,
  Button,
  AlertBox,
  LoadingSpinner,
  SkeletonLoader,
  EmptyState,
  Table,
  Modal,
  TimelineItem,
} from '../components/UIComponents';
import {
  studentFetchEnrolledCourses,
  studentFetchActiveSessions,
  studentFetchAttendanceSummary,
  studentFetchCourseAttendance,
  studentFetchAlerts,
  markAttendance,
  studentFetchAttendanceHistory,
} from '../utils/dashboardQueries';
import Sidebar from '../components/Sidebar';
import AdminTopBar from '../components/AdminTopBar';

/**
 * StudentDashboard - Friendly, reassuring interface for students
 * Focus: Attendance progress tracking, course overview, session participation
 * Aesthetic: Green accent, encouraging messages, progress visualization, achievements
 */
const StudentDashboard = () => {
  const navigate = useNavigate();
  const { currentUser, userRole } = useAuth();

  // State management
  const [courses, setCourses] = useState([]);
  const [activeSessions, setActiveSessions] = useState([]);
  const [attendanceSummary, setAttendanceSummary] = useState(null);
  const [courseAttendance, setCourseAttendance] = useState([]);
  const [attendanceHistory, setAttendanceHistory] = useState([]);
  const [courseStats, setCourseStats] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');

  // Modal states
  const [showSessionModal, setShowSessionModal] = useState(false);
  const [selectedSession, setSelectedSession] = useState(null);
  const [qrCode, setQrCode] = useState(null);

  // Fetch data on component mount
  useEffect(() => {
    if (userRole !== 'student') {
      navigate('/admin/dashboard');
      return;
    }
    fetchStudentData();
  }, [userRole, navigate]);

  // Auto-refresh every 30 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      fetchStudentData();
    }, 30000);
    return () => clearInterval(interval);
  }, []);

  const fetchStudentData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Fetch all data in parallel
      const [
        coursesData,
        sessionsData,
        summaryData,
        attendanceData,
        alertsData,
      ] = await Promise.all([
        studentFetchEnrolledCourses(currentUser.id),
        studentFetchActiveSessions(currentUser.id),
        studentFetchAttendanceSummary(currentUser.id),
        studentFetchCourseAttendance(currentUser.id),
        studentFetchAlerts(currentUser.id),
      ]);

      setCourses(coursesData);
      setActiveSessions(sessionsData);
      setAttendanceSummary(summaryData);
      setCourseAttendance(attendanceData);
      setAlerts(alertsData);
    } catch (err) {
      console.error('Error fetching student data:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleMarkAttendance = async (sessionId) => {
    try {
      await markAttendance(currentUser.id, sessionId);
      setError(null);
      await fetchStudentData();
      setShowSessionModal(false);
      // Show success message
      const message = `âœ“ Attendance marked! You're all set.`;
      setAlerts((prev) => [
        {
          id: Date.now(),
          type: 'success',
          message,
          resolved_at: null,
        },
        ...prev,
      ]);
    } catch (err) {
      setError('Failed to mark attendance: ' + err.message);
    }
  };

  const handleSessionClick = async (session) => {
    setSelectedSession(session);
    try {
      // Generate QR code for session if available
      // This would typically be handled by a library like qrcode.react
      setQrCode(`SESSION_${session.id}`);
    } catch (err) {
      console.error('Error loading session details:', err);
    }
    setShowSessionModal(true);
  };

  const getAttendanceStatus = (percentage) => {
    if (percentage >= 80) return 'excellent';
    if (percentage >= 70) return 'good';
    if (percentage >= 60) return 'fair';
    return 'low';
  };

  const getMotivationalMessage = () => {
    if (!attendanceSummary) return '';
    const percentage = attendanceSummary.attendancePercentage || 0;

    if (percentage >= 95) return 'ðŸŒŸ Perfect attendance! Keep it up!';
    if (percentage >= 80) return 'âœ¨ Great job! You\'re doing well!';
    if (percentage >= 70) return 'ðŸ‘ Good progress! Stay focused!';
    if (percentage >= 60) return 'ðŸ’ª You can do better! Keep attending!';
    return 'âš ï¸ Attendance needs attention. Join more sessions!';
  };

  const isAlertResolved = (alert) => alert.resolved_at !== null;

  if (!currentUser) return <LoadingSpinner />;

  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-50 to-gray-100 dark:from-slate-900 dark:to-slate-800">
      <Sidebar role={userRole} />

      <div className="flex-1 flex flex-col overflow-hidden">
        <AdminTopBar title="My Attendance" userRole={userRole} />

        {/* Main Content */}
        <main className="flex-1 overflow-auto">
          <div className="p-4 sm:p-6 lg:p-8">
            {/* Error Alert */}
            {error && (
              <div className="mb-6">
                <AlertBox type="error" message={error} />
              </div>
            )}

            {/* Tab Navigation */}
            <div className="flex gap-2 mb-6 border-b border-gray-200 dark:border-slate-700">
              <button
                onClick={() => setActiveTab('overview')}
                className={`px-4 py-3 font-medium text-sm transition-all ${
                  activeTab === 'overview'
                    ? 'text-green-600 dark:text-green-400 border-b-2 border-green-600 dark:border-green-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-300'
                }`}
              >
                Overview
              </button>
              <button
                onClick={() => setActiveTab('courses')}
                className={`px-4 py-3 font-medium text-sm transition-all ${
                  activeTab === 'courses'
                    ? 'text-green-600 dark:text-green-400 border-b-2 border-green-600 dark:border-green-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-300'
                }`}
              >
                My Courses
              </button>
              <button
                onClick={() => setActiveTab('sessions')}
                className={`px-4 py-3 font-medium text-sm transition-all ${
                  activeTab === 'sessions'
                    ? 'text-green-600 dark:text-green-400 border-b-2 border-green-600 dark:border-green-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-300'
                }`}
              >
                Active Sessions
              </button>
            </div>

            {/* Overview Tab */}
            {activeTab === 'overview' && (
              <div className="space-y-6">
                {/* Motivational Banner */}
                <div className="p-6 bg-gradient-to-r from-green-50 to-emerald-50 dark:from-green-900/20 dark:to-emerald-900/20 rounded-xl border border-green-200 dark:border-green-800">
                  <p className="text-lg font-semibold text-green-900 dark:text-green-300">
                    {getMotivationalMessage()}
                  </p>
                </div>

                {/* Attendance Summary Card */}
                {loading ? (
                  <SkeletonLoader height="150px" />
                ) : attendanceSummary ? (
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <DataCard
                      title="Overall Attendance"
                      stats={[
                        {
                          label: 'Percentage',
                          value: `${attendanceSummary.attendancePercentage}%`,
                        },
                      ]}
                    >
                      <div className="mt-4">
                        <ProgressBar
                          percentage={attendanceSummary.attendancePercentage}
                          status={getAttendanceStatus(
                            attendanceSummary.attendancePercentage
                          )}
                          showLabel={true}
                        />
                      </div>
                    </DataCard>

                    <DataCard
                      title="Session Stats"
                      stats={[
                        {
                          label: 'Attended',
                          value: attendanceSummary.totalPresent,
                        },
                        {
                          label: 'Missed',
                          value: attendanceSummary.totalAbsent,
                        },
                      ]}
                    >
                      <div className="mt-4 text-sm text-gray-600 dark:text-gray-400">
                        Total Sessions: {attendanceSummary.totalPresent + attendanceSummary.totalAbsent}
                      </div>
                    </DataCard>

                    <DataCard
                      title="Enrolled Courses"
                      stats={[
                        {
                          label: 'Courses',
                          value: courses.length,
                        },
                      ]}
                    >
                      <div className="mt-4 text-sm text-gray-600 dark:text-gray-400">
                        Active in {Math.max(1, courses.length)} course(s)
                      </div>
                    </DataCard>
                  </div>
                ) : null}

                {/* Active Sessions for Today */}
                <SectionCard title="Sessions Available Now" icon="event_available">
                  {loading ? (
                    <SkeletonLoader height="200px" />
                  ) : activeSessions.filter((s) => {
                    const now = new Date();
                    const startTime = new Date(s.start_time);
                    const endTime = new Date(s.end_time);
                    return now >= startTime && now <= endTime;
                  }).length > 0 ? (
                    <div className="space-y-3">
                      {activeSessions
                        .filter((s) => {
                          const now = new Date();
                          const startTime = new Date(s.start_time);
                          const endTime = new Date(s.end_time);
                          return now >= startTime && now <= endTime;
                        })
                        .map((session) => (
                          <div
                            key={session.id}
                            className="flex items-center justify-between p-4 bg-green-50 dark:bg-green-900/20 rounded-lg border border-green-200 dark:border-green-800 hover:shadow-md transition-shadow"
                          >
                            <div className="flex-1">
                              <p className="font-semibold text-gray-900 dark:text-white">
                                {session.class?.course?.name}
                              </p>
                              <p className="text-sm text-gray-600 dark:text-gray-400">
                                {session.class?.name} â€¢{' '}
                                {new Date(session.start_time).toLocaleTimeString('en-US', {
                                  hour: '2-digit',
                                  minute: '2-digit',
                                })}
                              </p>
                            </div>
                            <Button
                              variant="primary"
                              size="sm"
                              onClick={() => handleSessionClick(session)}
                            >
                              Mark Attendance
                            </Button>
                          </div>
                        ))}
                    </div>
                  ) : (
                    <EmptyState
                      title="No Active Sessions"
                      message="There are no sessions happening right now. Check back later!"
                      icon="schedule"
                    />
                  )}
                </SectionCard>

                {/* Alerts & Notifications */}
                {alerts.length > 0 && (
                  <SectionCard title="Important Notices" icon="notifications">
                    <div className="space-y-2">
                      {alerts.slice(0, 5).map((alert) => (
                        <div
                          key={alert.id}
                          className={`p-3 rounded-lg border ${
                            isAlertResolved(alert)
                              ? 'bg-gray-50 dark:bg-slate-800 border-gray-200 dark:border-slate-700'
                              : 'bg-yellow-50 dark:bg-yellow-900/20 border-yellow-200 dark:border-yellow-800'
                          }`}
                        >
                          <p className="text-sm text-gray-900 dark:text-white">
                            {alert.message}
                          </p>
                        </div>
                      ))}
                    </div>
                  </SectionCard>
                )}

                {/* Quick Stats */}
                <SectionCard title="Attendance Summary by Course" icon="bar_chart">
                  {loading ? (
                    <SkeletonLoader height="200px" />
                  ) : courseAttendance.length > 0 ? (
                    <div className="space-y-4">
                      {courseAttendance.map((course) => (
                        <div key={course.courseId} className="pb-4 border-b border-gray-200 dark:border-slate-700 last:border-0">
                          <div className="flex items-center justify-between mb-2">
                            <p className="font-medium text-gray-900 dark:text-white">
                              {course.courseName}
                            </p>
                            <StatusBadge
                              status={getAttendanceStatus(course.percentage)}
                            />
                          </div>
                          <ProgressBar
                            percentage={course.percentage}
                            status={getAttendanceStatus(course.percentage)}
                            label={`${course.attended}/${course.total} sessions`}
                          />
                        </div>
                      ))}
                    </div>
                  ) : (
                    <EmptyState
                      title="No Courses Yet"
                      message="You haven't enrolled in any courses yet"
                      icon="school"
                    />
                  )}
                </SectionCard>
              </div>
            )}

            {/* Courses Tab */}
            {activeTab === 'courses' && (
              <div className="space-y-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                  My Enrolled Courses
                </h3>

                {loading ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {[1, 2, 3, 4].map((i) => (
                      <SkeletonLoader key={i} height="200px" />
                    ))}
                  </div>
                ) : courses.length > 0 ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {courses.map((course) => {
                      const courseStats = courseAttendance.find(
                        (c) => c.courseId === course.id
                      );
                      return (
                        <DataCard
                          key={course.id}
                          title={course.name}
                          subtitle={`Instructor: ${course.delegate?.first_name || 'N/A'}`}
                          stats={[
                            {
                              label: 'Attendance',
                              value: `${courseStats?.percentage || 0}%`,
                            },
                            {
                              label: 'Sessions',
                              value: courseStats?.total || 0,
                            },
                          ]}
                        >
                          <div className="mt-4">
                            <ProgressBar
                              percentage={courseStats?.percentage || 0}
                              status={getAttendanceStatus(
                                courseStats?.percentage || 0
                              )}
                            />
                            <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
                              {courseStats?.attended || 0} attended /{' '}
                              {courseStats?.total || 0} total
                            </p>
                          </div>
                        </DataCard>
                      );
                    })}
                  </div>
                ) : (
                  <EmptyState
                    title="No Courses Enrolled"
                    message="You haven't enrolled in any courses yet. Contact your administrator to enroll."
                    icon="school"
                  />
                )}
              </div>
            )}

            {/* Sessions Tab */}
            {activeTab === 'sessions' && (
              <div className="space-y-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                  All Available Sessions
                </h3>

                {loading ? (
                  <SkeletonLoader height="300px" />
                ) : activeSessions.length > 0 ? (
                  <Table
                    columns={[
                      { key: 'course', label: 'Course' },
                      { key: 'time', label: 'Time' },
                      { key: 'status', label: 'Attendance' },
                      { key: 'action', label: 'Action' },
                    ]}
                    data={activeSessions.map((session) => {
                      const now = new Date();
                      const startTime = new Date(session.start_time);
                      const endTime = new Date(session.end_time);
                      const isActive = now >= startTime && now <= endTime;
                      const isEnded = now > endTime;

                      return {
                        id: session.id,
                        course: (
                          <div>
                            <p className="font-medium text-gray-900 dark:text-white">
                              {session.class?.course?.name}
                            </p>
                            <p className="text-sm text-gray-600 dark:text-gray-400">
                              {session.class?.name}
                            </p>
                          </div>
                        ),
                        time: (
                          <div>
                            <p className="text-sm font-medium text-gray-900 dark:text-white">
                              {new Date(session.start_time).toLocaleDateString()}
                            </p>
                            <p className="text-xs text-gray-500 dark:text-gray-400">
                              {new Date(session.start_time).toLocaleTimeString('en-US', {
                                hour: '2-digit',
                                minute: '2-digit',
                              })}
                            </p>
                          </div>
                        ),
                        status: isActive ? (
                          <StatusBadge status="open" />
                        ) : isEnded ? (
                          <StatusBadge status="closed" />
                        ) : (
                          <StatusBadge status="upcoming" />
                        ),
                        action: isActive ? (
                          <Button
                            variant="primary"
                            size="sm"
                            onClick={() => handleSessionClick(session)}
                          >
                            Mark Now
                          </Button>
                        ) : (
                          <Button variant="outline" size="sm" disabled>
                            {isEnded ? 'Closed' : 'Upcoming'}
                          </Button>
                        ),
                      };
                    })}
                    loading={loading}
                  />
                ) : (
                  <EmptyState
                    title="No Sessions Available"
                    message="Check back soon for available attendance sessions"
                    icon="event"
                  />
                )}
              </div>
            )}
          </div>
        </main>
      </div>

      {/* Session Details Modal */}
      {selectedSession && (
        <Modal
          isOpen={showSessionModal}
          title="Mark Attendance"
          onClose={() => {
            setShowSessionModal(false);
            setSelectedSession(null);
            setQrCode(null);
          }}
          onSubmit={() => handleMarkAttendance(selectedSession.id)}
          submitText="Confirm Attendance"
          submitVariant="success"
        >
          <div className="space-y-6">
            {/* Session Details */}
            <div className="p-4 bg-green-50 dark:bg-green-900/20 rounded-lg border border-green-200 dark:border-green-800">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-xs text-gray-600 dark:text-gray-400">Course</p>
                  <p className="font-semibold text-gray-900 dark:text-white">
                    {selectedSession.class?.course?.name}
                  </p>
                </div>
                <div>
                  <p className="text-xs text-gray-600 dark:text-gray-400">Class</p>
                  <p className="font-semibold text-gray-900 dark:text-white">
                    {selectedSession.class?.name}
                  </p>
                </div>
                <div>
                  <p className="text-xs text-gray-600 dark:text-gray-400">Start Time</p>
                  <p className="font-semibold text-gray-900 dark:text-white">
                    {new Date(selectedSession.start_time).toLocaleTimeString('en-US', {
                      hour: '2-digit',
                      minute: '2-digit',
                    })}
                  </p>
                </div>
                <div>
                  <p className="text-xs text-gray-600 dark:text-gray-400">End Time</p>
                  <p className="font-semibold text-gray-900 dark:text-white">
                    {new Date(selectedSession.end_time).toLocaleTimeString('en-US', {
                      hour: '2-digit',
                      minute: '2-digit',
                    })}
                  </p>
                </div>
              </div>
            </div>

            {/* QR Code Display */}
            {qrCode && (
              <div className="flex justify-center p-4 bg-gray-50 dark:bg-slate-800 rounded-lg border border-gray-200 dark:border-slate-700">
                <div className="text-center">
                  <p className="text-sm text-gray-600 dark:text-gray-400 mb-4">
                    Scan QR Code to Mark Attendance
                  </p>
                  <div className="w-32 h-32 bg-white rounded-lg flex items-center justify-center">
                    <span className="material-symbols-outlined text-4xl text-gray-400">
                      qr_code_2
                    </span>
                  </div>
                </div>
              </div>
            )}

            {/* Confirmation Message */}
            <AlertBox
              type="info"
              message="Click 'Confirm Attendance' below to mark yourself as present in this session."
            />
          </div>
        </Modal>
      )}
    </div>
  );
};

export default StudentDashboard;
