import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
  StatCard,
  DataCard,
  SectionCard,
  StatusBadge,
  ProgressBar,
  Button,
  AlertBox,
  LoadingSpinner,
  SkeletonLoader,
  EmptyState,
  Table,
  Modal,
} from '../components/UIComponents';
import {
  delegateFetchAssignedClasses,
  delegateFetchSessions,
  delegateFetchSessionAttendance,
  delegateFetchStats,
  createSession,
} from '../utils/dashboardQueries';
import Sidebar from '../components/Sidebar';
import AdminTopBar from '../components/AdminTopBar';

/**
 * DelegateDashboard - Efficient, action-driven interface for instructors
 * Focus: Fast session management, real-time attendance tracking, student monitoring
 * Aesthetic: Purple accent, clean cards, action buttons, live updates
 */
const DelegateDashboard = () => {
  const navigate = useNavigate();
  const { currentUser, userRole } = useAuth();

  // State management
  const [stats, setStats] = useState(null);
  const [assignedClasses, setAssignedClasses] = useState([]);
  const [sessions, setSessions] = useState([]);
  const [sessionAttendance, setSessionAttendance] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');
  const [refreshInterval, setRefreshInterval] = useState(30000);
  
  // Modal states
  const [showCreateSession, setShowCreateSession] = useState(false);
  const [showSessionDetails, setShowSessionDetails] = useState(false);
  const [selectedSession, setSelectedSession] = useState(null);
  const [sessionFormData, setSessionFormData] = useState({
    classId: '',
    startTime: new Date().toISOString().slice(0, 16),
    endTime: new Date(Date.now() + 3600000).toISOString().slice(0, 16),
  });

  // Fetch data on component mount and at intervals
  useEffect(() => {
    if (userRole !== 'delegate') {
      navigate('/student/dashboard');
      return;
    }
    fetchDelegateData();
  }, [userRole, navigate]);

  // Auto-refresh every 30 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      fetchDelegateData();
    }, refreshInterval);
    return () => clearInterval(interval);
  }, [refreshInterval]);

  const fetchDelegateData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Fetch all data in parallel
      const [statsData, classesData, sessionsData] = await Promise.all([
        delegateFetchStats(currentUser.id),
        delegateFetchAssignedClasses(currentUser.id),
        delegateFetchSessions(currentUser.id),
      ]);

      setStats(statsData);
      setAssignedClasses(classesData);
      setSessions(sessionsData);

      // Fetch attendance for each session
      if (sessionsData.length > 0) {
        const attendanceMap = {};
        for (const session of sessionsData) {
          const attendance = await delegateFetchSessionAttendance(session.id);
          attendanceMap[session.id] = attendance;
        }
        setSessionAttendance(attendanceMap);
      }
    } catch (err) {
      console.error('Error fetching delegate data:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateSession = async (e) => {
    e.preventDefault();
    try {
      await createSession({
        classId: sessionFormData.classId,
        delegateId: currentUser.id,
        startTime: sessionFormData.startTime,
        endTime: sessionFormData.endTime,
      });
      setShowCreateSession(false);
      setSessionFormData({
        classId: '',
        startTime: new Date().toISOString().slice(0, 16),
        endTime: new Date(Date.now() + 3600000).toISOString().slice(0, 16),
      });
      await fetchDelegateData();
    } catch (err) {
      setError('Failed to create session: ' + err.message);
    }
  };

  const handleSelectSession = (session) => {
    setSelectedSession(session);
    setShowSessionDetails(true);
  };

  const getSessionStatus = (session) => {
    const now = new Date();
    const startTime = new Date(session.start_time);
    const endTime = new Date(session.end_time);

    if (now < startTime) return 'upcoming';
    if (now > endTime) return 'closed';
    return 'open';
  };

  const getAttendanceStats = (sessionId) => {
    const attendance = sessionAttendance[sessionId] || [];
    const total = attendance.length;
    const present = attendance.filter((a) => a.status === 'present').length;
    const absent = attendance.filter((a) => a.status === 'absent').length;
    const percentage = total > 0 ? Math.round((present / total) * 100) : 0;

    return { total, present, absent, percentage };
  };

  if (!currentUser) return <LoadingSpinner />;

  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-50 to-gray-100 dark:from-slate-900 dark:to-slate-800">
      <Sidebar role={userRole} />

      <div className="flex-1 flex flex-col overflow-hidden">
        <AdminTopBar title="Instructor Control Panel" userRole={userRole} />

        {/* Main Content */}
        <main className="flex-1 overflow-auto">
          <div className="p-4 sm:p-6 lg:p-8">
            {/* Error Alert */}
            {error && (
              <div className="mb-6">
                <AlertBox type="error" message={error} />
              </div>
            )}

            {/* Tab Navigation */}
            <div className="flex gap-2 mb-6 border-b border-gray-200 dark:border-slate-700">
              <button
                onClick={() => setActiveTab('overview')}
                className={`px-4 py-3 font-medium text-sm transition-all ${
                  activeTab === 'overview'
                    ? 'text-purple-600 dark:text-purple-400 border-b-2 border-purple-600 dark:border-purple-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-300'
                }`}
              >
                Overview
              </button>
              <button
                onClick={() => setActiveTab('sessions')}
                className={`px-4 py-3 font-medium text-sm transition-all ${
                  activeTab === 'sessions'
                    ? 'text-purple-600 dark:text-purple-400 border-b-2 border-purple-600 dark:border-purple-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-300'
                }`}
              >
                Live Sessions
              </button>
              <button
                onClick={() => setActiveTab('classes')}
                className={`px-4 py-3 font-medium text-sm transition-all ${
                  activeTab === 'classes'
                    ? 'text-purple-600 dark:text-purple-400 border-b-2 border-purple-600 dark:border-purple-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-300'
                }`}
              >
                My Classes
              </button>
            </div>

            {/* Overview Tab */}
            {activeTab === 'overview' && (
              <div className="space-y-6">
                {/* KPI Cards */}
                {loading ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                    {[1, 2, 3, 4].map((i) => (
                      <SkeletonLoader key={i} height="120px" />
                    ))}
                  </div>
                ) : stats ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                    <StatCard
                      label="Total Students"
                      value={stats.totalStudents}
                      icon="group"
                      trend={`${stats.enrolledStudents} enrolled`}
                    />
                    <StatCard
                      label="Active Classes"
                      value={stats.totalClasses}
                      icon="class"
                      trend={`${stats.activeClasses} active`}
                    />
                    <StatCard
                      label="Today's Sessions"
                      value={stats.sessionsToday}
                      icon="schedule"
                      trend={`${stats.openSessions} open now`}
                    />
                    <StatCard
                      label="Avg Attendance"
                      value={`${stats.avgAttendance}%`}
                      icon="check_circle"
                      trend={`${stats.totalAttendance} marked`}
                    />
                  </div>
                ) : null}

                {/* Active Sessions Overview */}
                <SectionCard title="Active Sessions Right Now" icon="play_circle">
                  {loading ? (
                    <SkeletonLoader height="200px" />
                  ) : sessions.filter((s) => getSessionStatus(s) === 'open').length > 0 ? (
                    <div className="space-y-3">
                      {sessions
                        .filter((s) => getSessionStatus(s) === 'open')
                        .map((session) => {
                          const stats = getAttendanceStats(session.id);
                          return (
                            <div
                              key={session.id}
                              className="flex items-center justify-between p-4 bg-purple-50 dark:bg-purple-900/20 rounded-lg border border-purple-200 dark:border-purple-800 hover:shadow-md transition-shadow"
                            >
                              <div className="flex-1">
                                <p className="font-semibold text-gray-900 dark:text-white">
                                  {session.class?.course?.name}
                                </p>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  {session.class?.name}  {stats.present}/{stats.total} present
                                </p>
                              </div>
                              <Button
                                variant="primary"
                                size="sm"
                                onClick={() => handleSelectSession(session)}
                              >
                                View Details
                              </Button>
                            </div>
                          );
                        })}
                    </div>
                  ) : (
                    <EmptyState
                      title="No Active Sessions"
                      message="All your sessions are scheduled for later or have ended"
                      icon="schedule"
                    />
                  )}
                </SectionCard>

                {/* Quick Actions */}
                <SectionCard title="Quick Actions" icon="bolt">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                    <Button
                      variant="primary"
                      onClick={() => setShowCreateSession(true)}
                      className="justify-center"
                    >
                      <span className="material-symbols-outlined mr-2">add</span>
                      Create New Session
                    </Button>
                    <Button
                      variant="outline"
                      onClick={() => setActiveTab('classes')}
                      className="justify-center"
                    >
                      <span className="material-symbols-outlined mr-2">calendar_month</span>
                      View My Classes
                    </Button>
                  </div>
                </SectionCard>

                {/* Today's Schedule */}
                <SectionCard title="Today's Schedule" icon="today">
                  {loading ? (
                    <SkeletonLoader height="150px" />
                  ) : sessions.filter((s) => {
                    const today = new Date().toDateString();
                    const sessionDate = new Date(s.start_time).toDateString();
                    return sessionDate === today;
                  }).length > 0 ? (
                    <div className="space-y-2">
                      {sessions
                        .filter((s) => {
                          const today = new Date().toDateString();
                          const sessionDate = new Date(s.start_time).toDateString();
                          return sessionDate === today;
                        })
                        .sort(
                          (a, b) =>
                            new Date(a.start_time) - new Date(b.start_time)
                        )
                        .map((session) => (
                          <div
                            key={session.id}
                            className="flex items-center justify-between p-3 bg-white dark:bg-slate-800 rounded-lg border border-gray-200 dark:border-slate-700"
                          >
                            <div>
                              <p className="font-medium text-gray-900 dark:text-white">
                                {new Date(session.start_time).toLocaleTimeString(
                                  'en-US',
                                  { hour: '2-digit', minute: '2-digit' }
                                )}{' '}
                                - {session.class?.name}
                              </p>
                              <p className="text-xs text-gray-500 dark:text-gray-400">
                                {session.class?.course?.name}
                              </p>
                            </div>
                            <StatusBadge status={getSessionStatus(session)} />
                          </div>
                        ))}
                    </div>
                  ) : (
                    <EmptyState
                      title="No Sessions Today"
                      message="You have no scheduled sessions for today"
                      icon="event_available"
                    />
                  )}
                </SectionCard>
              </div>
            )}

            {/* Sessions Tab */}
            {activeTab === 'sessions' && (
              <div className="space-y-6">
                <div className="flex justify-between items-center">
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                    All Sessions
                  </h3>
                  <Button
                    variant="primary"
                    size="sm"
                    onClick={() => setShowCreateSession(true)}
                  >
                    <span className="material-symbols-outlined mr-1">add</span>
                    New Session
                  </Button>
                </div>

                {loading ? (
                  <SkeletonLoader height="300px" />
                ) : sessions.length > 0 ? (
                  <Table
                    columns={[
                      { key: 'course', label: 'Course & Class' },
                      { key: 'time', label: 'Time' },
                      { key: 'attendance', label: 'Attendance' },
                      { key: 'status', label: 'Status' },
                      { key: 'action', label: 'Action' },
                    ]}
                    data={sessions.map((session) => ({
                      id: session.id,
                      course: (
                        <div>
                          <p className="font-medium text-gray-900 dark:text-white">
                            {session.class?.course?.name}
                          </p>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            {session.class?.name}
                          </p>
                        </div>
                      ),
                      time: (
                        <div>
                          <p className="text-sm font-medium text-gray-900 dark:text-white">
                            {new Date(session.start_time).toLocaleDateString()}
                          </p>
                          <p className="text-xs text-gray-500 dark:text-gray-400">
                            {new Date(session.start_time).toLocaleTimeString('en-US', {
                              hour: '2-digit',
                              minute: '2-digit',
                            })}
                            {' - '}
                            {new Date(session.end_time).toLocaleTimeString('en-US', {
                              hour: '2-digit',
                              minute: '2-digit',
                            })}
                          </p>
                        </div>
                      ),
                      attendance: (
                        <ProgressBar
                          percentage={getAttendanceStats(session.id).percentage}
                          label={`${getAttendanceStats(session.id).present}/${
                            getAttendanceStats(session.id).total
                          }`}
                        />
                      ),
                      status: <StatusBadge status={getSessionStatus(session)} />,
                      action: (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleSelectSession(session)}
                        >
                          View
                        </Button>
                      ),
                    }))}
                    loading={loading}
                  />
                ) : (
                  <EmptyState
                    title="No Sessions Created"
                    message="Create your first attendance session to get started"
                    icon="schedule"
                  />
                )}
              </div>
            )}

            {/* Classes Tab */}
            {activeTab === 'classes' && (
              <div className="space-y-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                  My Assigned Classes
                </h3>

                {loading ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {[1, 2, 3, 4].map((i) => (
                      <SkeletonLoader key={i} height="200px" />
                    ))}
                  </div>
                ) : assignedClasses.length > 0 ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {assignedClasses.map((classItem) => {
                      const classSessions = sessions.filter(
                        (s) => s.class_id === classItem.id
                      );
                      const enrolledCount = classItem.enrollments?.length || 0;

                      return (
                        <DataCard
                          key={classItem.id}
                          title={classItem.name}
                          subtitle={classItem.course?.name}
                          stats={[
                            { label: 'Students', value: enrolledCount },
                            { label: 'Sessions', value: classSessions.length },
                          ]}
                        >
                          <div className="mt-4 space-y-3">
                            <Button
                              variant="primary"
                              size="sm"
                              onClick={() => setShowCreateSession(true)}
                              className="w-full justify-center"
                            >
                              Start Session
                            </Button>
                            <Button
                              variant="outline"
                              size="sm"
                              className="w-full justify-center"
                            >
                              View Students
                            </Button>
                          </div>
                        </DataCard>
                      );
                    })}
                  </div>
                ) : (
                  <EmptyState
                    title="No Classes Assigned"
                    message="You don't have any classes assigned yet. Contact the administrator."
                    icon="school"
                  />
                )}
              </div>
            )}
          </div>
        </main>
      </div>

      {/* Create Session Modal */}
      <Modal
        isOpen={showCreateSession}
        title="Create New Attendance Session"
        onClose={() => setShowCreateSession(false)}
        onSubmit={handleCreateSession}
        submitText="Create Session"
        submitVariant="primary"
      >
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Select Class
            </label>
            <select
              value={sessionFormData.classId}
              onChange={(e) =>
                setSessionFormData({ ...sessionFormData, classId: e.target.value })
              }
              className="w-full px-3 py-2 border border-gray-300 dark:border-slate-600 rounded-lg bg-white dark:bg-slate-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-purple-500"
              required
            >
              <option value="">Choose a class...</option>
              {assignedClasses.map((classItem) => (
                <option key={classItem.id} value={classItem.id}>
                  {classItem.course?.name} - {classItem.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Start Time
            </label>
            <input
              type="datetime-local"
              value={sessionFormData.startTime}
              onChange={(e) =>
                setSessionFormData({ ...sessionFormData, startTime: e.target.value })
              }
              className="w-full px-3 py-2 border border-gray-300 dark:border-slate-600 rounded-lg bg-white dark:bg-slate-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-purple-500"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              End Time
            </label>
            <input
              type="datetime-local"
              value={sessionFormData.endTime}
              onChange={(e) =>
                setSessionFormData({ ...sessionFormData, endTime: e.target.value })
              }
              className="w-full px-3 py-2 border border-gray-300 dark:border-slate-600 rounded-lg bg-white dark:bg-slate-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-purple-500"
              required
            />
          </div>
        </div>
      </Modal>

      {/* Session Details Modal */}
      {selectedSession && (
        <Modal
          isOpen={showSessionDetails}
          title="Session Attendance Details"
          onClose={() => {
            setShowSessionDetails(false);
            setSelectedSession(null);
          }}
          size="lg"
        >
          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4 p-4 bg-purple-50 dark:bg-purple-900/20 rounded-lg">
              <div>
                <p className="text-xs text-gray-600 dark:text-gray-400">Course</p>
                <p className="font-semibold text-gray-900 dark:text-white">
                  {selectedSession.class?.course?.name}
                </p>
              </div>
              <div>
                <p className="text-xs text-gray-600 dark:text-gray-400">Class</p>
                <p className="font-semibold text-gray-900 dark:text-white">
                  {selectedSession.class?.name}
                </p>
              </div>
              <div>
                <p className="text-xs text-gray-600 dark:text-gray-400">Status</p>
                <div className="mt-1">
                  <StatusBadge status={getSessionStatus(selectedSession)} />
                </div>
              </div>
              <div>
                <p className="text-xs text-gray-600 dark:text-gray-400">Attendance</p>
                <p className="font-semibold text-gray-900 dark:text-white">
                  {getAttendanceStats(selectedSession.id).percentage}%
                </p>
              </div>
            </div>

            {/* Attendance Table */}
            {sessionAttendance[selectedSession.id] ? (
              <Table
                columns={[
                  { key: 'student', label: 'Student' },
                  { key: 'status', label: 'Status' },
                  { key: 'time', label: 'Time' },
                ]}
                data={sessionAttendance[selectedSession.id].map((record) => ({
                  id: record.id,
                  student: (
                    <div>
                      <p className="font-medium text-gray-900 dark:text-white">
                        {record.student?.first_name} {record.student?.last_name}
                      </p>
                      <p className="text-xs text-gray-600 dark:text-gray-400">
                        {record.student?.email}
                      </p>
                    </div>
                  ),
                  status: <StatusBadge status={record.status} />,
                  time: record.marked_at
                    ? new Date(record.marked_at).toLocaleTimeString('en-US', {
                        hour: '2-digit',
                        minute: '2-digit',
                      })
                    : '-',
                }))}
              />
            ) : null}
          </div>
        </Modal>
      )}
    </div>
  );
};

export default DelegateDashboard;
