import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [emailFocused, setEmailFocused] = useState(false);
  const [passwordFocused, setPasswordFocused] = useState(false);
  const { login, currentUser, userRole } = useAuth();
  const navigate = useNavigate();

  // Redirect if already logged in
  useEffect(() => {
    console.log('üîÑ Login: Checking if user is already logged in - currentUser:', !!currentUser, 'userRole:', userRole);
    
    if (currentUser && userRole) {
      console.log('‚úÖ Login: User is logged in with role:', userRole);
      
      if (userRole === 'admin') {
        console.log('üìç Login: Redirecting to admin dashboard');
        navigate('/admin/dashboard', { replace: true });
      } else if (userRole === 'delegate') {
        console.log('üìç Login: Redirecting to delegate dashboard');
        navigate('/delegate/dashboard', { replace: true });
      } else if (userRole === 'student') {
        console.log('üìç Login: Redirecting to student dashboard');
        navigate('/student/dashboard', { replace: true });
      }
    }
  }, [currentUser, userRole, navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    console.log('üîê Login.handleLogin: Login attempt with email:', email);
    setError('');
    setLoading(true);

    try {
      // Validate email format
      if (!email.toLowerCase().endsWith('@ictuniversity.edu.cm')) {
        console.warn('‚ùå Login.handleLogin: Invalid email domain:', email);
        setError('‚ùå Only ICT University emails (@ictuniversity.edu.cm) are allowed');
        setLoading(false);
        return;
      }

      if (!password) {
        console.warn('‚ùå Login.handleLogin: No password provided');
        setError('‚ùå Please enter your password');
        setLoading(false);
        return;
      }

      console.log('üîÑ Login.handleLogin: Calling AuthContext.login()...');
      
      // Call login - AuthContext handles role fetching automatically
      await login(email, password);
      
      console.log('‚úÖ Login.handleLogin: Login successful! AuthContext should be updated');
      
    } catch (err) {
      console.error('‚ùå Login.handleLogin: Login error caught:', err.message);
      
      let errorMsg = err?.message || 'Invalid email or password';
      
      if (err?.message?.includes('Invalid')) {
        errorMsg = 'Invalid email or password. Please check your credentials.';
      } else if (err?.message?.includes('CORS') || err?.message?.includes('fetch')) {
        errorMsg = 'Connection error. Please check your internet connection and try again.';
      } else if (err?.message?.includes('timeout')) {
        errorMsg = 'Login is taking too long. Please try again or check your connection.';
      } else if (err?.status === 401) {
        errorMsg = 'Invalid email or password. Please check your credentials.';
      } else if (err?.status === 403) {
        errorMsg = 'Access denied. Your account may be disabled.';
      }
      
      setError(`‚ùå ${errorMsg}`);
      setLoading(false);
    }
  };

  // Unified Central Login Page
  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '16px',
      backgroundColor: '#F8FAFC'
    }}>
      <div style={{
        maxWidth: '500px',
        width: '100%',
        borderRadius: '12px',
        boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
        overflow: 'hidden',
        backgroundColor: '#FFFFFF',
        border: '1px solid #E2E8F0'
      }}>
        {/* Header */}
        <div style={{
          padding: '40px 32px',
          color: 'white',
          backgroundColor: '#1E3A8A',
          textAlign: 'center'
        }}>
          <div style={{ fontSize: '56px', marginBottom: '16px' }}>üéì</div>
          <h1 style={{ fontSize: '32px', fontWeight: 'bold', margin: '0 0 8px 0' }}>
            ICT University
          </h1>
          <p style={{ fontSize: '14px', color: '#93C5FD', margin: '0' }}>
            Time-Based Attendance System
          </p>
        </div>

        {/* Form Container */}
        <div style={{ padding: '32px' }}>
          {/* Title */}
          <h2 style={{
            fontSize: '24px',
            fontWeight: 'bold',
            marginBottom: '8px',
            color: '#0F172A'
          }}>
            Central Login
          </h2>
          <p style={{
            fontSize: '14px',
            marginBottom: '24px',
            color: '#475569',
            lineHeight: '1.5'
          }}>
            Sign in with your ICT University email. Your role will be automatically assigned.
          </p>

          {/* Role Info Cards */}
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(3, 1fr)',
            gap: '12px',
            marginBottom: '24px'
          }}>
            {[
              { icon: 'üéì', label: 'Student', desc: 'Mark Attendance' },
              { icon: 'üë®‚Äçüè´', label: 'Delegate', desc: 'Manage Sessions' },
              { icon: 'üëë', label: 'Admin', desc: 'Admin Dashboard' }
            ].map((item, idx) => (
              <div
                key={idx}
                style={{
                  padding: '12px',
                  borderRadius: '8px',
                  border: '1px solid #E2E8F0',
                  backgroundColor: '#F9FAFB',
                  textAlign: 'center'
                }}
              >
                <div style={{ fontSize: '28px', marginBottom: '6px' }}>{item.icon}</div>
                <div style={{ fontWeight: '600', fontSize: '13px', color: '#0F172A', marginBottom: '2px' }}>
                  {item.label}
                </div>
                <div style={{ fontSize: '11px', color: '#6B7280' }}>{item.desc}</div>
              </div>
            ))}
          </div>

          {/* Divider */}
          <div style={{
            height: '1px',
            backgroundColor: '#E2E8F0',
            marginBottom: '24px'
          }}></div>

          {/* Error Messages */}
          {error && (
            <div style={{
              marginBottom: '16px',
              padding: '12px 16px',
              backgroundColor: '#FEE2E2',
              border: '1px solid #FECACA',
              borderRadius: '8px',
              fontSize: '13px',
              color: '#991B1B'
            }}>
              {error}
            </div>
          )}

          {/* Login Form */}
          <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
            {/* Email Input */}
            <div>
              <label style={{
                display: 'block',
                fontSize: '13px',
                fontWeight: '600',
                marginBottom: '8px',
                color: '#0F172A'
              }}>
                Email Address
              </label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="your.email@ictuniversity.edu.cm"
                style={{
                  width: '100%',
                  padding: '12px 16px',
                  borderRadius: '8px',
                  border: '1px solid #E2E8F0',
                  fontSize: '14px',
                  color: '#0F172A',
                  boxSizing: 'border-box',
                  transition: 'border-color 0.2s'
                }}
                onFocus={(e) => e.target.style.borderColor = '#1E3A8A'}
                onBlur={(e) => e.target.style.borderColor = '#E2E8F0'}
                required
              />
              <p style={{
                fontSize: '12px',
                marginTop: '6px',
                color: '#6B7280',
                margin: '6px 0 0 0'
              }}>
                Use your institutional email
              </p>
            </div>

            {/* Password Input with Toggle */}
            <div>
              <label style={{
                display: 'block',
                fontSize: '13px',
                fontWeight: '600',
                marginBottom: '8px',
                color: '#0F172A'
              }}>
                Password
              </label>
              <div style={{ position: 'relative' }}>
                <input
                  type={showPassword ? 'text' : 'password'}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  style={{
                    width: '100%',
                    padding: '12px 16px',
                    paddingRight: '44px',
                    borderRadius: '8px',
                    border: '1px solid #E2E8F0',
                    fontSize: '14px',
                    color: '#0F172A',
                    boxSizing: 'border-box',
                    transition: 'border-color 0.2s'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#1E3A8A'}
                  onBlur={(e) => e.target.style.borderColor = '#E2E8F0'}
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  style={{
                    position: 'absolute',
                    right: '12px',
                    top: '50%',
                    transform: 'translateY(-50%)',
                    backgroundColor: 'transparent',
                    border: 'none',
                    cursor: 'pointer',
                    fontSize: '18px',
                    color: '#6B7280',
                    padding: '4px 8px',
                    transition: 'color 0.2s'
                  }}
                  onMouseEnter={(e) => e.target.style.color = '#1E3A8A'}
                  onMouseLeave={(e) => e.target.style.color = '#6B7280'}
                >
                  {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
              </div>
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              disabled={loading}
              style={{
                width: '100%',
                fontWeight: '600',
                padding: '12px 16px',
                borderRadius: '8px',
                transition: 'background-color 0.2s',
                marginTop: '8px',
                boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
                color: 'white',
                fontSize: '15px',
                border: 'none',
                cursor: loading ? 'not-allowed' : 'pointer',
                backgroundColor: loading ? '#1E40AF' : '#1E3A8A',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px'
              }}
              onMouseEnter={(e) => !loading && (e.target.style.backgroundColor = '#1D3A8A')}
              onMouseLeave={(e) => !loading && (e.target.style.backgroundColor = '#1E3A8A')}
            >
              {loading ? (
                <>
                  <span style={{
                    display: 'inline-block',
                    width: '16px',
                    height: '16px',
                    border: '2px solid white',
                    borderTopColor: 'transparent',
                    borderRadius: '50%',
                    animation: 'spin 1s linear infinite'
                  }}></span>
                  Signing in...
                </>
              ) : (
                <>üîê Sign In</>
              )}
            </button>
          </form>

          {/* Footer Info */}
          <div style={{
            marginTop: '20px',
            padding: '12px',
            backgroundColor: '#F0F9FF',
            borderRadius: '8px',
            fontSize: '12px',
            color: '#475569',
            textAlign: 'center',
            lineHeight: '1.5'
          }}>
            üí° <strong>Tip:</strong> Your role is automatically determined by your registration. All users access this single login page.
          </div>
        </div>
      </div>

      {/* Keyframe Animation */}
      <style>{`
        @keyframes spin {
          to { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default Login;
