import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Table from '../components/Table';
import Badge from '../components/Badge';

const StudentAttendanceHistory = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  const [attendanceData, setAttendanceData] = useState([]);
  const [loadingData, setLoadingData] = useState(true);
  const [stats, setStats] = useState({
    total: 0,
    present: 0,
    late: 0,
    absent: 0
  });

  useEffect(() => {
    if (loading) return;

    if (!currentUser || userRole !== 'student') {
      navigate('/student');
      return;
    }

    fetchAttendanceHistory();
  }, [currentUser, userRole, loading, navigate]);

  const fetchAttendanceHistory = async () => {
    try {
      setLoadingData(true);

      // Get attendance records for this student
      const { data: attendanceRecords } = await supabase
        .from('attendance')
        .select(`
          id,
          created_at,
          sessions (
            id,
            session_date,
            start_time,
            classes (
              class_name,
              courses (
                course_code,
                course_title
              )
            )
          )
        `)
        .eq('student_id', currentUser.id)
        .order('created_at', { ascending: false });

      // Format data for table
      const history = (attendanceRecords || []).map(record => ({
        id: record.id,
        class: record.sessions?.classes?.courses?.course_code || 'Unknown',
        date: new Date(record.sessions?.session_date).toLocaleDateString(),
        time: record.sessions?.start_time || 'N/A',
        status: 'present'
      }));

      setAttendanceData(history);

      // Calculate stats
      setStats({
        total: history.length,
        present: history.length,
        late: 0,
        absent: 0
      });
    } catch (err) {
      console.error('Error fetching attendance history:', err);
      setAttendanceData([]);
      setStats({ total: 0, present: 0, late: 0, absent: 0 });
    } finally {
      setLoadingData(false);
    }
  };

  if (loading || loadingData) return (
    <Layout role="student">
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600">Loading attendance history...</p>
        </div>
      </div>
    </Layout>
  );

  if (!currentUser || userRole !== 'student') return null;

  return (
    <Layout role="student">
      <div className="space-y-8">
        <div>
          <h1 className="text-3xl font-bold mb-2">Attendance History</h1>
          <p className="text-gray-600">View your attendance records</p>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
          <Card>
            <div className="text-center">
              <p className="text-3xl font-bold text-blue-600">{stats.total}</p>
              <p className="text-sm text-gray-600 mt-2">Total Sessions</p>
            </div>
          </Card>
          <Card>
            <div className="text-center">
              <p className="text-3xl font-bold text-green-600">{stats.present}</p>
              <p className="text-sm text-gray-600 mt-2">Present</p>
            </div>
          </Card>
          <Card>
            <div className="text-center">
              <p className="text-3xl font-bold text-amber-600">{stats.late}</p>
              <p className="text-sm text-gray-600 mt-2">Late</p>
            </div>
          </Card>
          <Card>
            <div className="text-center">
              <p className="text-3xl font-bold text-red-600">{stats.absent}</p>
              <p className="text-sm text-gray-600 mt-2">Absent</p>
            </div>
          </Card>
        </div>

        {/* Attendance Table */}
        <Card title="ðŸ“‹ Detailed History">
          {attendanceData.length > 0 ? (
            <Table
              columns={[
                { key: 'class', label: 'Class' },
                { key: 'date', label: 'Date' },
                { key: 'time', label: 'Time Marked' },
                {
                  key: 'status',
                  label: 'Status',
                  render: (status) => <Badge label={status.toUpperCase()} variant={status === 'present' ? 'success' : status === 'late' ? 'warning' : 'danger'} />,
                },
              ]}
              data={attendanceData}
              searchable
              paginated
              itemsPerPage={10}
            />
          ) : (
            <p className="text-gray-600">No attendance history available</p>
          )}
        </Card>
      </div>
    </Layout>
  );
};

export default StudentAttendanceHistory;
