import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate, useParams } from 'react-router-dom';

const Login = ({ role: propRole }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login, currentUser, userRole } = useAuth();
  const navigate = useNavigate();
  const params = useParams();
  
  // Get role from route parameter or prop
  const role = propRole || params.role || 'student';

  // Redirect if already logged in with correct role
  useEffect(() => {
    console.log('üîÑ Login: Checking if user is already logged in - currentUser:', !!currentUser, 'userRole:', userRole);
    
    if (currentUser && userRole) {
      console.log('‚úÖ Login: User is logged in with role:', userRole);
      
      if (userRole === 'admin') {
        console.log('üìç Login: Redirecting to admin dashboard');
        navigate('/admin/dashboard', { replace: true });
      } else if (userRole === 'delegate') {
        console.log('üìç Login: Redirecting to delegate dashboard');
        navigate('/delegate/dashboard', { replace: true });
      } else if (userRole === 'student') {
        console.log('üìç Login: Redirecting to student dashboard');
        navigate('/student/dashboard', { replace: true });
      }
    }
  }, [currentUser, userRole, navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    console.log('üîê Login.handleLogin: Login attempt with email:', email);
    setError('');
    setLoading(true);

    try {
      // Validate email format
      if (!email.toLowerCase().endsWith('@ictuniversity.edu.cm')) {
        console.warn('‚ùå Login.handleLogin: Invalid email domain:', email);
        setError('‚ùå Only ICT University emails (@ictuniversity.edu.cm) are allowed');
        setLoading(false);
        return;
      }

      if (!password) {
        console.warn('‚ùå Login.handleLogin: No password provided');
        setError('‚ùå Please enter your password');
        setLoading(false);
        return;
      }

      console.log('üîÑ Login.handleLogin: Calling AuthContext.login()...');
      
      // Add timeout - if auth doesn't complete in 10 seconds, fail (with non-blocking login, should be instant)
      const loginPromise = login(email, password);
      const timeoutPromise = new Promise((_, reject) =>
        setTimeout(() => reject(new Error('Login timeout - please check your connection')), 10000)
      );
      
      await Promise.race([loginPromise, timeoutPromise]);
      
      console.log('‚úÖ Login.handleLogin: Login successful! AuthContext should be updated');
      // setLoading will be cleared by the redirect happening in useEffect
      // Don't set it to false here - the component will unmount on redirect
      
    } catch (err) {
      console.error('‚ùå Login.handleLogin: Login error caught:', err.message);
      console.error('‚ùå Login.handleLogin: Error details:', { message: err?.message, code: err?.code, status: err?.status });
      // Show more detailed error message
      let errorMsg = err?.message || 'Invalid email or password';
      
      // Provide specific error messages for common issues
      if (err?.message?.includes('Invalid')) {
        errorMsg = 'Invalid email or password. Please check your credentials.';
      } else if (err?.message?.includes('CORS') || err?.message?.includes('fetch')) {
        errorMsg = 'Connection error. Please check your internet connection and try again.';
      } else if (err?.message?.includes('timeout')) {
        errorMsg = 'Login is taking too long. Please try again or check your connection.';
      } else if (err?.status === 401) {
        errorMsg = 'Invalid email or password. Please check your credentials.';
      } else if (err?.status === 403) {
        errorMsg = 'Access denied. Your account may be disabled.';
      }
      
      setError(`‚ùå ${errorMsg}`);
      setLoading(false);
    }
  };

  // Get emoji and titles based on role
  const getRoleEmoji = () => {
    if (role === 'admin') return 'üë®‚Äçüíº';
    if (role === 'delegate') return 'üë®‚Äçüè´';
    return 'üéì';
  };

  const getRoleTitle = () => {
    if (role === 'admin') return 'Admin';
    if (role === 'delegate') return 'Delegate';
    return 'Student';
  };

  const getRoleDescription = () => {
    if (role === 'admin') return 'Administer users and classes';
    if (role === 'delegate') return 'Manage attendance sessions for your classes';
    return 'Mark attendance using GPS';
  };

  // Simple login view (no role selection)
  return (
    <div className="min-h-screen flex items-center justify-center p-4" style={{ backgroundColor: '#F8FAFC' }}>
      <div className="max-w-md w-full rounded-xl shadow-lg overflow-hidden" style={{ backgroundColor: '#FFFFFF', borderColor: '#E2E8F0', borderWidth: '1px' }}>
        {/* Header */}
        <div className="p-8 text-white" style={{ backgroundColor: '#1E3A8A' }}>
          <div className="flex items-center justify-between mb-2">
            <div>
              <h1 className="text-3xl font-bold">ICT University</h1>
              <p className="text-blue-100">GPS-Based Attendance</p>
            </div>
            <div className="text-4xl">{getRoleEmoji()}</div>
          </div>
        </div>

        {/* Form Container */}
        <div className="p-8">
          {/* Role Display */}
          <h2 className="text-2xl font-bold mb-1" style={{ color: '#0F172A' }}>
            {getRoleTitle()} Login
          </h2>
          <p className="text-sm mb-6" style={{ color: '#475569' }}>
            {getRoleDescription()}
          </p>

          {/* Error Messages */}
          {error && (
            <div className="mb-6 p-4 bg-red-50 border text-red-700 rounded-lg text-sm" style={{ borderColor: '#E2E8F0' }}>
              {error}
            </div>
          )}

          {/* Login Form */}
          <form onSubmit={handleLogin} className="space-y-4">
            {/* Email Input */}
            <div>
              <label className="block text-sm font-semibold mb-2" style={{ color: '#0F172A' }}>
                Email Address
              </label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="your.email@ictuniversity.edu.cm"
                className="w-full px-4 py-3 rounded-lg focus:outline-none focus:ring-2 transition"
                style={{ 
                  borderColor: '#E2E8F0',
                  borderWidth: '1px',
                  color: '#0F172A',
                  focusRingColor: '#1E3A8A'
                }}
                required
              />
              <p className="text-xs mt-1" style={{ color: '#475569' }}>Use your institutional email</p>
            </div>

            {/* Password Input with Toggle */}
            <div>
              <label className="block text-sm font-semibold mb-2" style={{ color: '#0F172A' }}>
                Password
              </label>
              <div className="relative">
                <input
                  type={showPassword ? 'text' : 'password'}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  className="w-full px-4 py-3 rounded-lg focus:outline-none focus:ring-2 transition"
                  style={{ 
                    borderColor: '#E2E8F0',
                    borderWidth: '1px',
                    color: '#0F172A'
                  }}
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 transition text-lg"
                  style={{ color: '#475569' }}
                  onMouseEnter={(e) => e.target.style.color = '#1E3A8A'}
                  onMouseLeave={(e) => e.target.style.color = '#475569'}
                >
                  {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
              </div>
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              disabled={loading}
              className="w-full font-semibold py-3 rounded-lg transition mt-6 shadow-md hover:shadow-lg text-white flex items-center justify-center gap-2"
              style={{ 
                backgroundColor: loading ? '#0EA5E9' : '#1E3A8A',
                opacity: loading ? 1 : 1,
                cursor: loading ? 'not-allowed' : 'pointer'
              }}
              onMouseEnter={(e) => !loading && (e.target.style.backgroundColor = '#1D4ED8')}
              onMouseLeave={(e) => !loading && (e.target.style.backgroundColor = '#1E3A8A')}
            >
              {loading ? (
                <>
                  <span className="inline-block animate-spin w-4 h-4 border-2 border-white border-t-transparent rounded-full"></span>
                  Signing in...
                </>
              ) : (
                'Sign In'
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
