import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Alert from '../components/Alert';
import { calculateHaversineDistance } from '../utils/gpsUtils';

const StudentMarkAttendance = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  const [gpsStatus, setGpsStatus] = useState('Not Started');
  const [gpsData, setGpsData] = useState(null);
  const [gpsWatchId, setGpsWatchId] = useState(null);
  const [qrCode, setQrCode] = useState('');
  const [scanning, setScanning] = useState(false);
  const [attendanceResult, setAttendanceResult] = useState(null);
  
  // Campus location (ICT University Yaound√©)
  const CAMPUS_LAT = 3.8621;
  const CAMPUS_LON = 11.5021;
  const GEOFENCE_RADIUS = 200; // meters
  const MAX_GPS_ACCURACY = 50; // meters

  useEffect(() => {
    if (loading) return;
    
    if (!currentUser || userRole !== 'student') {
      navigate('/student');
      return;
    }
  }, [currentUser, userRole, loading, navigate]);

  if (loading) return <div>Loading...</div>;
  if (!currentUser || userRole !== 'student') return null;

  const handleQRScan = () => {
    alert('QR Code scanning feature - to be implemented');
  };

  const handleStartGPS = () => {
    if (navigator.geolocation) {
      setGpsStatus('Acquiring location...');
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setGpsStatus(`‚úÖ Ready (${position.coords.latitude.toFixed(4)}, ${position.coords.longitude.toFixed(4)})`);
        },
        (error) => {
          setGpsStatus(`‚ùå Error: ${error.message}`);
        }
      );
    } else {
      setGpsStatus('‚ùå GPS not supported');
    }
  };

  return (
    <Layout role="student">
      <div className="space-y-8">
        <div>
          <h1 className="text-3xl font-bold mb-2">Mark Attendance</h1>
          <p className="text-gray-600">Scan QR code and enable GPS to mark your attendance</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* GPS Status */}
          <Card title="üìç GPS Status">
            <div className="space-y-4">
              <div className="p-4 bg-blue-50 rounded-lg border border-blue-200">
                <p className="text-sm text-gray-600">Current Status:</p>
                <p className="text-lg font-bold text-blue-900 mt-2">{gpsStatus}</p>
              </div>
              <button
                onClick={handleStartGPS}
                className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-lg transition"
              >
                üß≠ Enable GPS
              </button>
            </div>
          </Card>

          {/* QR Scanner */}
          <Card title="üì± QR Code Scanner">
            <div className="space-y-4">
              <div className="p-8 bg-gray-100 rounded-lg border-2 border-dashed border-gray-300 text-center">
                <p className="text-4xl mb-2">üì∑</p>
                <p className="text-gray-600 mb-4">Ready to scan QR code</p>
              </div>
              <button
                onClick={handleQRScan}
                className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded-lg transition"
              >
                ‚úÖ Scan QR Code
              </button>
            </div>
          </Card>
        </div>

        {/* Instructions */}
        <Card title="üìã Instructions">
          <div className="space-y-3">
            <p className="flex items-start">
              <span className="text-2xl mr-3">1Ô∏è‚É£</span>
              <span>Enable GPS by clicking the "Enable GPS" button</span>
            </p>
            <p className="flex items-start">
              <span className="text-2xl mr-3">2Ô∏è‚É£</span>
              <span>Wait for your location to be acquired (within 50m of campus)</span>
            </p>
            <p className="flex items-start">
              <span className="text-2xl mr-3">3Ô∏è‚É£</span>
              <span>Click "Scan QR Code" when instructor displays the QR code</span>
            </p>
            <p className="flex items-start">
              <span className="text-2xl mr-3">4Ô∏è‚É£</span>
              <span>Your attendance will be marked automatically</span>
            </p>
          </div>
        </Card>

        <Alert
          type="info"
          title="‚ÑπÔ∏è GPS Privacy Notice"
          message="Your GPS location is used only for geofence verification and is not stored after attendance marking."
        />
      </div>
    </Layout>
  );
};

export default StudentMarkAttendance;
