import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Sidebar = ({ role }) => {
  const location = useLocation();
  const { logout } = useAuth();
  const [isOpen, setIsOpen] = useState(true);

  const getMenuItems = () => {
    const baseClass = role === 'admin' ? '/admin' : role === 'delegate' ? '/delegate' : '/student';

    if (role === 'admin') {
      return [
        { label: 'Dashboard', path: `${baseClass}/dashboard`, icon: 'üìä' },
        { label: 'Students', path: `${baseClass}/students`, icon: 'üë•' },
        { label: 'Delegates', path: `${baseClass}/delegates`, icon: 'üßë‚Äçüíº' },
        { label: 'Classes & Courses', path: `${baseClass}/classes`, icon: 'üìö' },
        { label: 'Attendance Sessions', path: `${baseClass}/sessions`, icon: 'üìã' },
        { label: 'Reports', path: `${baseClass}/reports`, icon: 'üìà' },
        { label: 'System Logs', path: `${baseClass}/logs`, icon: 'üìù' },
        { label: 'Settings', path: `${baseClass}/settings`, icon: '‚öôÔ∏è' },
      ];
    } else if (role === 'student') {
      return [
        { label: 'Dashboard', path: `${baseClass}/dashboard`, icon: 'üìä' },
        { label: 'Mark Attendance', path: `${baseClass}/mark`, icon: '‚úÖ' },
        { label: 'Attendance History', path: `${baseClass}/history`, icon: 'üìã' },
        { label: 'My Courses', path: `${baseClass}/courses`, icon: 'üìö' },
        { label: 'Profile', path: `${baseClass}/profile`, icon: 'üë§' },
      ];
    } else if (role === 'delegate') {
      return [
        { label: 'Dashboard', path: `${baseClass}/dashboard`, icon: 'üìä' },
        { label: 'Open Attendance Session', path: `${baseClass}/open-session`, icon: 'üîì' },
        { label: 'Live Attendance', path: `${baseClass}/live`, icon: 'üì°' },
        { label: 'Attendance Reports', path: `${baseClass}/reports`, icon: 'üìä' },
        { label: 'Profile', path: `${baseClass}/profile`, icon: 'üë§' },
      ];
    }
    return [];
  };

  const menuItems = getMenuItems();
  const isActive = (path) => location.pathname === path;

  return (
    <aside
      className={`bg-gradient-to-b from-blue-900 to-blue-800 text-white transition-all duration-300 ${
        isOpen ? 'w-64' : 'w-20'
      } flex flex-col shadow-lg`}
    >
      {/* Logo */}
      <div className="p-4 border-b border-blue-700 flex items-center justify-between">
        {isOpen && <h1 className="font-bold text-lg">Attendance</h1>}
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="p-2 hover:bg-blue-700 rounded-lg transition"
        >
          {isOpen ? '‚Üê' : '‚Üí'}
        </button>
      </div>

      {/* Navigation Menu */}
      <nav className="flex-1 overflow-y-auto py-4">
        {menuItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`flex items-center px-4 py-3 mx-2 rounded-lg transition-all ${
              isActive(item.path)
                ? 'bg-blue-500 text-white shadow-md'
                : 'text-blue-100 hover:bg-blue-700'
            }`}
            title={!isOpen ? item.label : ''}
          >
            <span className="text-xl">{item.icon}</span>
            {isOpen && <span className="ml-3 text-sm font-medium">{item.label}</span>}
          </Link>
        ))}
      </nav>

      {/* Logout Button */}
      <div className="p-4 border-t border-blue-700">
        <button
          onClick={logout}
          className={`w-full flex items-center justify-center px-4 py-2 rounded-lg bg-red-600 hover:bg-red-700 text-white transition-colors ${
            !isOpen && 'px-2'
          }`}
          title={!isOpen ? 'Logout' : ''}
        >
          <span className="text-lg">üö™</span>
          {isOpen && <span className="ml-2 text-sm font-medium">Logout</span>}
        </button>
      </div>
    </aside>
  );
};

export default Sidebar;
