      <>
        <button
          onClick={() => setShowAddSessionModal(false)}
          className="px-4 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
        >
          Cancel
        </button>
        <button
          onClick={() => {
            setSuccess('âœ… Session created successfully!');
            setShowAddSessionModal(false);
            setTimeout(() => setSuccess(null), 3000);
          }}
          className="px-4 py-2 bg-[#135bec] text-white rounded text-sm font-medium hover:bg-blue-700"
        >
          Create Session
        </button>
      </>
    }
  >
    <div className="space-y-4">
      <div>
        <label className="block text-sm font-medium text-gray-900 mb-2">Session Date *</label>
        <input
          type="date"
          value={sessionForm.session_date}
          onChange={(e) => setSessionForm(prev => ({ ...prev, session_date: e.target.value }))}
          className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
        />
      </div>
      <div>
        <label className="block text-sm font-medium text-gray-900 mb-2">Start Time *</label>
        <input
          type="time"
          value={sessionForm.start_time}
          onChange={(e) => setSessionForm(prev => ({ ...prev, start_time: e.target.value }))}
          className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
        />
      </div>
      <div>
        <label className="block text-sm font-medium text-gray-900 mb-2">End Time *</label>
        <input
          type="time"
          value={sessionForm.end_time}
          onChange={(e) => setSessionForm(prev => ({ ...prev, end_time: e.target.value }))}
          className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
        />
      </div>
      <div>
        <label className="block text-sm font-medium text-gray-900 mb-2">Venue *</label>
        <input
          type="text"
          value={sessionForm.venue}
          onChange={(e) => setSessionForm(prev => ({ ...prev, venue: e.target.value }))}
          placeholder="Enter venue"
          className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
        />
      </div>
    </div>
  </Modal>

  {/* Import CSV Modal */}
  <Modal
    isOpen={showImportCSVModal}
    title="Import CSV Data"
    onClose={() => setShowImportCSVModal(false)}
    size="md"
    footer={
      <>
        <button
          onClick={() => setShowImportCSVModal(false)}
          className="px-4 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
        >
          Cancel
        </button>
        <button
          onClick={handleImportCSV}
          className="px-4 py-2 bg-[#135bec] text-white rounded text-sm font-medium hover:bg-blue-700"
        >
          Import
        </button>
      </>
    }
  >
    <div className="space-y-4">
      <div>
        <label className="block text-sm font-medium text-gray-900 mb-2">Select CSV File *</label>
        <input
          type="file"
          accept=".csv"
          onChange={(e) => setCSVFile(e.target.files?.[0] || null)}
          className="w-full px-3 py-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-[#135bec]"
        />
      </div>
      <div className="p-3 bg-blue-50 rounded border border-blue-200">
        <p className="text-xs text-blue-800">
          <strong>CSV Format:</strong> course_code, course_title, program, year_level
        </p>
      </div>
    </div>
  </Modal>

  {/* Export Report Modal */}
  <Modal
    isOpen={showExportModal}
    title="Export Attendance Report"
    onClose={() => setShowExportModal(false)}
    size="md"
    footer={
      <>
        <button
          onClick={() => setShowExportModal(false)}
          className="px-4 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
        >
          Cancel
        </button>
        <button
          onClick={handleExportReport}
          className="px-4 py-2 bg-[#135bec] text-white rounded text-sm font-medium hover:bg-blue-700"
        >
          Export
        </button>
      </>
    }
  >
    <div className="space-y-4">
      <p className="text-sm text-gray-600">
        Export attendance data and statistics for {new Date().toLocaleDateString()}
      </p>
      <div className="p-3 bg-green-50 rounded border border-green-200">
        <p className="text-xs text-green-800">
          <strong>Report includes:</strong> Total sessions, attendance records, and system statistics
        </p>
      </div>
    </div>
  </Modal>
    </>
  );
};

export default AdminDashboard;
