import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Button from '../components/Button';
import Badge from '../components/Badge';
import Alert from '../components/Alert';
import ProgressCircle from '../components/ProgressCircle';
import Table from '../components/Table';

const StudentDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  const [activeSessions, setActiveSessions] = useState([]);
  const [sessionsLoading, setSessionsLoading] = useState(true);

  // Protect route - only allow students
  useEffect(() => {
    if (loading) {
      console.log('‚è≥ StudentDashboard: Loading auth...');
      return; // Don't redirect while loading
    }
    
    console.log('üîç StudentDashboard: Checking auth - currentUser:', !!currentUser, 'userRole:', userRole);
    
    if (!currentUser) {
      console.log('‚ùå StudentDashboard: No user, redirecting to /student');
      navigate('/student');
      return;
    }
    
    // Check if user role has been fetched yet
    if (userRole === null) {
      console.log('‚è≥ StudentDashboard: User role not yet loaded, waiting...');
      return;
    }
    
    // Only redirect if we have user data and they're NOT a student
    if (userRole !== 'student') {
      console.log('‚ùå StudentDashboard: User is not a student, redirecting to /student');
      navigate('/student');
      return;
    }
    
    console.log('‚úÖ StudentDashboard: User is student, allowing access');
    fetchActiveSessions();
  }, [currentUser, userRole, loading, navigate]);

  const fetchActiveSessions = async () => {
    try {
      setSessionsLoading(true);
      const { data, error } = await supabase
        .from('sessions')
        .select(`
          id,
          token,
          created_at,
          expires_at,
          class_id,
          classes (
            class_name
          )
        `)
        .gt('expires_at', new Date().toISOString())
        .limit(3);

      if (error) throw error;
      setActiveSessions(data || []);
    } catch (err) {
      console.error('Error fetching sessions:', err);
      setActiveSessions([]);
    } finally {
      setSessionsLoading(false);
    }
  };

  const handleMarkAttendance = () => {
    navigate('/student/mark');
  };

  const [attendancePercentage] = useState(85);
  const [todayClasses] = useState([
    { id: 1, name: 'Advanced Web Dev', time: '09:00', room: 'Lab 1' },
    { id: 2, name: 'Database Design', time: '10:30', room: 'Lab 2' },
  ]);

  const [attendanceHistory] = useState([
    { id: 1, class: 'Advanced Web Dev', date: '2025-12-18', time: '09:15', status: 'present' },
    { id: 2, class: 'Database Design', date: '2025-12-18', time: '10:35', status: 'late' },
    { id: 3, class: 'Python Programming', date: '2025-12-17', time: '14:00', status: 'present' },
    { id: 4, class: 'Mobile App Dev', date: '2025-12-17', time: '11:00', status: 'absent' },
  ]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-blue-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-16 w-16 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600 font-medium">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!currentUser) {
    console.log('üî¥ StudentDashboard: Rendering null - no currentUser');
    return <div style={{ padding: '20px', color: 'red' }}>Error: No user logged in</div>;
  }

  if (userRole !== 'student') {
    console.log('üî¥ StudentDashboard: Rendering null - userRole is:', userRole);
    return <div style={{ padding: '20px', color: 'red' }}>Error: Access denied (role: {userRole})</div>;
  }

  return (
    <Layout role="student">
      <div className="space-y-8">
        {/* Overall Attendance Progress */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          <Card className="lg:col-span-1">
            <ProgressCircle
              percentage={attendancePercentage}
              label="Overall Attendance"
              color={attendancePercentage >= 80 ? 'green' : attendancePercentage >= 70 ? 'amber' : 'red'}
            />
          </Card>

          {/* Today's Classes */}
          <Card title="üìö Today's Classes" className="lg:col-span-1">
            <div className="space-y-2">
              {todayClasses.map((cls) => (
                <div key={cls.id} className="p-3 bg-blue-50 rounded-lg border border-blue-200">
                  <p className="font-semibold text-gray-900">{cls.name}</p>
                  <p className="text-xs text-gray-600">
                    üïê {cls.time} | üìç {cls.room}
                  </p>
                </div>
              ))}
            </div>
          </Card>

          {/* Active Sessions */}
          <Card title="üü¢ Active Sessions" className="lg:col-span-1">
            {sessionsLoading ? (
              <p className="text-gray-600 text-sm">Loading...</p>
            ) : activeSessions.length > 0 ? (
              <div className="space-y-2">
                {activeSessions.slice(0, 2).map(session => (
                  <div key={session.id} className="p-2 bg-green-50 rounded border border-green-200">
                    <p className="font-semibold text-green-900 text-sm">{session.classes?.class_name || 'Class Session'}</p>
                    <p className="text-xs text-green-700">
                      üü¢ Active Now
                    </p>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-600 text-sm">No active sessions</p>
            )}
          </Card>
        </div>

        {/* Active Sessions to Mark Attendance */}
        {activeSessions.length > 0 && (
          <Card title="üìù Mark Your Attendance">
            <div className="space-y-3">
              <p className="text-gray-600 text-sm">
                {activeSessions.length} session{activeSessions.length > 1 ? 's' : ''} available for attendance marking
              </p>
              <Button 
                variant="primary"
                onClick={handleMarkAttendance}
                className="w-full"
              >
                ‚úÖ Go to Attendance Page
              </Button>
            </div>
          </Card>
        )}

        {/* Attendance Warning (if needed) */}
        {attendancePercentage < 75 && (
          <Alert
            type="warning"
            title="‚ö†Ô∏è Low Attendance Alert"
            message="Your attendance is below 75%. Please ensure you mark attendance in all sessions."
          />
        )}

        {/* Recent Attendance History */}
        <Card title="üìã Recent Attendance History">
          <Table
            columns={[
              { key: 'class', label: 'Class' },
              { key: 'date', label: 'Date' },
              { key: 'time', label: 'Time' },
              {
                key: 'status',
                label: 'Status',
                render: (status) => <Badge label={status.toUpperCase()} variant={status} />,
              },
            ]}
            data={attendanceHistory}
            searchable
            paginated
            itemsPerPage={5}
          />
        </Card>
      </div>
    </Layout>
  );
};

export default StudentDashboard;
