import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
  adminFetchStats,
  adminFetchOpenSessions,
  adminFetchCourseAttendance,
  adminFetchAttendanceAlerts,
  adminFetchAttendanceExportData
} from '../utils/dashboardQueries';
import {
  generateAttendanceCSV,
  generateAttendanceTSV,
  generateSummaryReport,
  getFormattedDateForFilename,
  isValidDateRange
} from '../utils/excelExport';
import {
  StatCard,
  SectionCard,
  StatusBadge,
  ProgressBar,
  Button,
  AlertBox,
  Table,
  EmptyState,
  LoadingSpinner,
  Modal
} from '../components/UIComponents';

const AdminDashboard = () => {
  const { currentUser, userRole, loading, logout } = useAuth();
  const navigate = useNavigate();

  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [darkMode, setDarkMode] = useState(false);
  const [activeTab, setActiveTab] = useState('overview');
  
  const [stats, setStats] = useState({
    totalStudents: 0,
    totalDelegates: 0,
    totalCourses: 0,
    totalClasses: 0,
    sessionsToday: 0,
    error: null
  });
  
  const [openSessions, setOpenSessions] = useState([]);
  const [courseAttendance, setCourseAttendance] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [loadingData, setLoadingData] = useState(true);

  // Export modal states
  const [showExportModal, setShowExportModal] = useState(false);
  const [exportFilters, setExportFilters] = useState({
    courseCode: '',
    startDate: '',
    endDate: ''
  });
  const [exportLoading, setExportLoading] = useState(false);

  useEffect(() => {
    if (!loading && (!currentUser || userRole !== 'admin')) {
      navigate('/admin');
      return;
    }
    
    if (!loading && currentUser && userRole === 'admin') {
      fetchAllDashboardData();
      const interval = setInterval(fetchAllDashboardData, 30000);
      return () => clearInterval(interval);
    }
  }, [currentUser, userRole, loading, navigate]);

  const fetchAllDashboardData = async () => {
    try {
      setLoadingData(true);
      const [statsData, sessionsData, attendanceData, alertsData] = await Promise.all([
        adminFetchStats(),
        adminFetchOpenSessions(),
        adminFetchCourseAttendance(10),
        adminFetchAttendanceAlerts()
      ]);

      setStats(statsData);
      setOpenSessions(sessionsData.sessions);
      setCourseAttendance(attendanceData.courseAttendance);
      setAlerts(alertsData.alerts);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoadingData(false);
    }
  };

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/login');
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  const handleExportData = async (format = 'csv') => {
    try {
      setExportLoading(true);

      // Validate date range
      if (exportFilters.startDate && exportFilters.endDate) {
        if (!isValidDateRange(exportFilters.startDate, exportFilters.endDate)) {
          alert('Start date must be before end date');
          setExportLoading(false);
          return;
        }
      }

      // Fetch export data
      const { data, error } = await adminFetchAttendanceExportData(exportFilters);

      if (error) {
        alert('Error fetching data: ' + error);
        setExportLoading(false);
        return;
      }

      if (!data || data.length === 0) {
        alert('No data found for the selected filters');
        setExportLoading(false);
        return;
      }

      // Generate file based on format
      const dateString = getFormattedDateForFilename();
      const filename = `attendance_${dateString}.${format === 'summary' ? 'csv' : format}`;

      if (format === 'csv') {
        generateAttendanceCSV(data, filename);
      } else if (format === 'xlsx') {
        generateAttendanceTSV(data, filename);
      } else if (format === 'summary') {
        generateSummaryReport(data, filename);
      }

      // Close modal and reset filters
      setShowExportModal(false);
      setExportFilters({
        courseCode: '',
        startDate: '',
        endDate: ''
      });

      alert(`Data exported successfully as ${filename}`);
    } catch (error) {
      console.error('Export error:', error);
      alert('Error exporting data: ' + error.message);
    } finally {
      setExportLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gradient-to-br from-gray-50 to-gray-100 dark:from-gray-950 dark:to-gray-900">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (!currentUser || userRole !== 'admin') {
    return null;
  }

  const navItems = [
    { id: 'overview', label: 'Overview', icon: 'dashboard' },
    { id: 'sessions', label: 'Sessions', icon: 'event_note' },
    { id: 'courses', label: 'Courses', icon: 'book' },
    { id: 'alerts', label: 'Alerts', icon: 'warning' },
  ];

  return (
    <div className={`flex h-screen overflow-hidden ${darkMode ? 'dark' : ''}`}>
      {/* SIDEBAR */}
      <aside className={`${sidebarOpen ? 'w-64' : 'w-20'} bg-white dark:bg-gray-900 border-r border-gray-200 dark:border-gray-800 flex flex-col transition-all duration-300 shadow-lg`}>
        <div className="p-6 border-b border-gray-200 dark:border-gray-800">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-blue-600 to-blue-700 flex items-center justify-center text-white font-bold shadow-lg">
              <span className="material-symbols-outlined">admin_panel_settings</span>
            </div>
            {sidebarOpen && (
              <div>
                <p className="font-bold text-sm text-gray-900 dark:text-white">Admin</p>
                <p className="text-xs text-gray-500 dark:text-gray-400">Control Panel</p>
              </div>
            )}
          </div>
        </div>

        <nav className="flex-1 overflow-y-auto px-3 py-6 space-y-1">
          {navItems.map((item) => (
            <button
              key={item.id}
              onClick={() => setActiveTab(item.id)}
              className={`w-full flex items-center gap-3 px-4 py-3 rounded-lg transition-all duration-200 ${
                activeTab === item.id
                  ? 'bg-blue-50 dark:bg-blue-900/30 text-blue-600 dark:text-blue-400 font-semibold'
                  : 'text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800'
              }`}
            >
              <span className="material-symbols-outlined text-xl">{item.icon}</span>
              {sidebarOpen && <span className="text-sm">{item.label}</span>}
            </button>
          ))}
        </nav>

        <div className="p-3 border-t border-gray-200 dark:border-gray-800 space-y-2">
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="w-full flex items-center gap-3 px-4 py-3 rounded-lg text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800 transition-all"
          >
            <span className="material-symbols-outlined text-xl">
              {darkMode ? 'light_mode' : 'dark_mode'}
            </span>
            {sidebarOpen && <span className="text-sm">Theme</span>}
          </button>

          <button
            onClick={handleLogout}
            className="w-full flex items-center gap-3 px-4 py-3 rounded-lg text-red-600 dark:text-red-400 hover:bg-red-50 dark:hover:bg-red-900/10 transition-all"
          >
            <span className="material-symbols-outlined text-xl">logout</span>
            {sidebarOpen && <span className="text-sm font-semibold">Logout</span>}
          </button>
        </div>

        <div className="p-3 border-t border-gray-200 dark:border-gray-800">
          <button
            onClick={() => setSidebarOpen(!sidebarOpen)}
            className="w-full flex items-center justify-center py-2 rounded-lg text-gray-500 hover:bg-gray-100 dark:hover:bg-gray-800 transition-all"
          >
            <span className="material-symbols-outlined">
              {sidebarOpen ? 'navigate_before' : 'navigate_next'}
            </span>
          </button>
        </div>
      </aside>

      {/* MAIN CONTENT */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* TOP HEADER */}
        <header className="sticky top-0 z-40 bg-white dark:bg-gray-900 border-b border-gray-200 dark:border-gray-800 shadow-sm">
          <div className="px-8 py-4 flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
                Admin Control Panel
              </h1>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                System-wide attendance management
              </p>
            </div>

            <div className="flex items-center gap-4">
              <button className="relative p-2 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors">
                <span className="material-symbols-outlined text-gray-700 dark:text-gray-300">notifications</span>
                {alerts.length > 0 && (
                  <span className="absolute top-1 right-1 w-3 h-3 bg-red-500 rounded-full animate-pulse" />
                )}
              </button>

              <div className="w-10 h-10 rounded-full bg-gradient-to-br from-blue-600 to-blue-700 flex items-center justify-center text-white font-bold cursor-pointer hover:shadow-lg transition-shadow">
                {currentUser?.email?.charAt(0).toUpperCase()}
              </div>
            </div>
          </div>
        </header>

        {/* SCROLLABLE CONTENT */}
        <main className="flex-1 overflow-y-auto bg-gradient-to-br from-gray-50 to-gray-100 dark:from-gray-950 dark:to-gray-900">
          <div className="p-8 max-w-7xl mx-auto space-y-8">
            {/* OVERVIEW TAB */}
            {activeTab === 'overview' && (
              <>
                {/* KPI CARDS */}
                <section>
                  <h2 className="text-lg font-bold text-gray-900 dark:text-white mb-6">
                     System Overview
                  </h2>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                    <StatCard label="Total Students" value={stats.totalStudents} icon="group" trend=" 12% from last month" />
                    <StatCard label="Total Delegates" value={stats.totalDelegates} icon="people" trend="Active instructors" />
                    <StatCard label="Total Courses" value={stats.totalCourses} icon="book" trend="Registered courses" />
                    <StatCard label="Sessions Today" value={stats.sessionsToday} icon="event_note" trend="Active now" trendColor="amber" />
                  </div>
                </section>

                {/* ALERTS */}
                {alerts.length > 0 && (
                  <section>
                    <h2 className="text-lg font-bold text-gray-900 dark:text-white mb-6">
                       System Alerts ({alerts.length})
                    </h2>
                    <div className="space-y-3">
                      {alerts.slice(0, 5).map((alert, idx) => (
                        <AlertBox
                          key={idx}
                          type={alert.severity === 'critical' ? 'error' : 'warning'}
                          title={alert.message}
                          message={alert.courseTitle ? `${alert.courseTitle}: ${alert.attendance}` : alert.message}
                          closable={false}
                        />
                      ))}
                    </div>
                  </section>
                )}

                {/* COURSE ATTENDANCE OVERVIEW */}
                <section>
                  <div className="flex items-center justify-between mb-6">
                    <h2 className="text-lg font-bold text-gray-900 dark:text-white">
                       Course Attendance Overview
                    </h2>
                    <Button variant="secondary" size="sm" icon="download">Export</Button>
                  </div>

                  <SectionCard>
                    {courseAttendance.length > 0 ? (
                      <div className="space-y-4">
                        {courseAttendance.slice(0, 8).map((course, idx) => (
                          <div key={idx} className="flex items-center justify-between gap-4 pb-4 border-b border-gray-200 dark:border-gray-700 last:border-0">
                            <div className="flex-1 min-w-0">
                              <p className="font-semibold text-gray-900 dark:text-white truncate">
                                {course.courseTitle}
                              </p>
                              <p className="text-xs text-gray-500 dark:text-gray-400">
                                {course.courseCode}  {course.enrolledStudents} students
                              </p>
                            </div>
                            <div className="flex items-center gap-4">
                              <div className="text-right min-w-fit">
                                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                                  {course.attendancePercentage}%
                                </p>
                                <StatusBadge status={course.status} />
                              </div>
                              <div className="w-24">
                                <ProgressBar
                                  percentage={course.attendancePercentage}
                                  status={course.status}
                                />
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <EmptyState icon="analytics" title="No Data" message="No course attendance data available" />
                    )}
                  </SectionCard>
                </section>
              </>
            )}

            {/* SESSIONS TAB */}
            {activeTab === 'sessions' && (
              <section>
                <h2 className="text-lg font-bold text-gray-900 dark:text-white mb-6">
                   Open Attendance Sessions
                </h2>

                <SectionCard>
                  {loadingData ? (
                    <div className="space-y-3">
                      {Array(3).fill(0).map((_, i) => (
                        <div key={i} className="h-12 bg-gray-200 dark:bg-gray-700 rounded animate-pulse" />
                      ))}
                    </div>
                  ) : openSessions.length > 0 ? (
                    <Table
                      columns={[
                        { label: 'Session ID', key: 'tokenPreview' },
                        { label: 'Course', key: 'className' },
                        { label: 'Delegate', key: 'delegateName' },
                        { label: 'Date', key: 'date' },
                        { label: 'Time', key: 'startTime' },
                        {
                          label: 'Status',
                          render: (row) => <StatusBadge status={row.status} />
                        }
                      ]}
                      data={openSessions}
                    />
                  ) : (
                    <EmptyState icon="event_busy" title="No Sessions" message="No active sessions at the moment" />
                  )}
                </SectionCard>
              </section>
            )}

            {/* QUICK ACTIONS */}
            {activeTab === 'overview' && (
              <section>
                <h2 className="text-lg font-bold text-gray-900 dark:text-white mb-6">
                   Quick Actions
                </h2>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                  {[
                    { icon: 'person_add', title: 'Enroll Student', color: 'blue' },
                    { icon: 'event_note', title: 'Create Session', color: 'green' },
                    { icon: 'upload_file', title: 'Import CSV', color: 'amber' },
                    { icon: 'class', title: 'Add Class', color: 'purple' },
                  ].map((action, idx) => (
                    <button
                      key={idx}
                      className="bg-white dark:bg-gray-900 rounded-xl p-6 shadow-sm border border-gray-200 dark:border-gray-800 hover:shadow-lg hover:border-blue-300 dark:hover:border-blue-700 transition-all text-left group"
                    >
                      <div className="p-3 bg-blue-100 dark:bg-blue-900/30 rounded-lg mb-4 w-fit group-hover:scale-110 transition-transform">
                        <span className="material-symbols-outlined text-blue-600 dark:text-blue-400 text-2xl">
                          {action.icon}
                        </span>
                      </div>
                      <p className="font-semibold text-gray-900 dark:text-white mb-1">
                        {action.title}
                      </p>
                      <p className="text-xs text-gray-500 dark:text-gray-400">
                        Manage system
                      </p>
                    </button>
                  ))}
                </div>

                {/* EXPORT SECTION */}
                <div className="mt-8 pt-8 border-t border-gray-200 dark:border-gray-800">
                  <h3 className="text-lg font-bold text-gray-900 dark:text-white mb-4">
                     Export Attendance Data
                  </h3>
                  <p className="text-sm text-gray-600 dark:text-gray-400 mb-4">
                    Export attendance records to Excel or CSV for analysis and reporting
                  </p>
                  <Button
                    variant="primary"
                    onClick={() => setShowExportModal(true)}
                    className="w-full md:w-auto"
                  >
                    <span className="material-symbols-outlined mr-2">download</span>
                    Export Attendance Data
                  </Button>
                </div>
              </section>
            )}
          </div>
        </main>
      </div>

      {/* Export Modal */}
      <Modal
        isOpen={showExportModal}
        title="Export Attendance Data"
        onClose={() => setShowExportModal(false)}
        size="lg"
      >
        <div className="space-y-4">
          {/* Instructions */}
          <div className="p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-800">
            <p className="text-sm text-blue-900 dark:text-blue-200">
              Export attendance records with optional filtering by course and date range.
              Choose your preferred format (CSV, Excel, or Summary Report).
            </p>
          </div>

          {/* Course Filter */}
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Filter by Course (Optional)
            </label>
            <input
              type="text"
              placeholder="Course code (e.g., CS101)"
              value={exportFilters.courseCode}
              onChange={(e) =>
                setExportFilters({ ...exportFilters, courseCode: e.target.value })
              }
              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* Date Range Filter */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                Start Date (Optional)
              </label>
              <input
                type="date"
                value={exportFilters.startDate}
                onChange={(e) =>
                  setExportFilters({ ...exportFilters, startDate: e.target.value })
                }
                className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                End Date (Optional)
              </label>
              <input
                type="date"
                value={exportFilters.endDate}
                onChange={(e) =>
                  setExportFilters({ ...exportFilters, endDate: e.target.value })
                }
                className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500"
              />
            </div>
          </div>

          {/* Export Format Selection */}
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
              Export Format
            </label>
            <div className="space-y-2">
              <button
                onClick={() => handleExportData('csv')}
                disabled={exportLoading}
                className="w-full px-4 py-3 text-left rounded-lg border-2 border-blue-300 dark:border-blue-700 bg-blue-50 dark:bg-blue-900/20 hover:bg-blue-100 dark:hover:bg-blue-900/30 transition-colors disabled:opacity-50"
              >
                <p className="font-medium text-gray-900 dark:text-white">
                  CSV Format
                </p>
                <p className="text-xs text-gray-600 dark:text-gray-400">
                  Standard comma-separated values  Universal compatibility
                </p>
              </button>

              <button
                onClick={() => handleExportData('xlsx')}
                disabled={exportLoading}
                className="w-full px-4 py-3 text-left rounded-lg border-2 border-green-300 dark:border-green-700 bg-green-50 dark:bg-green-900/20 hover:bg-green-100 dark:hover:bg-green-900/30 transition-colors disabled:opacity-50"
              >
                <p className="font-medium text-gray-900 dark:text-white">
                  Excel Format
                </p>
                <p className="text-xs text-gray-600 dark:text-gray-400">
                  Direct Excel compatibility  Full formatting preserved
                </p>
              </button>

              <button
                onClick={() => handleExportData('summary')}
                disabled={exportLoading}
                className="w-full px-4 py-3 text-left rounded-lg border-2 border-purple-300 dark:border-purple-700 bg-purple-50 dark:bg-purple-900/20 hover:bg-purple-100 dark:hover:bg-purple-900/30 transition-colors disabled:opacity-50"
              >
                <p className="font-medium text-gray-900 dark:text-white">
                  Summary Report
                </p>
                <p className="text-xs text-gray-600 dark:text-gray-400">
                  Course & student statistics  Aggregate attendance percentages
                </p>
              </button>
            </div>
          </div>

          {/* Loading State */}
          {exportLoading && (
            <div className="text-center py-4">
              <p className="text-sm text-gray-600 dark:text-gray-400">
                Generating export file...
              </p>
            </div>
          )}

          {/* Close Button */}
          <div className="flex justify-end gap-2 pt-4">
            <Button
              variant="outline"
              onClick={() => setShowExportModal(false)}
              disabled={exportLoading}
            >
              Cancel
            </Button>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default AdminDashboard;
