import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import StatCard from '../components/StatCard';
import Card from '../components/Card';
import Table from '../components/Table';
import Badge from '../components/Badge';
import { BarChart } from '../components/Chart';

const AdminDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();

  // Redirect if not an admin
  useEffect(() => {
    if (loading) {
      console.log('â³ AdminDashboard: Loading auth...');
      return; // Don't redirect while loading
    }
    
    console.log('ðŸ” AdminDashboard: Checking auth - currentUser:', !!currentUser, 'userRole:', userRole);
    
    if (!currentUser) {
      console.log('âŒ AdminDashboard: No user, redirecting to /admin');
      navigate('/admin');
      return;
    }
    
    // Only redirect if we have user data and they're NOT an admin
    if (userRole && userRole !== 'admin') {
      console.log('âŒ AdminDashboard: User is not admin, redirecting to /admin');
      navigate('/admin');
      return;
    }
    
    console.log('âœ… AdminDashboard: User is admin, allowing access');
  }, [userRole, loading, currentUser, navigate]);

  const [stats, setStats] = useState({
    totalStudents: 0,
    totalDelegates: 0,
    totalClasses: 0,
    sessionsToday: 0,
  });

  const [recentSessions, setRecentSessions] = useState([
    { id: 1, class: 'Advanced Web Dev', date: '2025-12-18', time: '09:00', status: 'closed', attendance: '85%' },
    { id: 2, class: 'Database Design', date: '2025-12-18', time: '10:30', status: 'open', attendance: '92%' },
    { id: 3, class: 'Python Programming', date: '2025-12-17', time: '14:00', status: 'closed', attendance: '78%' },
  ]);

  const [attendanceData] = useState([
    { label: 'Advanced Web Dev', value: 85 },
    { label: 'Database Design', value: 92 },
    { label: 'Python Programming', value: 78 },
    { label: 'Mobile App Dev', value: 88 },
  ]);

  useEffect(() => {
    if (currentUser && userRole === 'admin') {
      fetchStats();
    }
  }, [currentUser, userRole]);

  const fetchStats = async () => {
    try {
      const [studentsRes, delegatesRes, classesRes] = await Promise.all([
        supabase.from('users').select('id', { count: 'exact' }).eq('role', 'student'),
        supabase.from('users').select('id', { count: 'exact' }).eq('role', 'delegate'),
        supabase.from('classes').select('id', { count: 'exact' }),
      ]);

      setStats({
        totalStudents: studentsRes.count || 0,
        totalDelegates: delegatesRes.count || 0,
        totalClasses: classesRes.count || 0,
        sessionsToday: 2,
      });
    } catch (err) {
      console.error('Error fetching stats:', err);
    }
  };

  const getStatusBadge = (status) => {
    const colors = {
      open: 'bg-green-100 text-green-800',
      closed: 'bg-gray-100 text-gray-800',
      locked: 'bg-red-100 text-red-800',
    };
    return colors[status] || colors.closed;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-blue-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-16 w-16 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600 font-medium">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!currentUser) {
    console.log('ðŸ”´ AdminDashboard: Rendering null - no currentUser');
    return <div style={{ padding: '20px', color: 'red' }}>Error: No user logged in</div>;
  }

  if (userRole !== 'admin') {
    console.log('ðŸ”´ AdminDashboard: Rendering null - userRole is:', userRole);
    return <div style={{ padding: '20px', color: 'red' }}>Error: Access denied (role: {userRole})</div>;
  }

  return (
    <Layout role="admin">
      <div className="space-y-8">
        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <StatCard title="Total Students" value={stats.totalStudents} icon="ðŸ‘¥" color="blue" />
          <StatCard title="Total Delegates" value={stats.totalDelegates} icon="ðŸ§‘â€ðŸ’¼" color="green" />
          <StatCard title="Total Classes" value={stats.totalClasses} icon="ðŸ“š" color="amber" />
          <StatCard title="Sessions Today" value={stats.sessionsToday} icon="ðŸ“‹" color="green" />
        </div>

        {/* Attendance Chart */}
        <Card title="ðŸ“Š Attendance Overview">
          <BarChart data={attendanceData} />
        </Card>

        {/* Recent Sessions Table */}
        <Card title="ðŸ“‹ Recent Attendance Sessions">
          <Table
            columns={[
              { key: 'id', label: 'ID' },
              { key: 'class', label: 'Class' },
              { key: 'date', label: 'Date' },
              { key: 'time', label: 'Time' },
              {
                key: 'status',
                label: 'Status',
                render: (status) => <Badge label={status.toUpperCase()} variant={status} />,
              },
              { key: 'attendance', label: 'Attendance' },
            ]}
            data={recentSessions}
            searchable
            paginated
            itemsPerPage={5}
          />
        </Card>
      </div>
    </Layout>
  );
};

export default AdminDashboard;
