import React, { createContext, useContext, useEffect, useState } from 'react';
import { supabase } from '../supabase/supabase';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [userRole, setUserRole] = useState(null); // 'student', 'delegate', 'admin'
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Helper function to fetch and update user data
  const updateUserFromDatabase = async (userId) => {
    try {
      console.log('ðŸ“± AuthContext: Fetching user data from database for ID:', userId);
      const { data: userData, error: userError } = await supabase
        .from('users')
        .select('*')
        .eq('id', userId)
        .single();
      
      if (userError) {
        console.warn('âš ï¸ AuthContext: User data fetch failed:', userError.message);
        return null;
      }
      
      console.log('âœ… AuthContext: User data retrieved - role:', userData?.role, 'name:', userData?.name);
      return userData;
    } catch (err) {
      console.error('âŒ AuthContext: Error fetching user data:', err.message);
      return null;
    }
  };

  useEffect(() => {
    // Check for existing session on mount
    let unsubscribed = false;
    let timeoutId;

    const initAuth = async () => {
      try {
        console.log('ðŸ“± AuthContext: Initializing auth on mount...');
        
        // Set a timeout to prevent hanging
        const timeoutPromise = new Promise((_, reject) => {
          timeoutId = setTimeout(() => reject(new Error('Session retrieval timeout')), 5000);
        });

        try {
          const { data: { session }, error: sessionError } = await Promise.race([
            supabase.auth.getSession(),
            timeoutPromise
          ]);
          
          clearTimeout(timeoutId);

          if (sessionError) {
            console.warn('âš ï¸ AuthContext: Session retrieval error:', sessionError.message);
          }

          if (session?.user && !unsubscribed) {
            console.log('ðŸ“± AuthContext: Existing session found for user:', session.user.email);
            const userData = await updateUserFromDatabase(session.user.id);
            
            if (!unsubscribed) {
              setCurrentUser({ ...session.user, ...(userData || {}) });
              setUserRole(userData?.role || null);
            }
          } else if (!unsubscribed) {
            console.log('ðŸ“± AuthContext: No existing session found');
            setCurrentUser(null);
            setUserRole(null);
          }
        } catch (timeoutErr) {
          console.warn('âš ï¸ AuthContext: Session check timed out, proceeding without session');
          if (!unsubscribed) {
            setCurrentUser(null);
            setUserRole(null);
          }
        }
      } catch (err) {
        console.error('âŒ AuthContext: Auth initialization error:', err.message);
        setError(err.message);
        if (!unsubscribed) {
          setCurrentUser(null);
          setUserRole(null);
        }
      } finally {
        if (!unsubscribed) {
          console.log('âœ… AuthContext: Loading complete');
          setLoading(false);
        }
      }
    };

    initAuth();

    // Subscribe to auth changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange(async (event, session) => {
      if (unsubscribed) return;
      
      console.log('ðŸ“± AuthContext: Auth state changed - Event type:', event, '| Session exists:', !!session?.user);
      
      try {
        if (session?.user) {
          console.log('ðŸ“± AuthContext: Processing auth state change for user:', session.user.email);
          const userData = await updateUserFromDatabase(session.user.id);
          
          if (!unsubscribed) {
            setCurrentUser({ ...session.user, ...(userData || {}) });
            setUserRole(userData?.role || null);
            console.log('âœ… AuthContext: User state updated - Role:', userData?.role);
          }
        } else {
          console.log('ðŸ“± AuthContext: Auth state change - no session, clearing user');
          if (!unsubscribed) {
            setCurrentUser(null);
            setUserRole(null);
          }
        }
      } catch (err) {
        console.error('âŒ AuthContext: Error processing auth state change:', err.message);
        setError(err.message);
      }
    });

    return () => {
      unsubscribed = true;
      clearTimeout(timeoutId);
      if (subscription) {
        subscription.unsubscribe();
      }
    };
  }, []);

  const login = async (email, password) => {
    try {
      console.log('ðŸ“± AuthContext.login: Starting login process for:', email);
      setLoading(true);
      setError(null);
      
      const { data, error } = await supabase.auth.signInWithPassword({ email, password });
      
      if (error) {
        console.error('âŒ AuthContext.login: Supabase auth error:', error.message);
        throw error;
      }
      
      console.log('âœ… AuthContext.login: Auth successful for user:', data.user?.email);
      
      // Wait a bit for the auth state subscription to fire and update context
      console.log('ðŸ“± AuthContext.login: Waiting for auth state to propagate...');
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Manually ensure user data is fetched and set
      if (data.user?.id) {
        console.log('ðŸ“± AuthContext.login: Fetching user data from database...');
        const userData = await updateUserFromDatabase(data.user.id);
        setCurrentUser({ ...data.user, ...(userData || {}) });
        setUserRole(userData?.role || null);
        console.log('âœ… AuthContext.login: Context updated with user data - role:', userData?.role);
      }
      
      setLoading(false);
      return data.user;
    } catch (err) {
      console.error('âŒ AuthContext.login: Exception during login:', err.message);
      setError(err.message);
      setLoading(false);
      throw err;
    }
  };

  const logout = async () => {
    const { error } = await supabase.auth.signOut();
    if (error) throw error;
  };

  const value = {
    currentUser,
    userRole,
    login,
    logout,
    loading,
    error,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
