import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Alert from '../components/Alert';
import { calculateHaversineDistance } from '../utils/gpsUtils';

const StudentMarkAttendance = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  const [gpsStatus, setGpsStatus] = useState('Not Started');
  const [gpsData, setGpsData] = useState(null);
  const [gpsWatchId, setGpsWatchId] = useState(null);
  const [qrCode, setQrCode] = useState('');
  const [scanning, setScanning] = useState(false);
  const [attendanceResult, setAttendanceResult] = useState(null);
  
  // Campus location (ICT University Yaound√©)
  const CAMPUS_LAT = 3.8621;
  const CAMPUS_LON = 11.5021;
  const GEOFENCE_RADIUS = 200; // meters
  const MAX_GPS_ACCURACY = 50; // meters

  useEffect(() => {
    if (loading) return;
    
    if (!currentUser || userRole !== 'student') {
      navigate('/student');
      return;
    }
  }, [currentUser, userRole, loading, navigate]);

  if (loading) return <div>Loading...</div>;
  if (!currentUser || userRole !== 'student') return null;

  const handleQRScan = () => {
    setScanning(true);
    // In a real app, this would use react-qr-scanner library
    const scannedCode = prompt('üì± Enter or scan QR code (for demo):');
    if (scannedCode) {
      setQrCode(scannedCode);
      validateAttendance(scannedCode);
    }
    setScanning(false);
  };

  const handleStartGPS = () => {
    if (navigator.geolocation) {
      setGpsStatus('üß≠ Acquiring location...');
      
      // Stop previous watch if exists
      if (gpsWatchId) {
        navigator.geolocation.clearWatch(gpsWatchId);
      }

      const watchId = navigator.geolocation.watchPosition(
        (position) => {
          const { latitude, longitude, accuracy } = position.coords;
          setGpsData({ latitude, longitude, accuracy });
          
          // Calculate distance to campus
          const distance = calculateHaversineDistance(latitude, longitude, CAMPUS_LAT, CAMPUS_LON);
          
          // Validate GPS
          let status = '‚úÖ ';
          if (accuracy > MAX_GPS_ACCURACY) {
            status = '‚ö†Ô∏è ';
          }
          if (distance > GEOFENCE_RADIUS) {
            status = '‚ùå ';
          }
          
          status += `GPS OK | Distance: ${distance.toFixed(0)}m | Accuracy: ${accuracy.toFixed(0)}m`;
          setGpsStatus(status);
        },
        (error) => {
          setGpsStatus(`‚ùå Error: ${error.message}`);
        },
        {
          enableHighAccuracy: true,
          maximumAge: 0,
          timeout: 10000,
        }
      );
      
      setGpsWatchId(watchId);
    } else {
      setGpsStatus('‚ùå GPS not supported on your device');
    }
  };

  const handleStopGPS = () => {
    if (gpsWatchId) {
      navigator.geolocation.clearWatch(gpsWatchId);
      setGpsWatchId(null);
    }
    setGpsStatus('Stopped');
    setGpsData(null);
  };

  const validateAttendance = (qrCode) => {
    if (!gpsData) {
      setAttendanceResult({
        success: false,
        message: '‚ùå GPS data not available. Enable GPS first.',
      });
      return;
    }

    const distance = calculateHaversineDistance(
      gpsData.latitude,
      gpsData.longitude,
      CAMPUS_LAT,
      CAMPUS_LON
    );

    // Validate GPS accuracy
    if (gpsData.accuracy > MAX_GPS_ACCURACY) {
      setAttendanceResult({
        success: false,
        message: `‚ùå GPS signal too weak. Accuracy: ${gpsData.accuracy.toFixed(0)}m (max: ${MAX_GPS_ACCURACY}m). Move to open area.`,
      });
      return;
    }

    // Validate geofence
    if (distance > GEOFENCE_RADIUS) {
      setAttendanceResult({
        success: false,
        message: `‚ùå Outside campus geofence. Distance: ${distance.toFixed(0)}m from center. Max: ${GEOFENCE_RADIUS}m`,
      });
      return;
    }

    // Simulate recording attendance
    console.log('üìù Recording attendance with GPS data:', {
      qrCode,
      latitude: gpsData.latitude,
      longitude: gpsData.longitude,
      accuracy: gpsData.accuracy,
      distance,
    });

    setAttendanceResult({
      success: true,
      message: `‚úÖ Attendance Marked!\nDistance: ${distance.toFixed(0)}m | Accuracy: ${gpsData.accuracy.toFixed(0)}m`,
    });
  };

  if (loading) return <div>Loading...</div>;
  if (!currentUser || userRole !== 'student') return null;

  return (
    <Layout role="student">
      <div className="space-y-8">
        <div>
          <h1 className="text-3xl font-bold mb-2">Mark Attendance</h1>
          <p className="text-gray-600">Scan QR code and enable GPS to mark your attendance</p>
        </div>

        {attendanceResult && (
          <Alert
            type={attendanceResult.success ? 'success' : 'error'}
            title={attendanceResult.success ? '‚úÖ Success' : '‚ùå Failed'}
            message={attendanceResult.message}
          />
        )}

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* GPS Status */}
          <Card title="üìç GPS Status">
            <div className="space-y-4">
              <div className="p-4 bg-blue-50 rounded-lg border border-blue-200">
                <p className="text-sm text-gray-600">Current Status:</p>
                <p className="text-lg font-bold text-blue-900 mt-2">{gpsStatus}</p>
                {gpsData && (
                  <div className="text-xs text-gray-600 mt-3 space-y-1">
                    <p>üìå Latitude: {gpsData.latitude.toFixed(6)}¬∞</p>
                    <p>üìå Longitude: {gpsData.longitude.toFixed(6)}¬∞</p>
                    <p>üìè Accuracy: {gpsData.accuracy.toFixed(1)}m</p>
                  </div>
                )}
              </div>
              <div className="flex gap-2">
                <button
                  onClick={handleStartGPS}
                  disabled={gpsWatchId !== null}
                  className="flex-1 bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 text-white font-semibold py-2 px-4 rounded-lg transition"
                >
                  üß≠ {gpsWatchId ? 'GPS Active' : 'Enable GPS'}
                </button>
                {gpsWatchId && (
                  <button
                    onClick={handleStopGPS}
                    className="flex-1 bg-red-600 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded-lg transition"
                  >
                    ‚èπÔ∏è Stop GPS
                  </button>
                )}
              </div>
            </div>
          </Card>

          {/* QR Scanner */}
          <Card title="üì± QR Code Scanner">
            <div className="space-y-4">
              <div className="p-8 bg-gray-100 rounded-lg border-2 border-dashed border-gray-300 text-center">
                <p className="text-4xl mb-2">üì∑</p>
                <p className="text-gray-600 mb-2">Ready to scan QR code</p>
                {qrCode && <p className="text-sm text-green-600 font-mono">Code: {qrCode}</p>}
              </div>
              <button
                onClick={handleQRScan}
                disabled={scanning || !gpsData}
                className="w-full bg-green-600 hover:bg-green-700 disabled:bg-gray-400 text-white font-semibold py-2 px-4 rounded-lg transition"
              >
