import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabase/supabase';
import { useAuth } from '../contexts/AuthContext';
import Layout from '../components/Layout';
import Card from '../components/Card';
import Button from '../components/Button';
import Alert from '../components/Alert';

const StudentProfile = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  
  const [profileData, setProfileData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    matricule: ''
  });

  // Protect route - only allow students
  useEffect(() => {
    if (loading) return;

    if (!currentUser || userRole !== 'student') {
      navigate('/student');
      return;
    }

    fetchStudentProfile();
  }, [currentUser, userRole, loading, navigate]);

  const fetchStudentProfile = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const { data, error: fetchError } = await supabase
        .from('users')
        .select('id, name, email, matricule, role')
        .eq('id', currentUser.id)
        .single();

      if (fetchError) throw fetchError;

      setProfileData(data);
      setFormData({
        name: data.name,
        email: data.email,
        matricule: data.matricule
      });
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSaveProfile = async () => {
    try {
      setError(null);

      const { error: updateError } = await supabase
        .from('users')
        .update({
          name: formData.name,
          email: formData.email
        })
        .eq('id', currentUser.id);

      if (updateError) throw updateError;

      setProfileData(prev => ({
        ...prev,
        name: formData.name,
        email: formData.email
      }));
      setIsEditing(false);
    } catch (err) {
      setError(err.message);
    }
  };

  if (isLoading) {
    return (
      <Layout role="student">
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '400px' }}>
          <p>Loading profile...</p>
        </div>
      </Layout>
    );
  }

  return (
    <Layout role="student">
      <div style={{ maxWidth: '600px', margin: '0 auto' }}>
        <h1>Student Profile</h1>

        {error && <Alert type="error" message={error} />}

        {profileData && (
          <Card>
            <div style={{ padding: '20px' }}>
              {!isEditing ? (
                <div>
                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Name
                    </label>
                    <p style={{ margin: 0, fontSize: '16px', fontWeight: '500' }}>
                      {profileData.name}
                    </p>
                  </div>

                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Matricule
                    </label>
                    <p style={{ margin: 0, fontSize: '16px', fontWeight: '500' }}>
                      {profileData.matricule}
                    </p>
                  </div>

                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Email
                    </label>
                    <p style={{ margin: 0, fontSize: '16px', fontWeight: '500' }}>
                      {profileData.email}
                    </p>
                  </div>

                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Role
                    </label>
                    <p style={{ margin: 0, fontSize: '16px', fontWeight: '500', textTransform: 'capitalize' }}>
                      {profileData.role}
                    </p>
                  </div>

                  <Button 
                    onClick={() => setIsEditing(true)}
                    style={{ marginTop: '20px' }}
                  >
                    Edit Profile
                  </Button>
                </div>
              ) : (
                <div>
                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Name
                    </label>
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      style={{
                        width: '100%',
                        padding: '8px',
                        border: '1px solid #ddd',
                        borderRadius: '4px',
                        fontSize: '14px',
                        boxSizing: 'border-box'
                      }}
                    />
                  </div>

                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Email
                    </label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      style={{
                        width: '100%',
                        padding: '8px',
                        border: '1px solid #ddd',
                        borderRadius: '4px',
                        fontSize: '14px',
                        boxSizing: 'border-box'
                      }}
                    />
                  </div>

                  <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontSize: '12px', color: '#666', marginBottom: '5px' }}>
                      Matricule (Read-only)
                    </label>
                    <p style={{ margin: 0, padding: '8px', backgroundColor: '#f5f5f5', borderRadius: '4px' }}>
                      {formData.matricule}
                    </p>
                  </div>

                  <div style={{ display: 'flex', gap: '10px', marginTop: '20px' }}>
                    <Button onClick={handleSaveProfile} style={{ flex: 1 }}>
                      Save Changes
                    </Button>
                    <Button 
                      onClick={() => {
                        setIsEditing(false);
                        setFormData({
                          name: profileData.name,
                          email: profileData.email,
                          matricule: profileData.matricule
                        });
                      }}
                      style={{ flex: 1, backgroundColor: '#6c757d' }}
                    >
                      Cancel
                    </Button>
                  </div>
                </div>
              )}
            </div>
          </Card>
        )}
      </div>
    </Layout>
  );
};

export default StudentProfile;
