import Card from '../components/Card';
import Button from '../components/Button';
import Badge from '../components/Badge';
import Alert from '../components/Alert';
import ProgressCircle from '../components/ProgressCircle';
import Table from '../components/Table';

const StudentDashboard = () => {
  const { currentUser, userRole, loading } = useAuth();
  const navigate = useNavigate();
  const [activeSessions, setActiveSessions] = useState([]);
  const [sessionsLoading, setSessionsLoading] = useState(true);
  const [courseAttendance, setCourseAttendance] = useState([]);
  const [attendanceLoading, setAttendanceLoading] = useState(true);
  const [myCourses, setMyCourses] = useState([]);
  const [coursesLoading, setCoursesLoading] = useState(true);
  const [attendanceHistory, setAttendanceHistory] = useState([]);
  const [historyLoading, setHistoryLoading] = useState(true);

  // Protect route - only allow students
  useEffect(() => {
    if (loading) {
      console.log('‚è≥ StudentDashboard: Loading auth...');
      return; // Don't redirect while loading
    }

    console.log('üîç StudentDashboard: Checking auth - currentUser:', !!currentUser, 'userRole:', userRole);

    if (!currentUser || userRole === null) {
      console.log('‚ùå StudentDashboard: No user or role, redirecting to /student');
      navigate('/student');
      return;
    }

    // Only redirect if we have user data and they're NOT a student
    if (userRole !== 'student') {
      console.log('‚ùå StudentDashboard: User is not a student, redirecting to /student');
      navigate('/student');
      return;
    }

    console.log('‚úÖ StudentDashboard: User is student, allowing access');
    fetchMyCourses();
    fetchActiveSessions();
    fetchCourseAttendance();
    fetchAttendanceHistory();
  }, [currentUser, userRole, loading, navigate]);

  const fetchMyCourses = async () => {
    try {
      setCoursesLoading(true);
      console.log('üìö Fetching my courses...');

      // Get student's class_id
      const { data: userData, error: userError } = await supabase
        .from('users')
        .select('class_id')
        .eq('id', currentUser.id)
        .single();

      if (userError) throw userError;

      if (!userData.class_id) {
        setMyCourses([]);
        return;
      }

      // Fetch class details with delegate
      const { data: classData, error: classError } = await supabase
        .from('classes')
        .select(`
          id,
          class_name,
          users!classes_delegate_id_fkey (
            name
          )
        `)
        .eq('id', userData.class_id)
        .single();

      if (classError) throw classError;

      // For now, assume one course per class
      const courses = [{
        id: classData.id,
        name: classData.class_name,
        code: classData.class_name.replace(/\s+/g, '').toUpperCase(),
        delegate: classData.users?.name || 'Unknown'
      }];

      console.log('‚úÖ My courses retrieved:', courses.length);
      setMyCourses(courses);
    } catch (err) {
      console.error('Error fetching my courses:', err);
      setMyCourses([]);
    } finally {
      setCoursesLoading(false);
    }
  };

  const fetchCourseAttendance = async () => {
    try {
      setAttendanceLoading(true);
      console.log('üìä Fetching course attendance...');

      // Get student's class
      const { data: userData } = await supabase
        .from('users')
        .select('class_id')
        .eq('id', currentUser.id)
        .single();

      if (!userData?.class_id) {
        setCourseAttendance([]);
        return;
      }

      // Fetch all sessions for this class
      const { data: sessions, error: sessionsError } = await supabase
        .from('sessions')
        .select('id, class_id')
        .eq('class_id', userData.class_id);

      if (sessionsError) throw sessionsError;

      // Count total sessions and attended
      const totalSessions = sessions?.length || 0;
      let attendedSessions = 0;

      if (sessions && sessions.length > 0) {
        for (const session of sessions) {
          const { data: attendance } = await supabase
            .from('attendance')
            .select('id')
            .eq('session_id', session.id)
            .eq('student_id', currentUser.id)
            .single();

          if (attendance) attendedSessions++;
        }
      }

      const percentage = totalSessions > 0 ? Math.round((attendedSessions / totalSessions) * 100) : 0;

      const attendanceData = [{
        id: userData.class_id,
        courseCode: myCourses[0]?.code || 'COURSE',
        courseName: myCourses[0]?.name || 'My Course',
        totalSessions,
        attendedSessions,
        percentage
      }];

      console.log('‚úÖ Course attendance retrieved:', attendanceData.length, 'courses');
      setCourseAttendance(attendanceData);
    } catch (err) {
      console.error('Error fetching course attendance:', err);
      setCourseAttendance([]);
    } finally {
      setAttendanceLoading(false);
    }
  };

  const fetchActiveSessions = async () => {
    try {
      setSessionsLoading(true);

      // Get student's class
      const { data: userData } = await supabase
        .from('users')
        .select('class_id')
        .eq('id', currentUser.id)
        .single();

      if (!userData?.class_id) {
        setActiveSessions([]);
        return;
      }

      const { data, error } = await supabase
        .from('sessions')
        .select(`
          id,
          token,
          created_at,
          expires_at,
          class_id,
          classes (
            class_name
          )
        `)
        .eq('class_id', userData.class_id)
        .gt('expires_at', new Date().toISOString());

      if (error) throw error;
      setActiveSessions(data || []);
    } catch (err) {
      console.error('Error fetching sessions:', err);
      setActiveSessions([]);
    } finally {
      setSessionsLoading(false);
    }
  };

  const fetchAttendanceHistory = async () => {
    try {
      setHistoryLoading(true);
      console.log('üìã Fetching attendance history...');

      const { data, error } = await supabase
        .from('attendance')
        .select(`
          id,
          timestamp,
          sessions (
            classes (
              class_name
            )
          )
        `)
        .eq('student_id', currentUser.id)
        .order('timestamp', { ascending: false });

      if (error) throw error;

      const history = data.map(record => ({
        id: record.id,
        class: record.sessions?.classes?.class_name || 'Unknown Class',
        date: new Date(record.timestamp).toLocaleDateString(),
        time: new Date(record.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
        status: 'present' // For now, assume present; could add late logic
      }));

      console.log('‚úÖ Attendance history retrieved:', history.length, 'records');
      setAttendanceHistory(history);
    } catch (err) {
      console.error('Error fetching attendance history:', err);
      setAttendanceHistory([]);
    } finally {
      setHistoryLoading(false);
    }
  };

  const handleMarkAttendance = useCallback((sessionId) => {
    navigate('/student/mark', { state: { sessionId } });
  }, [navigate]);

  const [todayClasses] = useState([
    { id: 1, name: 'Advanced Web Dev', time: '09:00', room: 'Lab 1' },
    { id: 2, name: 'Database Design', time: '10:30', room: 'Lab 2' },
  ]);

  // Memoize table columns to prevent unnecessary re-renders
  const tableColumns = useMemo(() => [
    { key: 'class', label: 'Course' },
    { key: 'date', label: 'Date' },
    { key: 'time', label: 'Time' },
    {
      key: 'status',
      label: 'Status',
      render: (status) => <Badge label={status.toUpperCase()} variant={status} />,
    },
  ], []);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-blue-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-16 w-16 border-4 border-blue-200 border-t-blue-600 mb-4" />
          <p className="text-gray-600 font-medium">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!currentUser) {
    console.log('üî¥ StudentDashboard: Rendering null - no currentUser');
    return <div style={{ padding: '20px', color: 'red' }}>Error: No user logged in</div>;
  }

  if (userRole !== 'student') {
    console.log('üî¥ StudentDashboard: Rendering null - userRole is:', userRole);
    return <div style={{ padding: '20px', color: 'red' }}>Error: Access denied (role: {userRole})</div>;
  }

  return (
    <Layout role="student">
      <div className="space-y-8">
        {/* My Courses */}
        <Card title="üìö My Courses">
          {coursesLoading ? (
            <p className="text-gray-600">Loading courses...</p>
          ) : myCourses.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {myCourses.map((course) => (
                <div key={course.id} className="p-4 bg-blue-50 rounded-lg border border-blue-200">
                  <h3 className="font-semibold text-gray-900">{course.name}</h3>
                  <p className="text-sm text-gray-600">Code: {course.code}</p>
                  <p className="text-sm text-gray-600">Delegate: {course.delegate}</p>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600">No courses enrolled</p>
          )}
        </Card>

        {/* Attendance Summary */}
        <div>
          <h2 className="text-2xl font-bold mb-6 text-gray-900">üìä Attendance Summary</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {attendanceLoading ? (
              <p className="text-gray-600">Loading attendance data...</p>
            ) : courseAttendance.length > 0 ? (
              courseAttendance.map((course) => (
                <Card key={course.id} title={`${course.courseCode}`}>
                  <div className="space-y-3">
                    <ProgressCircle
                      percentage={course.percentage}
                      label={course.courseName}
                      color={course.percentage >= 80 ? 'green' : course.percentage >= 70 ? 'amber' : 'red'}
                    />
                    <p className="text-sm text-gray-600 text-center pt-2">
                      {course.attendedSessions}/{course.totalSessions} sessions
                    </p>
                    {course.percentage < 75 && (
                      <p className="text-xs text-red-600 text-center">‚ö†Ô∏è Low attendance</p>
                    )}
                  </div>
                </Card>
              ))
            ) : (
              <div className="col-span-1 md:col-span-2 lg:col-span-3">
                <p className="text-gray-600">No attendance data available</p>
              </div>
            )}
          </div>
        </div>

        {/* Today's Classes and Active Sessions */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card title="üìÖ Today's Classes" className="lg:col-span-1">
            <div className="space-y-2">
              {todayClasses.map((cls) => (
                <div key={cls.id} className="p-3 bg-blue-50 rounded-lg border border-blue-200">
                  <p className="font-semibold text-gray-900">{cls.name}</p>
                  <p className="text-xs text-gray-600">
                    üïê {cls.time} | üìç {cls.room}
                  </p>
                </div>
              ))}
            </div>
          </Card>

          {/* Active Sessions */}
          <Card title="üü¢ Active Attendance Sessions" className="lg:col-span-1">
            {sessionsLoading ? (
              <p className="text-gray-600 text-sm">Loading...</p>
            ) : activeSessions.length > 0 ? (
              <div className="space-y-2">
                {activeSessions.map(session => (
                  <div key={session.id} className="p-2 bg-green-50 rounded border border-green-200">
                    <p className="font-semibold text-green-900 text-sm">{session.classes?.class_name || 'Class Session'}</p>
                    <p className="text-xs text-green-700">üü¢ Active Now</p>
                    <Button
                      variant="primary"
                      size="sm"
                      onClick={() => handleMarkAttendance(session.id)}
                      className="mt-2 w-full"
                    >
                      Mark Attendance
                    </Button>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-600 text-sm">No active sessions for your courses</p>
            )}
          </Card>
        </div>

        {/* Attendance Warning */}
        {courseAttendance.some(c => c.percentage < 75) && (
          <Alert
            type="warning"
            title="‚ö†Ô∏è Low Attendance Alert"
            message="Your attendance in some courses is below 75%. Please ensure you mark attendance in all sessions."
          />
        )}

        {/* Attendance History */}
        <Card title="üìã Attendance History">
          {historyLoading ? (
            <p className="text-gray-600">Loading history...</p>
          ) : (
            <Table
              columns={tableColumns}
              data={attendanceHistory}
              searchable
              paginated
              itemsPerPage={10}
            />
          )}
        </Card>

        {/* Profile */}
        <Card title="üë§ Profile">
          <div className="space-y-2">
            <p><strong>Name:</strong> {currentUser.user_metadata?.name || 'N/A'}</p>
            <p><strong>Email:</strong> {currentUser.email}</p>
            <p><strong>Matricule:</strong> {currentUser.user_metadata?.matricule || 'N/A'}</p>
            <p><strong>Role:</strong> {userRole}</p>
          </div>
        </Card>
      </div>
    </Layout>
  );
};

export default StudentDashboard;
