import React from 'react';
import { useAuth } from '../contexts/AuthContext';

const Header = ({ role }) => {
  const { currentUser } = useAuth();

  const roleDisplay = {
    admin: 'üëë Administrator',
    delegate: 'üßë‚Äçüíº Delegate',
    student: 'üéì Student',
  };

  return (
    <header className="bg-white border-b border-gray-200 shadow-sm">
      <div className="px-8 py-4 flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-blue-900">University Attendance System</h1>
          <p className="text-sm text-gray-600 mt-1">{roleDisplay[role]}</p>
        </div>
        <div className="flex items-center gap-4">
          <div className="text-right">
            <p className="font-semibold text-gray-900">{currentUser?.email}</p>
            <p className="text-xs text-gray-600">{currentUser?.full_name || 'User'}</p>
          </div>
          <div className="w-10 h-10 rounded-full bg-blue-500 flex items-center justify-center text-white font-bold">
            {currentUser?.email?.charAt(0).toUpperCase()}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
